#define pi 3.14159265

mat2 rotMat(float rot) {
    return mat2(cos(rot), -sin(rot),
                sin(rot), cos(rot));
}

float line(vec2 p1, vec2 p2, vec2 frag_pos, float width) {
    vec2 line_track = p2 - p1;
    vec2 hip = frag_pos - p1;
    float proj = clamp(dot(hip, line_track) / dot(line_track, line_track), 0.0, 1.0);
    vec2 perp_pos = p1 + line_track * proj;


    return (1.0 - step(width, distance(perp_pos, frag_pos))) ;
}

void transformSphere(in vec3 sphere[72], out vec3 transformed[72]) {
    for(int i = 0; i < 72; ++i) {

        sphere[i].xz *= rotMat(iTime*3.);
        sphere[i].xy *= rotMat(iTime*3.);

        sphere[i].xy /= sphere[i].z -4.0;
        transformed[i] = sphere[i] * 250.0 + vec3(400.0, 225.0, 1.0);

    }
}

float sphereCalc(in vec3 cube[72], vec2 frag_pos) {
    float is_sphere = 0.0;

    for(int i = 0; i < 64; ++i) {
        if (i % 8 != 7)
            is_sphere += line(cube[i].xy, cube[i + 1].xy, frag_pos, 1.0);
        else 
            is_sphere += line(cube[i].xy, cube[i - 7].xy, frag_pos, 1.0);

        is_sphere += line(cube[i].xy, cube[i + 8].xy, frag_pos, 1.0);
    }

    return min(is_sphere, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 points[72];
    float radious = 1.5;
    for (float i = 0.; i < 9.; i++) {
        float angle = i * pi / 8.;
        float size = radious * sin(angle);
        float alt = radious * cos(angle);
        vec3 height = vec3(0, alt, 0);

        int index = int(i) * 8;

        points[index ] = height + vec3(sin(0.)           , 0, cos(0.))           * size;
        points[index + 1] = height + vec3(sin(pi / 4.)      , 0, cos(pi / 4.))      * size;
        points[index + 2] = height + vec3(sin(pi / 2.)      , 0, cos(pi / 2.))      * size;
        points[index + 3] = height + vec3(sin(3. * pi / 4.)  , 0, cos(3. * pi / 4.))  * size;
        points[index + 4] = height + vec3(sin(pi)          , 0, cos(pi))          * size;
        points[index + 5] = height + vec3(sin(-3. * pi / 4.) , 0, cos(-3. * pi / 4.)) * size;
        points[index + 6] = height + vec3(sin(-pi / 2.)     , 0, cos(-pi / 2.))     * size;
        points[index + 7] = height + vec3(sin(-pi / 4.)     , 0, cos(-pi / 4.))     * size;
    }


    vec3 transformed[72];

    transformSphere(points, transformed);
    float is_sphere = sphereCalc(transformed, fragCoord.xy);

    vec3 final_color = vec3(1.0 - is_sphere);

    fragColor = vec4(final_color,1.0);
}

