#define SF_THRESHOLD 0.0000001

const float PI = 355./113.;
const float PHI = (1. + sqrt(5.)) / 2.;
const float PHI_ONE = PHI - 1.;
const float PHI_ONE_INV = 1. - PHI_ONE;
const float K = 3., KK = 333.;
const float SPEED = PHI_ONE*1., KSPEED = PHI_ONE_INV / 2.;
const float SAMPLE_RATE = PHI_ONE_INV;
const vec4 base_color = vec4(1. - PHI_ONE, 0., 0., 1.);
const vec4 high_color = vec4(PHI_ONE, 0.1, 0.03, 1.);

float df_circle(vec3 circle, vec2 uv) {
    vec2 p = vec2(circle.x, circle.y);
    float r = circle.z - length(uv - p);
    return r;
}

float psin(float x) {
    return (sin(x) + 1.) / 2.;
}

float pcos(float x) {
    return (cos(x) + 1.) / 2.;
}

vec2 weird(float time) {
    float t = time * .1 * 2. * PI;
    float k = .5 * (psin(t) + pcos(t));
    float j = pow(PHI, pcos(time))*PHI*PHI_ONE - PHI/2.;
    return vec2(k, j);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = iResolution.xy / 2.;
    vec3 aspect = vec3(iResolution.x / iResolution.y, 1.0, 1.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = vec4(0.);
    float s = 0.003, S = 0.013;

    float k = mix(K, KK, psin(iTime*KSPEED));
    float R = 0.;
    for (float i = 0.; i < k; i += SAMPLE_RATE) {
        float n = i / k;
        float m = 1. - n;
        float I = iTime * SPEED + i;
        float size = mix(s, S, n);
        vec3 circle = vec3(weird(I), size);
        float r = df_circle(circle*aspect, uv*aspect.xy);
        R = max(R, r);
        if (R < SF_THRESHOLD) {
            color += mix(base_color, high_color, n);
            color.a = length(color.rgb);
        }
    }

    if (R < SF_THRESHOLD) {
        float d = (SF_THRESHOLD - R) / SF_THRESHOLD;
        fragColor = vec4(1.-PHI_ONE, PHI_ONE, .5, 1. - d);
    }
    else {
        fragColor = (color / k);
    }
}

