vec4 AlphaBlendColors(const vec4 baseColor, const vec4 appendColor)
{
    vec3 color = mix(baseColor.rgb, appendColor.rgb, appendColor.a);
    return vec4(color.x, color.y, color.z, baseColor.a + ((1.0 - baseColor.a) * appendColor.a));
}

vec3 EmissionToRGB(const vec3 emissiveColor)
{
    vec3 ret = emissiveColor;
    if (emissiveColor.r > 1.0)
    {
        float distribution = (emissiveColor.r - 1.0) * 0.5;
        ret.g += distribution;
        ret.b += distribution;
    }
    if (emissiveColor.g > 1.0)
    {
        float distribution = (emissiveColor.g - 1.0) * 0.5;
        ret.r += distribution;
        ret.b += distribution;
    }
    if (emissiveColor.b > 1.0)
    {
        float distribution = (emissiveColor.b - 1.0) * 0.5;
        ret.r += distribution;
        ret.g += distribution;
    }
    ret /= max(max(ret.r, ret.g), ret.b);
    return ret;
}


vec4 DrawCell(const vec2 uv, vec4 emissiveColor, float glowSize, float glowBoost)
{
    vec4 ret;
    float left = uv.y * 0.25;
    float right = 0.75 + (uv.y * 0.25);
    if
    (
        (uv.x >= (left - glowSize)) &&
        (uv.x <= (right + glowSize)) &&
        (uv.y >= -glowSize) &&
        (uv.y <= 1.0)
    )
    {
        float intensity =
            (
                (uv.x < left) ?
                    (1.0 - ((left - uv.x) / glowSize)) :
                    (
                        (uv.x > right) ?
                            (1.0 - ((uv.x - right) / glowSize)) :
                            1.0
                    )
            ) *
            (
                (uv.y < 0.0) ?
                    (1.0 - (-uv.y / glowSize)) :
                    (
                        (uv.x < left) ?
                            1.0 :
                            (
                                (uv.x > right) ?
                                    1.0 :
                                    (uv.y / glowBoost)
                            )
                    )
            ) * glowBoost;
        vec3 color = EmissionToRGB(emissiveColor.rgb * emissiveColor.a * intensity);
        ret.rgba = vec4(color.r, color.g, color.b, intensity * emissiveColor.a * (1.0 - uv.y));
    }
    return ret;
}

void mainImage(out vec4 fragmentColor, in vec2 fragmentCoordinates)
{
    vec2 uv = (fragmentCoordinates.xy - (iResolution.xy * 0.5)) / ((iResolution.x > iResolution.y) ? iResolution.y : iResolution.x);
    fragmentColor = vec4(clamp(skyColor.r * uv.y, 0.0, 1.0), clamp(skyColor.g * uv.y, 0.0, 1.0), clamp(skyColor.b * uv.y, 0.0, 1.0), 1.0);
#if defined SCREENSHOT_MODE
    float y_movement = mod(screenshotTime, 1.0);
#else
    float y_movement = mod(iTime * 0.5, 1.0);
#endif
    float x_movement = y_movement * 0.25;
    for (int cell_index = 0, x; cell_index < cells.length(); cell_index++)
    {
        Cell cell = cells[cell_index];
        for (x = 0; x < 6; x++)
        {
            fragmentColor = AlphaBlendColors(fragmentColor, DrawCell(vec2((uv.x + x_movement * cell.movementSpeedMultiplier) * minimalGridAxisCellCount + (4.0 - float(cell_index) * 2.0) + 0.75 * 0.5 - (minimalGridAxisCellCount * 0.25) * float(x), (uv.y + y_movement * cell.movementSpeedMultiplier) * minimalGridAxisCellCount + (-1.5 + float(cell_index)) - minimalGridAxisCellCount * float(x)), cell.color, cellGlowSize, cellGlowBoost));
        }
    }
}

// Witcher Signs, June 2022
// by ChutneyPot
//https://www.shadertoy.com/view/Nd3fR7
// soundcloud track: Dan Vasc - Toss A Coin To Your Witcher (Metal Cover) [Bass Boosted]
// link: https://soundcloud.com/dailybassboost/dan-vasc-toss-a-coin-to-your-witcher-bass-boosted
//--------------------------------------------------------------------------
#define CORE 0.1
#define GLOW 0.25

#define PULSE 4.0

#define BASE vec4(0.5, 0.5, 0.5, 1.0)
#define YRDEN vec4(1.0, 0.0, 1.0, 1.0)
#define QUEN vec4(1.0, 1.0, 0.0, 1.0)
#define IGNI vec4(1.0, 0.0, 0.0, 1.0)
#define AXII vec4(0.0, 1.0, 0.0, 1.0)
#define AARD vec4(0.0, 1.0, 1.0, 1.0)

const float s = 2.5;
const vec2 o = vec2(1.6, 1.8);

float Line(in vec2 p, in vec2 a, in vec2 b)
{
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);

    return length(pa - ba * h);
}

void Neon(in vec2 uv, in vec2 a, in vec2 b, inout float c, inout float g)
{
    c = max(c, 1.0 - smoothstep(Line(uv, a, b), 0.0, CORE));
    g = max(g, 1.0 - smoothstep(Line(uv, a, b), 0.0, GLOW));
}

vec4 Flash(in vec4 sc, in float c, in float g, in float v, in float t)
{    
    vec4 gc = mix(vec4(0), sc, t);
    vec4 cc = mix(BASE, vec4(1), t);

    vec4 col = mix(vec4(0), gc, max(g, v));
    return mix(col, cc, c);
}

vec4 Yrden(in vec2 uv, in float v, in float t)
{
    uv.x += 14.0;

    float c = 0.0, g = 0.0;
    Neon(uv, vec2(0.0 - o.x, s), vec2(0.0 + o.x, -s), c, g);
    Neon(uv, vec2(0.0 + o.x, s), vec2(s - 2.1, s - o.y), c, g);
    Neon(uv, vec2(0.0 - o.x, -s), vec2(-s + 2.1, -s + o.y), c, g);
    Neon(uv, vec2(0.0 + o.x, s), vec2(0.0 - o.x, s), c, g);
    Neon(uv, vec2(0.0 - o.x, -s), vec2(0.0 + o.x, -s), c, g);

    return Flash(YRDEN, c, g, v, t);
}

vec4 Quen(in vec2 uv, in float v, in float t)
{
    uv.x += 7.0;

    float c = 0.0, g = 0.0;
    Neon(uv, vec2(-s, s), vec2(0.0, -s), c, g);
    Neon(uv, vec2(0.0, -s), vec2(s - o.x, -s + o.y), c, g);
    Neon(uv, vec2(s), vec2(0.0 + o.x, s - o.y), c, g);
    Neon(uv, vec2(0.0 + o.x, s - o.y), vec2(0.0, s - o.y), c, g);
    Neon(uv, vec2(-s, s), vec2(s), c, g);

    return Flash(QUEN, c, g, v, t);
}

vec4 Igni(in vec2 uv, in float v, in float t)
{    
    uv.x += 0.5;

    float c = 0.0, g = 0.0;
    Neon(uv, vec2(0.0, s), vec2(-s), c, g);
    Neon(uv, vec2(s) - o, vec2(s, -s), c, g);
    Neon(uv, vec2(-s), vec2(s, -s), c, g);

    return Flash(IGNI, c, g, v, t);
}

vec4 Axii(in vec2 uv, in float v, in float t)
{
    uv.x -= 6.5;

    float c = 0.0, g = 0.0;
    Neon(uv, vec2(-s, s), vec2(0.0, -s), c, g);
    Neon(uv, vec2(0.0, -s), vec2(0.0 + o.x, s - o.y), c, g);
    Neon(uv, vec2(-s, s), vec2(s), c, g);

    return Flash(AXII, c, g, v, t);
}

vec4 Aard(in vec2 uv, in float v, in float t)
{
    uv.x -= 13.0;         

    float c = 0.0, g = 0.0;
    Neon(uv, vec2(0.0, s), vec2(s, -s), c, g);
    Neon(uv, vec2(0.0, s), vec2(-s + o.x, s - o.y), c, g);
    Neon(uv, vec2(-s), vec2(0.0 - o.x, -s + o.y), c, g);
    Neon(uv, vec2(0.0 - o.x, -s + o.y), vec2(0.0, -s + o.y), c, g);
    Neon(uv, vec2(-s), vec2(s, -s), c, g);

    return Flash(AARD, c, g, v, t);
}

float Pulse(in float t)
{
    float p = PULSE * 0.5;
    return clamp(min(t / p, p - t / p), 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{  
    vec2 st = fragCoord / iResolution.xy;
    vec2 uv = st;

    st *= 1.0 - st;
    float v = st.x * st.y * 15.0;
    v = 1.0 - pow(v, 0.2);

    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 20.0;  

    float t = mod(iTime, PULSE * 5.0);
    vec4 yr = Yrden(uv, v, Pulse(t));
    vec4 qu = Quen(uv, v, Pulse(t - PULSE));
    vec4 ig = Igni(uv, v, Pulse(t - PULSE * 2.0));
    vec4 ax = Axii(uv, v, Pulse(t - PULSE * 3.0));
    vec4 aa = Aard(uv, v, Pulse(t - PULSE * 4.0));

    vec4 col = vec4(0.0);
    col = mix(col, yr, yr.w);
    col = mix(col, qu, qu.w);
    col = mix(col, ig, ig.w);
    col = mix(col, ax, ax.w);
    col = mix(col, aa, aa.w);

    fragColor = col;
}

#define M_PI 3.141592654

uniform float u_time;

float set_min_line_width(float in_width){
    // if the width of or height of the window is too small
    // increase the size of the lines to make them draw clearly
    float max_screen_size;
    if(iResolution.y > iResolution.x) { max_screen_size = iResolution.y; }
    else {  max_screen_size = iResolution.x; }
    float min_line_width = 38./max_screen_size;
    if(in_width > min_line_width) {
        min_line_width = in_width;
    }
    return min_line_width;
}

void circle(vec2 st, float radius, vec3 in_color, inout vec3 out_color){
    vec2 dist = st-vec2(0.5);
	float inside_circle =  1.-smoothstep(radius-(radius*0.01),
                         radius+(radius*0.01),
                         dot(dist,dist)*4.0);           
    if(inside_circle > 0.0) { out_color = in_color; }
}

float circle_antialiased(vec2 st, float radius){
    vec2 dist = st-vec2(0.5);
	return 1.-smoothstep(radius-(radius*0.01),
                         radius+(radius*0.01),
                         dot(dist,dist)*4.0);
}

// draws an aliased circle
void circle_line(vec2 st, float radius, float thickness, vec3 in_color, inout vec3 out_color){
    vec2 dist = st-vec2(0.5); // set center of circle
    thickness /= 2.;
	float inside_circle = smoothstep( radius-thickness, radius, dot(dist,dist)*4.0) -
          smoothstep( radius, radius+thickness, dot(dist,dist)*4.0);                     
    if(inside_circle > 0.0) { out_color = in_color; }
}

float circle_line_antialiased(vec2 st, float radius, float thickness){
    vec2 dist = st-vec2(0.5); // set center of circle
    thickness /= 2.;
	return smoothstep( radius-thickness, radius, dot(dist,dist)*4.0) -
          smoothstep( radius, radius+thickness, dot(dist,dist)*4.0);
}

void rectangle(vec2 uv, vec2 point_1, vec2 point_2, vec3 color_in, inout vec3 color_out){
    if(uv.x > point_1.x && uv.x < point_2.x && uv.y > point_1.y && uv.y < point_2.y){
        color_out = color_in;
    }
}

float edge_function(vec2 a, vec2 b, vec2 c){
    return ((c.x - a.x) * (b.y - a.y) - (c.y - a.y) * (b.x - a.x));
}

void draw_triangle(vec2 uv, vec2 a, vec2 b, vec2 c,
    vec3 color_in, inout vec3 color_out) {
    float a_inside = edge_function(a, b, uv);
    float b_inside = edge_function(b, c, uv);
    float c_inside = edge_function(c, a, uv);

    if( a_inside > .0 || b_inside > .0 || c_inside > .0 ) {
        // TODO: Remove the else here.
    } else {
        color_out = color_in;
    }
}

float radar_sweep(vec2 uv, float len){
    uv.x -= 0.5; // TODO: this shouldn't be needed?
    uv.y -= 0.5;
    float theta0 = 120.0 * iTime; // 120.0 sets the speed of rotation
    float theta = mod(180.0 * atan(uv.y,uv.x) / M_PI+theta0, 360.0);
    float angle = iTime;
    return smoothstep(2./M_PI, 1.999/M_PI, theta);
}

vec3 color_white = vec3(1.);
vec3 color_hlt_blue = vec3(37.9/100.,76.5/100.,79.5/100.);
vec3 color_lt_blue = vec3(70.7/100.0,90.8/100.,94.2/100.);
vec3 color_orange = vec3(89.5/100.,34.2/100.,19.1/100.);

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y/iResolution.x;
    uv.y -= .25;
    uv.x -= .45;
    uv *= 15.80;

    vec3 col = vec3(0.);
    vec2 center_pos = vec2(15.)-uv;

    col += radar_sweep(uv, -4.26) * color_lt_blue;

    // Blue lines
    //rectangle(uv, vec2(-4.26, 0.5), vec2(0.5, .56), color_hlt_blue, col);

    col += circle_line_antialiased(uv, .18,  set_min_line_width(0.1)) * color_lt_blue;
    col += circle_line_antialiased(uv, 15.7, set_min_line_width(2.)) * color_lt_blue;
    col += circle_line_antialiased(uv, 43., set_min_line_width(2.)) * color_lt_blue;
    col += circle_line_antialiased(uv, 90.5, set_min_line_width(2.)) *color_white;
    col += circle_line_antialiased(uv, 155.5, set_min_line_width(5.)) * color_hlt_blue;

    // White triangles
    // left
    draw_triangle(uv, vec2(-5.6,0.32), vec2(-5.4,0.5), vec2(-5.6,0.65), color_white, col);
    // right
    draw_triangle(uv, vec2(6.6,0.32), vec2(6.6,0.65), vec2(6.4,0.5), color_white, col);

    // Red lines
    uv.y -= 1.97;

    col += circle_antialiased(uv, 0.05) * color_orange;
    col += circle_line_antialiased(uv, 0.12, set_min_line_width(0.0001)) * color_orange;
    col += circle_line_antialiased(uv, 8.6, set_min_line_width(0.8)) * color_orange;

    // White dots
    uv.y += 0.5;
    uv.x -= 0.15;
    col += circle_antialiased(uv, 0.022) * color_white;

    uv.y -= 2.2;
    uv.x += 0.8;
    col += circle_antialiased(uv, 0.022) * color_white;    

    //fragColor = vec4(0., uv.y,0.,1.);
    fragColor = vec4( col,1.0);
}

const float PI = 3.14159;

vec3 mod289(vec3 x) {
    return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 mod289(vec4 x) {
    return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x) {
    return mod289(((x*34.0)+10.0)*x);
}

vec4 taylorInvSqrt(vec4 r) {
    return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(vec3 v) { 
    const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
    const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);
    vec3 i  = floor(v + dot(v, C.yyy) );
    vec3 x0 =   v - i + dot(i, C.xxx) ;
    vec3 g = step(x0.yzx, x0.xyz);
    vec3 l = 1.0 - g;
    vec3 i1 = min( g.xyz, l.zxy );
    vec3 i2 = max( g.xyz, l.zxy );
    vec3 x1 = x0 - i1 + C.xxx;
    vec3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y
    vec3 x3 = x0 - D.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y
    i = mod289(i); 
    vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));
    float n_ = 0.142857142857; // 1.0/7.0
    vec3  ns = n_ * D.wyz - D.xzx;
    vec4 j = p - 49.0 * floor(p * ns.z * ns.z);  //  mod(p,7*7)
    vec4 x_ = floor(j * ns.z);
    vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)
    vec4 x = x_ *ns.x + ns.yyyy;
    vec4 y = y_ *ns.x + ns.yyyy;
    vec4 h = 1.0 - abs(x) - abs(y);
    vec4 b0 = vec4( x.xy, y.xy );
    vec4 b1 = vec4( x.zw, y.zw );
    vec4 s0 = floor(b0)*2.0 + 1.0;
    vec4 s1 = floor(b1)*2.0 + 1.0;
    vec4 sh = -step(h, vec4(0.0));
    vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
    vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;
    vec3 p0 = vec3(a0.xy,h.x);
    vec3 p1 = vec3(a0.zw,h.y);
    vec3 p2 = vec3(a1.xy,h.z);
    vec3 p3 = vec3(a1.zw,h.w);
    vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
    p0 *= norm.x;
    p1 *= norm.y;
    p2 *= norm.z;
    p3 *= norm.w;
    vec4 m = max(0.5 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
    m = m * m;
    return 105.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // zero in the center and closest edge is 1
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    float ratio = iResolution.x / iResolution.y;
    uv *= vec2(max(1.0, ratio), max(1.0, 1.0 / ratio));

    float ang = atan(uv.y, uv.x) + PI;
    float dist = length(uv);
    float radius = 0.9 + snoise(vec3(sin(ang), cos(ang), iTime)) * 0.1;
    vec3 mask = smoothstep(0.01, 0.0, dist - radius) * vec3(1.0);
    vec3 grad = vec3(0.1, 0.2, 0.5) + dist * vec3(0.05, 0.05, 0.3);
    vec3 glow = mask * smoothstep(0.3, 0.0, abs(dist - radius)) * vec3(0.2);
    vec3 color = mask * grad + glow;
    fragColor = vec4(color, 1.0);
}

float cross2(vec2 a, vec2 b) {
    return a.x * b.y - a.y * b.x;
}

const int n = 5;

vec2 pts[n] = vec2[n](
    vec2(-1.0, 1.0),
    vec2(0.0, 2.0),
    vec2(1.0, 1.0),
    vec2(1.0, -1.0),
    vec2(-1.0, -1.0)
);

const int l = 8;

vec2 trans(vec2 a1, vec2 a2, vec2 b1, vec2 b2, vec2 c) {
    vec2 t = a2 - a1;
    t /= dot(t, t);
    vec2 s = b2 - b1;
    return mat2(
        -s.x, -s.y,
        s.y, -s.x
    ) * mat2(
        t.x, -t.y,
        t.y, t.x
    ) * (c - a1) + b2;
}

bool checkIn(vec2 c) {
    bool a = true;
    for (int i = 0; i < n; i++) {
        if (cross2(c - pts[i], pts[(i + 1) % n] - pts[i]) <= -0.000001) {
            a = false;
        }
    }
    return a;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float x = iTime;
    pts = vec2[n](
        vec2(-1.0, 1.0),
        vec2(cos(x), 1.0 + abs(sin(x))),
        vec2(1.0, 1.0),
        vec2(1.0, -1.0),
        vec2(-1.0, -1.0)
    );

    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 2.0 * vec2(iResolution.x / iResolution.y, 1.0);

    vec2 c = uv * 4.0 + vec2(0.0, 3.0);

    vec4 o = vec4(0.0);
    for (int j = 0; j < l; j++) {
        for (int k = 0; k < (1 << j); k++) {
            vec2 d = c;
            for (int i = 0; i < j; i++) {
                if ((k & (1 << i)) == 0) {
                    d = trans(pts[0], pts[1], pts[3], pts[4], d);
                } else {
                    d = trans(pts[1], pts[2], pts[3], pts[4], d);
                }
            }
            if (checkIn(d)) {
                o += (0.3 + 0.7 * float(l - j - 1) / float(l - 1)) *
                    vec4(1.0, (float(k) + 0.5) / float(1 << j), 0.4, 0.0);
            }
        }
    }

    fragColor = o;
}

