const float EPSILON =.1;
const int max_step=255;
const float not_hit=10000.0;
const float max_len=100.;

float sp(vec3 sample_point,vec3 center,float size){
    return length(sample_point-center)-size;
}
float cube(vec3 sample_point,vec3 center,float size){
    vec3 d=abs(sample_point-center)-vec3(size);
    float in_d=min(max(d.x,max(d.y,d.z)),0.);
    float out_d= length(max(d, 0.0));
    //if(in_d==0.)return 0.0;
	return out_d*(1.-in_d)+in_d;
}

float scene(vec3 sample_point){
    float cube1=cube(sample_point,vec3(0.),.5);
    float sp1=sp(sample_point,vec3(.5),.5);
	float dep=min(cube1,sp1);
    //if(sp1<EPSILON)return not_hit;
    return dep;
    //return min(sp(sample_point,vec3(1.)),not_hit);
}
vec3 ray_d(float fov,vec2 size,vec2 fargcoord){
    vec2 xy=fargcoord-size/2.;
    float z=size.y/tan(radians(fov/2.));
    //return normalize(vec3(-z,xy.x,xy.y));
    return normalize(vec3(xy,-z));
}
float ray_march(vec3 eye, vec3 d){
    float depth=0.;
    for(int i=0;i<max_step;i++){
        float dis=scene(eye+d*depth);

        if(dis<EPSILON)return depth;

        depth+=dis;

        if(depth>=not_hit)return not_hit;

    }
    return not_hit;
}

vec3 culc_normal(vec3 p){
        return normalize(vec3(
        scene(vec3(p.x + EPSILON, p.y, p.z)) - scene(vec3(p.x - EPSILON, p.y, p.z)),
        scene(vec3(p.x, p.y + EPSILON, p.z)) - scene(vec3(p.x, p.y - EPSILON, p.z)),
        scene(vec3(p.x, p.y, p.z  + EPSILON)) - scene(vec3(p.x, p.y, p.z - EPSILON))
    ));
}
vec3 culc_light(vec3 diff_c,vec3 spec_c,float al, vec3 pos,vec3 eye,vec3 light_pos,vec3 power){
	vec3 n=culc_normal(pos);
    //not right but look good
    //vec3 l=culc_normal(light_pos-pos);
    //vec3 v=culc_normal(eye-pos);
    //vec3 r=culc_normal(reflect(-l,n));
    //right one use normalize
    vec3 l=normalize(light_pos-pos);
    vec3 v=normalize(eye-pos);
    vec3 r=normalize(reflect(-l,n));
	float dot_ln=dot(l,n);
    float dot_rv=dot(r,v);
    if(dot_ln<0.)return vec3(0.0, 0.0, 0.0);

    //use diff color
    if(dot_rv<0.)return power*diff_c*dot_ln;
    //reflection 
    return power*( diff_c*dot_ln + spec_c*pow(dot_rv,al));
}
vec3 phong_light(float al,vec3 light_pos,vec3 pos,vec3 eye,vec3 am_c,vec3 diff_c,vec3 spec_c){
     vec3 amb_light=.5*am_c;
    vec3 color=amb_light;
    vec3 light_power=vec3(.4);
    color+=culc_light(am_c,spec_c,al,pos,eye,light_pos,light_power);
    return color;
}
