            //uv *= -sign(uv.x);
            //uv.x += sign(uv.x)/2.;
            //uv.y += sign(uv.y)/2.;
            t2 = triangle_wave(uv.yx-.5,1.5);
            t3 = -triangle_wave(uv,1.5);
            uv.yx = (t2+t3);
        } 
        col.x = min(uv.y+uv.x+col.x,col.x)/sqrt(2.);
