vec3 surfaceLighting(vec3 pos, vec2 normal2D, vec3 albedo)
{
    vec3 normal = vec3(normal2D, 0.0);
    const vec3 LIGHT_DIR = normalize(vec3(1, 1, -1));

    float diffuse = max(0.1, dot(normal, -LIGHT_DIR));

