vec3 materialColor1(vec3 surfacePos, vec2 surfaceNormal)
{
    return vec3(1, 0, 0);
}

float material2NoiseAxis(vec3 surfacePos, vec2 surfaceNormal, vec2 surfaceUV)
{
    const vec2 NOISE_SCALE = vec2(80, 200);
    return perlinNoise(surfaceUV * NOISE_SCALE * 1.0, 5.423897);
}
vec3 materialColor2(vec3 surfacePos, vec2 surfaceNormal)
{
    //Use triplanar mapping, but optimize based on the fact
    //    that surfaces always have a horizontal normal.
    vec2 surfaceUvPlaneX = surfacePos.yz,
         surfaceUVPlaneY = surfacePos.xz;
    vec2 surfaceWeights = abs(surfaceNormal);
    surfaceWeights = pow(surfaceWeights, vec2(10)); //Make a shorter transition between planes
    surfaceWeights /= (surfaceWeights.x + surfaceWeights.y); //Normalize

    float noise = dot(surfaceWeights,
                      vec2(material2NoiseAxis(surfacePos, surfaceNormal, surfaceUvPlaneX),
                           material2NoiseAxis(surfacePos, surfaceNormal, surfaceUVPlaneY)));

    //Add a sort of cross-hatching effect.
    vec2 crossHatchUV = vec2(dot(surfaceWeights, surfacePos.xy),
                             surfacePos.z);
    crossHatchUV *= vec2(30, 30) * 5.0;
    float hatchStrength = max(OSCILLATE(crossHatchUV.x),
                              OSCILLATE(crossHatchUV.y));
    hatchStrength = pow(hatchStrength, 40.0);
    return vec3(hatchStrength);
}

vec3 materialColor3(vec3 surfacePos, vec2 surfaceNormal)
{
    vec3 albedo1 = vec3(0, 0, 1),
         albedo2 = vec3(1, 1, 0);

    vec2 surfaceUV1 = surfacePos.xz,
         surfaceUV2 = surfacePos.yz;

    //Generate noise using that UV.
    float noise1 = 0.5 * 2.0 * abs(perlinNoise(surfaceUV1 * vec2(100, 50), 1.2342) - 0.5),
          noise2 = 0.5 * 2.0 * abs(perlinNoise(surfaceUV2 * vec2(50, 100), 5.3224) - 0.5);
    float noiseFinal = min(noise1, noise2) / max(noise1, noise2);

    return mix(albedo1, albedo2, SHARPEN(SHARPEN(noiseFinal)));
}
vec3 materialColor(int id, vec3 surfacePos, vec2 surfaceNormal)
{
    vec3 albedo;
    if (id == 0)
        albedo = materialColor1(surfacePos, surfaceNormal);
    else if (id == 1)
        albedo = materialColor2(surfacePos, surfaceNormal);
    else if (id == 2)
        albedo = materialColor3(surfacePos, surfaceNormal);
    else
        albedo = vec3(1, 0, 1); //ERROR: Unknown material

    return albedo;//surfaceLighting(surfacePos, surfaceNormal, albedo);
}



vec3 sceneColor(Camera cam, ColumnCast surfaceHit, vec2 uv)
{
    //If nothing was hit, set the "hit distance" to the max value.
    float hitMask = step(0.0, surfaceHit.hitDist);
    bool hitNothing = (hitMask == 0.0);
    surfaceHit.hitDist = TRY(surfaceHit.hitDist, MAX_DISTANCE, hitNothing);

    //Compute 2D ray/position data.
    float camHeadingRadians = getPixelHeading(cam, uv.x);
    vec2 viewDir = vec2(cos(camHeadingRadians),
                        sin(camHeadingRadians));
    vec2 surfacePos = cam.pos + (viewDir * surfaceHit.hitDist);

    //Compute 3D ray/position data.
    float verticalFOVDegrees = CAM_HORIZONTAL_FOV_DEGREES * (iResolution.y / iResolution.x),
          verticalFOVHalfRadians = verticalFOVDegrees / 360.0 * PI2 / 2.0,
          pitch = mix(-verticalFOVHalfRadians,
                      verticalFOVHalfRadians,
                      uv.y),
          camZPos = 0.0;
    vec3 viewDir3D = rotateAround(vec3(viewDir, 0.0),
                                  vec3(getPerpendicularRight(viewDir), 0.0),
                                  pitch);
    float hitDist3D = TRY((surfacePos.x - cam.pos.x) / viewDir.x,
                          (surfacePos.y - cam.pos.y) / viewDir.y,
                          viewDir.x == 0.0);
    vec3 wallHitPos3D = vec3(cam.pos, camZPos) + (viewDir3D * hitDist3D);


    //Calculate the wall color.
    vec3 wallColor = TRY(materialColor(surfaceHit.materialID, wallHitPos3D, surfaceHit.hitNormal),
                         vec3(1, 0, 1), //No wall, so this color should be 100% obscured by fog
                         hitNothing);

    //Check for intersection with the floor or ceiling, occluding the wall intersection.
    float floorIntersectionDist = TRY((MIN_POS_3D.z - camZPos) / viewDir3D.z,
                                      999999.0,
                                      viewDir3D.z == 0.0),
          ceilingIntersectionDist = TRY((MAX_POS_3D.z - camZPos) / viewDir3D.z,
                                        999999.0,
                                        viewDir3D.z == 0.0);
    float floorMask = step(floorIntersectionDist, hitDist3D) *
                      step(0.0, floorIntersectionDist),
          ceilingMask = step(ceilingIntersectionDist, hitDist3D) *
                        step(0.0, ceilingIntersectionDist);
    ceilingMask *= 1.0-step(ceilingIntersectionDist, floorIntersectionDist);
    float wallMask = 1.0 - min(1.0, (floorMask + ceilingMask));

    //Recompute the ray length/hit pos, given the floor and ceiling intersections.
    float hitDist = (floorMask * floorIntersectionDist) +
                    (ceilingMask * ceilingIntersectionDist) +
                    (wallMask * hitDist3D);
    vec2 cutoffSurfacePos = cam.pos + (viewDir * hitDist);

    //Find the visible surface color.
    vec3 hitColor = (floorMask * floorColor(cutoffSurfacePos)) +
                    (ceilingMask * ceilingColor(cutoffSurfacePos)) +
                    (wallMask * wallColor);

    //Apply fog.
    vec3 foggedColor = mix(hitColor, fogColor(viewDir3D),
                           pow(min(1.0, hitDist / MAX_DISTANCE_3D + 0.2),
                               2.0));

    return foggedColor;
}


vec4 minimapColor(Camera cam, vec2 pos)
{
    //Flip the Y position for the display.
    //Not sure why this is needed.
    pos = vec2(pos.x,
               mix(MAX_POS.y, MIN_POS.y, INV_LERP(MIN_POS.y, MAX_POS.y, pos.y)));

    vec2 uv = posToUV(pos);

    //Add a circle to represent the camera.
    float camCircleMask = step(distance(pos, cam.pos), CAM_RADIUS);

    //Add lines representing the view cone.
    float viewLineMask = 0.0;
    for (int i = 0; i < 2; ++i)
    {
        float heading = getPixelHeading(cam, float(i));
        vec2 facingDir = vec2(cos(heading), sin(heading));

        float acrossLine = distToLine(cam.pos, facingDir, pos, true),
              alongLine = dot(facingDir, (pos - cam.pos));
        viewLineMask += border(acrossLine, 0.0,
                               0.02, 12.0) *
                        step(CAM_RADIUS, alongLine) *
                        step(alongLine, 0.2);
    }
    viewLineMask = SATURATE(viewLineMask);

    //Visualize the walls.
    Surface surf = readSurfaceData(BUF_A, uv);
    float surfMask = TRY(0.0, 1.0, surf.isSolid);
    vec3 surfColor = materialColor(surf.materialID, vec3(pos, 0.0), surf.normal);

    return vec4(SATURATE((camCircleMask * vec3(0, 0.3, 1.0)) +
                         (viewLineMask * vec3(0.5, 0.75, 0.0)) +
                         (surfMask * surfColor)),
                mix(0.675, 1.0,
                    max(camCircleMask,
                        max(viewLineMask, surfMask * 0.75))));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Compute screen coordinates.
    vec2 uv = fragCoord/iResolution.xy;
    int pixelX = int(fragCoord.x - 0.49999);
    vec2 pos = uvToPos(uv);

    Camera cam = readCamData(BUF_B);

    //Draw the minimap.
    vec2 minimapUVMin = vec2(0.7),
         minimapUVSize = vec2(0.27, 0.27),
         minimapUVMax = minimapUVMin + minimapUVSize,
         minimapUV = INV_LERP(minimapUVMin, minimapUVMax, uv);
    float minimapMask = step(0.0, minimapUV.x) * step(0.0, minimapUV.y) *
                        step(minimapUV.x, 1.0) * step(minimapUV.y, 1.0);
    float minimapBorder = (1.0 / 4.0) *
                          (border(minimapUV.x, 0.0, 1.0, 16.0) +
                           border(minimapUV.y, 0.0, 0.05, 2.0) +
                           border(minimapUV.x, 1.0, 1.0, 16.0) +
                           border(minimapUV.y, 1.0, 0.05, 2.0));
    vec4 cMinimap = minimapColor(cam, minimapUV); //TODO: Faster to skip the minimap if it's not covering this pixel?
    cMinimap.rgb *= 1.0 - minimapBorder;

    //Draw the world.
    ColumnCast column = readColumnCast(BUF_C, pixelX);
    vec3 cWorld = sceneColor(cam, column, uv);

    //Combine the various drawing layers.
    fragColor = vec4(mix(cWorld, cMinimap.rgb, minimapMask * cMinimap.a),
                     1.0);
}

