float wallThickness(vec2 pos)
{
    return 0.15;
    return pow(fract(iTime), 5.0);

    //Below is a thickness value that decreases towards the edge of the screen.
    vec2 uv = posToUV(pos);

    float edgeDist = min(uv.x, min(uv.y, min(1.0 - uv.x, 1.0 - uv.y)));
    float edgeT = 2.0 * edgeDist;
    edgeT = 1.0 - pow(edgeT, 0.42);

    return mix(0.15, 0.225, edgeT);
}
float wallNoise(vec2 pos)
{
    RUN_OCTAVE_NOISE(
        octaved, vec2, pos * 2.0,

        //Normal octave noise:
        //perlinNoise(p, floor(iTime)),
        //Ridge noise:
        abs(perlinNoise(p, 4.453) - 0.5),

        4,
        noise = pow(noise, 1.0),
        2.0
    );
    return octaved;
}
int wallBiome(vec2 pos)
{
    float noise = perlinNoise(pos * 4.0, 0.234);

    return int(mix(0.0, float(N_MATERIALS) - 0.0001, noise));
}

Surface getWall(vec2 pos)
{
    float fieldStrength = wallNoise(pos);

    //Sample the neighboring noise values, and use
    //    finite differences to compute a normal.
    const float EPSILON = 0.001;
    float fieldMinX = wallNoise(vec2(pos.x - EPSILON, pos.y)),
          fieldMinY = wallNoise(vec2(pos.x, pos.y - EPSILON));
    vec2 normal = normalize(vec2(fieldMinX - fieldStrength,
                                 fieldMinY - fieldStrength));

    int biome = wallBiome(pos);

    Surface surf;
    surf.isSolid = (fieldStrength < wallThickness(pos));
    surf.normal = normal;
    surf.materialID = biome;

    return surf;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    vec2 worldPos = uvToPos(uv);
    Surface surf = getWall(worldPos);

    fragColor = packSurfaceData(surf);
}

common_code:
#define PI (3.14159265)
#define PI2 (PI * 2.0)

#define INV_LERP(a, b, x) ((x - a) / (b - a))

#define SATURATE(x) (clamp(x, 0.0, 1.0))

#define SHARPEN(t) (smoothstep(0.0, 1.0, t))
#define SHARPEN2(t) (SHARPEN(SHARPEN(t))
#define SHARPENER(t) (t * t * t * (t * (t * 6. - 15.) + 10.))

#define OSCILLATE(t) (0.5 + (0.5 * sin((t) * PI2)))

#define TRY(value, backup, useBackup) ((useBackup) ? (backup) : (value))

vec2 getPerpendicularRight(vec2 dir)
{
    //OpenGL's screen is left-handed.
    return vec2(dir.y, -dir.x);
}

float distToLine(vec2 lineStart, vec2 lineDir, vec2 point, bool isRay)
{
    float t = dot(point - lineStart, lineDir);
    if (isRay)
        t = max(0.0, t);
    vec2 closestLinePoint = lineStart + (lineDir * t);

    return distance(point, closestLinePoint);
}

float border(float x, float target, float thickness, float dropoff)
{
    float dist = abs(x - target);
    float closeness = 1.0 - min(1.0, dist / thickness);
    return pow(closeness, dropoff);
}

vec3 rotateAround(vec3 p, vec3 rotAxis, float rotAngleRadians)
{
    //Reference: https://en.wikipedia.org/wiki/Rotation_matrix#Rotation_matrix_from_axis_and_angle
    float aCos = cos(rotAngleRadians),
          aSin = sin(rotAngleRadians),
          aCos1 = 1.0 - aCos;
    vec3 dirSine = rotAxis * aSin,
         dirSqrCos1 = rotAxis * rotAxis * aCos1;
    mat3 rotMat = mat3(
        //Column 1:
        aCos + dirSqrCos1.x,
        (rotAxis.y * rotAxis.x * aCos1) + dirSine.z,
        (rotAxis.z * rotAxis.x * aCos1) - dirSine.y,
        //Column 2:
        (rotAxis.x * rotAxis.y * aCos1) - dirSine.z,
        aCos + dirSqrCos1.y,
        (rotAxis.z * rotAxis.y * aCos1) + dirSine.x,
        //Column 3:
        (rotAxis.x * rotAxis.z * aCos1) + dirSine.y,
        (rotAxis.y * rotAxis.z * aCos1) - dirSine.x,
        aCos + dirSqrCos1.z
    );
    return rotMat * p;
}


//////////////////
// World Bounds //
//////////////////

#define MIN_POS vec2(0)
#define MAX_POS vec2(1)
#define MAX_DISTANCE (distance(MIN_POS, MAX_POS))

//Define the floor and ceiling height
#define MIN_POS_3D vec3(MIN_POS, -0.05)
#define MAX_POS_3D vec3(MAX_POS, 0.1)
#define MAX_DISTANCE_3D (distance(MIN_POS_3D, MAX_POS_3D))

vec2 posToUV(vec2 pos) { return INV_LERP(MIN_POS, MAX_POS, pos); }
vec2 uvToPos(vec2 uv) { return mix(MIN_POS, MAX_POS, uv); }


//////////////
// Buffer A //
//////////////

//Buffer A contains the walls of the level, in a sort of top-down view.
//Each pixel represents a space that may be solid or empty.
struct Surface
{
    bool isSolid;
    vec2 normal; //NOT normalized, for performance/sampling
    int materialID; // From 0 to (N_MATERIALS-1)
};

#define N_MATERIALS 3

Surface readSurfaceData(sampler2D bufferA, vec2 pos)
{
    Surface surf;
    vec4 pixel = texture(bufferA, posToUV(pos));

    surf.isSolid = pixel.r > 0.5;
    surf.normal = -1.0 + (2.0 * pixel.gb);
    surf.materialID = int(round(pixel.a * float(N_MATERIALS)));

    return surf;
}

vec4 packSurfaceData(Surface surf)
{
    return vec4(
        surf.isSolid ? 1.0 : 0.0,
        0.5 + (0.5 * surf.normal),
        float(surf.materialID) / float(N_MATERIALS)
    );
}


//////////////
// Buffer B //
//////////////

//Buffer B contains the player camera data in a small number of pixels.
//The rest of the buffer texture is unused.

struct Camera
{
    vec2 pos;
    float headingRadians, speed;
};

#define MAX_SPEED 0.1
#define CAM_RADIUS 0.01
#define CAM_HORIZONTAL_FOV_DEGREES 110.0


#define BUF_B_COORD ivec2(0)
#define BUF_B_FRAGCOORD vec2(0.5)
#define BUF_B_UV vec2(0)

Camera readCamData(sampler2D bufferB)
{
    vec4 color = texelFetch(bufferB, BUF_B_COORD, 0);
    return Camera(mix(MIN_POS, MAX_POS, color.rg),
                  color.b * PI2,
                  color.a * MAX_SPEED);
}
vec4 packCamData(Camera data)
{
    return vec4(INV_LERP(MIN_POS, MAX_POS, data.pos),
                mod(data.headingRadians, PI2) / PI2,
                clamp(data.speed / MAX_SPEED, 0.0, 1.0));
}

//Define the FOV math for the camera.
float getPixelHeading(Camera cam, float uvX)
{
    float fovRad = (CAM_HORIZONTAL_FOV_DEGREES / 360.0) * PI2,
          halfFovRad = fovRad / 2.0,
          headingOffset = mix(-halfFovRad, halfFovRad, uvX),
          heading = cam.headingRadians + headingOffset;
    return mod(heading, PI2);
}



//////////////
// Buffer C //
//////////////

//Buffer C contains a raycast for each column of the output image,
//    stored in the first row of the buffer.
//All other rows are unused.

struct ColumnCast
{
    float hitDist; //A negative value means "no hit".
    int materialID;
    vec2 hitNormal;
};

ColumnCast readColumnCast(sampler2D bufferC, int pixelX) //Taken as int because
                                                         //The width of this buffer matches
                                                         //    the width of the screen,
                                                         //    so no linear filtering is needed.
{
    vec4 pixel = texelFetch(bufferC, ivec2(pixelX, 0), 0);
    return ColumnCast(mix(-1.0, MAX_DISTANCE, pixel.r),
                      int(round(pixel.g * float(N_MATERIALS))),
                      -1.0 + (2.0 * pixel.ba));
}
vec4 packColumnCast(ColumnCast cc)
{
    return vec4(INV_LERP(-1.0, MAX_DISTANCE, cc.hitDist),
                INV_LERP(0.0, float(N_MATERIALS), float(cc.materialID)),
                0.5 + (0.5 * cc.hitNormal));
}



////////////////////
//    Hashing     //
////////////////////

//Source: https://stackoverflow.com/a/17479300

//Hashes 1D-4D uints to 1 uint,
//    or 1D-4D floats to 1 float.

uint hash( uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}
uint hash( uvec2 v ) { return hash( v.x ^ hash(v.y)                         ); }
uint hash( uvec3 v ) { return hash( v.x ^ hash(v.y) ^ hash(v.z)             ); }
uint hash( uvec4 v ) { return hash( v.x ^ hash(v.y) ^ hash(v.z) ^ hash(v.w) ); }
float floatConstruct( uint m ) {
    const uint ieeeMantissa = 0x007FFFFFu; // binary32 mantissa bitmask
    const uint ieeeOne      = 0x3F800000u; // 1.0 in IEEE binary32

    m &= ieeeMantissa;                     // Keep only mantissa bits (fractional part)
    m |= ieeeOne;                          // Add fractional part to 1.0

    float  f = uintBitsToFloat( m );       // Range [1:2]
    return f - 1.0;                        // Range [0:1]
}
float hash( float x ) { return floatConstruct(hash(floatBitsToUint(x))); }
float hash( vec2  v ) { return floatConstruct(hash(floatBitsToUint(v))); }
float hash( vec3  v ) { return floatConstruct(hash(floatBitsToUint(v))); }
float hash( vec4  v ) { return floatConstruct(hash(floatBitsToUint(v))); }

vec2 randUnitVector(float uniformRandom)
{
    float theta = uniformRandom * PI2;
    return vec2(cos(theta), sin(theta));
}


///////////////////////////////
//    Value/Perlin Noise     //
///////////////////////////////

float valueNoise(float x, float seed)
{
    float xMin = floor(x),
          xMax = ceil(x);

    float noiseMin = hash(vec2(xMin, seed)),
          noiseMax = hash(vec2(xMax, seed));

    float t = x - xMin;
    //t = SMOOTHERSTEP(t); //Actually gives worse results due to
                           //  the dumb simplicity of the underlying noise

    return mix(noiseMin, noiseMax, t);
}
float valueNoise(vec2 x, float seed)
{
    vec2 xMin = floor(x),
         xMax = ceil(x);
    vec4 xMinMax = vec4(xMin, xMax);

    vec2 t = x - xMin;
    //t = SMOOTHERSTEP(t); //Actually gives worse results due to
                           //  the dumb simplicity of the underlying noise

    #define VALUE_NOISE_2D(pos) hash(vec3(pos, seed))
    return mix(mix(VALUE_NOISE_2D(xMinMax.xy),
                   VALUE_NOISE_2D(xMinMax.zy),
                   t.x),
               mix(VALUE_NOISE_2D(xMinMax.xw),
                   VALUE_NOISE_2D(xMinMax.zw),
                   t.x),
               t.y);
}

#define PERLIN_MAX(nDimensions) (sqrt(float(nDimensions)) / 2.0)
float perlinNoise(float x, float seed)
{
    float xMin = floor(x),
          xMax = ceil(x),
          t = x - xMin;

    float value = mix(t         * sign(hash(vec2(xMin, seed)) - 0.5),
                      (1.0 - t) * sign(hash(vec2(xMax, seed)) - 0.5),
                      SHARPENER(t));
    return INV_LERP(-PERLIN_MAX(1), PERLIN_MAX(1), value);
}

vec2 perlinGradient2(float t)
{
    return randUnitVector(t);
}
float perlinNoise(vec2 p, float seed)
{
    vec2 pMin = floor(p),
         pMax = pMin + 1.0,
         t = p - pMin;
    vec4 pMinMax = vec4(pMin, pMax),
         tMinMax = vec4(t, t - 1.0);

    float noiseMinXMinY = dot(tMinMax.xy,
                              perlinGradient2(hash(vec3(pMinMax.xy, seed)))),
          noiseMaxXMinY = dot(tMinMax.zy,
                              perlinGradient2(hash(vec3(pMinMax.zy, seed)))),
          noiseMinXMaxY = dot(tMinMax.xw,
                              perlinGradient2(hash(vec3(pMinMax.xw, seed)))),
          noiseMaxXMaxY = dot(tMinMax.zw,
                              perlinGradient2(hash(vec3(pMinMax.zw, seed))));

    t = SHARPENER(t);
    float value = mix(mix(noiseMinXMinY, noiseMaxXMinY, t.x),
                      mix(noiseMinXMaxY, noiseMaxXMaxY, t.x),
                      t.y);
    return INV_LERP(-PERLIN_MAX(2), PERLIN_MAX(2), value);
}


/////////////////////////
//    Octave Noise     //
/////////////////////////


//Generates an implementation of octave noise.
#define RUN_OCTAVE_NOISE(outputName, posType, inputPos, callNoiseOnP, \
                         nOctaves, modifyNoise, persistence) \
float outputName; { \
    float sum = 0.0, \
          scale = 1.0, \
          weight = 1.0, \
          totalWeight = 0.0; \
    for (int i = 0; i < nOctaves; ++i) { \
        posType p = inputPos * scale; \
        float noise = callNoiseOnP; \
        modifyNoise; \
        sum += weight * noise; \
        totalWeight += weight; \
        scale *= persistence; \
        weight /= persistence; \
    } \
    outputName = sum / totalWeight; \
}

