Camera initializeCamera()
{
    //TODO: Implement something more clever,
    //         such as picking random starting points until one is empty.
    return Camera(mix(MIN_POS, MAX_POS, vec2(0.3, 0.5)),
                  PI2 * 0.75, 0.0);
}
void updateCamera(inout Camera cam)
{
    //TODO: Let the user control it.
    cam.headingRadians = mod(cam.headingRadians + 0.005, PI2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Only a few pixels are used in this buffer to store the camera state.
    if (fragCoord != BUF_B_FRAGCOORD)
    {
        fragColor = vec4(0, 0, 0, 0);
        return;
    }

    //On the first frame, pick a good initial position.
    if (iFrame == 0)
    {
        Camera firstState = initializeCamera();
        fragColor = packCamData(firstState);
    }
    //On subsequent frames, run the camera's control logic.
    else
    {
        Camera currentState = readCamData(BUF_B);
        updateCamera(currentState);
        fragColor = packCamData(currentState);
    }
}

