#define BUF_A iChannel0
#define BUF_B iChannel1


ColumnCast getRayHit(vec2 rayStart, vec2 rayDir)
{
    vec2 texel = uvToPos(1.0 / iResolution.xy);
    for (int i = 0; i < 500; ++i) //TODO: Max raymarch iterations based on map size
    {
        vec2 rayStep = rayDir * (texel * float(i)),
             rayPos = rayStart + rayStep;
        Surface surf = readSurfaceData(BUF_A, rayPos);
        if (surf.isSolid)
            return ColumnCast(length(rayStep), surf.materialID,
                              normalize(surf.normal)); //Used linear filtering on this buffer
        else if ((rayPos.x > MAX_POS.x) || (rayPos.x < MIN_POS.x) ||
                 (rayPos.y > MAX_POS.y) || (rayPos.y < MIN_POS.y))
            return ColumnCast(-1.0, 0, vec2(1, 0));
    }

    return ColumnCast(-1.0, 0, vec2(1, 0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (fragCoord.y >= 1.0)
    {
        fragColor = vec4(0, 0, 0, 1);
        return;
    }

    float uvX = fragCoord.x / iResolution.x;

    Camera cam = readCamData(BUF_B);
    cam.headingRadians = getPixelHeading(cam, uvX);

    //Raymarch from the camera into the world.
    vec2 rayDir = vec2(cos(cam.headingRadians),
                       sin(cam.headingRadians));
    ColumnCast hit = getRayHit(cam.pos, rayDir);

    fragColor = packColumnCast(hit);
}

