#define TEX_BUF_A iChannel1
#define TEX_BUF_B iChannel2

//TODO: Structs for each biome's pre-computed data that might be relevant around biome intersections.


vec3 colorTerrain_Water(Camera cam, SunState sun,
                        BufferA bufA, BufferB bufB,
                        BufferA bufACloseNeighbors[4], BufferA bufAFarNeighbors[4],
                        BufferB bufBCloseNeighbors[4], BufferB bufBFarNeighbors[4])
{
    return vec3(0.35, 0.55, 0.85);
}
vec3 colorTerrain_Plains(Camera cam, SunState sun,
                         BufferA bufA, BufferB bufB,
                         BufferA bufACloseNeighbors[4], BufferA bufAFarNeighbors[4],
                         BufferB bufBCloseNeighbors[4], BufferB bufBFarNeighbors[4])
{
    vec3 plainsColor = mix(vec3(0.0, 0.3, 0.1),
                           vec3(0.1, 0.97, 0.25),
                           pow(cam.pixelWorld.y / bufA.terrHeight, 0.48));

    //There may be some small caves coming from a nearby mountain biome.
    float caveEdge = bufA.caveMask * (1.0 - BUF_PULL_MAXX(bufACloseNeighbors).caveMask);
    vec3 caveColor = vec3(0.32, 0.19, 0.06),
         caveBorderColor = vec3(0.5);
    return mix(mix(plainsColor, caveColor, bufA.caveMask),
               caveBorderColor,
               caveEdge);
}

vec3 colorTerrain_Mountains(Camera cam, SunState sun,
                            BufferA bufA, BufferB bufB,
                            BufferA bufACloseNeighbors[4], BufferA bufAFarNeighbors[4],
                            BufferB bufBCloseNeighbors[4], BufferB bufBFarNeighbors[4])
{
    vec2 pos = cam.pixelWorld;

    //Cave data:
    float caveMask = bufA.caveMask,
          caveEdgeMask = caveMask * (1.0 - BUF_PULL_MAXX(bufACloseNeighbors).caveMask);
    //If on the floor of a cave, provide some noisy surface color.
    float caveFloorMask = bufA.caveMask *
                          SATURATE(
                              //An adjacent empty space means this is definitely a floor pixel.
                              (1.0 - BUF_PULL_MINY(bufACloseNeighbors).caveMask)
                              //Empty space a bit further above has a weaker effect on the floor mask.
                                + step(BUF_PULL_MINY(bufAFarNeighbors).caveMask,
                                       hashT(pos * 2.234).a)
                          ),
          caveFloorNoise = mix(0.5, 1.0, hashT(pos.x * 1.2312).b) *
                           octaveNoise(pos.x * 20.0, 2.22781, 3, 2.0),
          caveFloorColorT = SHARPEN(SHARPEN(SHARPEN(0.1 + pos.y / bufA.terrHeight)));

    //TODO: Raymarch to see if sunlight is visible from caves along the surface
    //TODO: Remove 'iron ore' from Buffer B, add rock data to it, give rocks a better border

    //Rocks:
    float rockNoise = 1.0 - worley3(pos * 80.0,
                                    mix(0.5, 0.0, pos.y / bufA.terrHeight),
                                    1.2135);
    rockNoise = pow(rockNoise * step(0.55, rockNoise), 0.75);
    rockNoise *= 1.0 - step(0.75, worley1(pos * 20.0,
                                          0.4,
                                          1.61421));
    float rockMask = step(0.5, rockNoise) *
                     //Keep inside the mountain biome, but spread out in shallower ground.
                     step(0.9 * (1.0 - pos.y), bufA.biome);
    float rockValue = rockMask
                        * SHARPEN(SHARPEN(SHARPEN(SHARPEN(pow(rockNoise, 1.5)))))
                        * mix(0.625, 1.0, hashT(floor(pos * 500.0)).r),
          rockBorderMask = border(rockNoise, 0.65, 0.55, 13.0);

    //Check gold ore.
    //TODO: Hiding this in a branch based on gold weight may speed things up, because most pixels have no gold.
    float oreGoldNoise = bufferOreWeight(bufB.goldMask),
          oreGoldMask = step(0.0001, bufB.goldMask),
          oreGoldNeighborCount = bufferOreMask(bufBCloseNeighbors[0].goldMask) +
                                 bufferOreMask(bufBCloseNeighbors[1].goldMask) +
                                 bufferOreMask(bufBCloseNeighbors[2].goldMask) +
                                 bufferOreMask(bufBCloseNeighbors[3].goldMask),
          oreGoldBorder = oreGoldMask *
                          pow(1.0 - (oreGoldNeighborCount / 4.0), 0.5);
    //Interpret the gold noise value as a bumpmap,
    //    and do pseudo-normal-mapping to make an interesting look.
    #define GOLD_BUMP(pull_dir) (bufferOreMask(pull_dir(bufBCloseNeighbors).goldMask) *    \
                                 mix(0.5, 1.0, bufferOreWeight(pull_dir(bufBCloseNeighbors).goldMask)))
    #define GOLD_LIGHTING_AMBIENT 0.475
    vec2 normal = normalize(vec2(0.00001) +
                            vec2(GOLD_BUMP(BUF_PULL_MINX) - GOLD_BUMP(BUF_PULL_MAXX),
                                 GOLD_BUMP(BUF_PULL_MINY) - GOLD_BUMP(BUF_PULL_MAXY)));
    float oreGoldBrightness = oreGoldMask *
                              max(GOLD_LIGHTING_AMBIENT, dot(-normal, sun.dir)),
          oreGoldSpecular = pow(oreGoldBrightness, 60.0);

    //Compute the final color:
    return mix(
        mix(
            mix(
                mix(
                    mix(
                        //The mountain's base color:
                        vec3(0.32, 0.19, 0.06),
                        //Apply the gold ore:
                        mix(
                            oreGoldBrightness * sun.brightness *
                              mix(mix(vec3(0.66, 0.62, 0.3),
                                      vec3(0.86, 0.8, 0.35),
                                      oreGoldNoise),
                                  //Apply the gold ore's border:
                                  vec3(1.0, 1.0, 0.3),
                                  oreGoldBorder),
                            //Apply specular color to the gold ore:
                            sun.brightness,
                            oreGoldSpecular
                        ),
                        oreGoldMask
                    ),
                    //Apply the rocks:
                    mix(vec3(0.87, 0.82, 0.85),
                        //Apply the rock border:
                        vec3(0.55, 0.5, 0.6),
                        rockBorderMask),
                    rockValue
                ),
                //Apply the cave hole color:
                vec3(0.032, 0.019, 0.006),
                caveMask
            ),
            //Apply the cave border color:
            pow(vec3(0.032, 0.019, 0.006), vec3(0.2)),
            caveEdgeMask
        ),
        //Apply the floor color to cave floors:
        mix(vec3(0.85, 0.35, 0.15),
            vec3(0.3, 0.85, 0.45),
            caveFloorColorT)
          * caveFloorNoise,
        caveFloorMask
    );
}

vec3 colorTerrain(Camera cam, SunState sun, BufferA bufA)
{
    BufferB bufB = readBufferB(TEX_BUF_B, cam.screenUV);

    //Sample from neighbors to get more information.
    vec2 closeRadius = vec2(1.0),
         farRadius = vec2(2.0);
    BUF_SAMPLE_AROUND_ME(BufferA, TEX_BUF_A, cam.screenUV, closeRadius, bufACloseNeighbors)
    BUF_SAMPLE_AROUND_ME(BufferA, TEX_BUF_A, cam.screenUV, farRadius, bufAFarNeighbors)
    BUF_SAMPLE_AROUND_ME(BufferB, TEX_BUF_B, cam.screenUV, closeRadius, bufBCloseNeighbors)
    BUF_SAMPLE_AROUND_ME(BufferB, TEX_BUF_B, cam.screenUV, farRadius, bufBFarNeighbors)

    //Use a branch to reduce the amount of coloring work per-pixel from 3 biomes to 2.
    //The branch should be pretty coherent, because
    //    biomes are large and don't change vertically.
    #define COLORING_ARGS cam,sun,bufA,bufB,\
                          bufACloseNeighbors,bufAFarNeighbors,bufBCloseNeighbors,bufBFarNeighbors
    if (bufA.biome < 0.5)
        return mix(colorTerrain_Water(COLORING_ARGS),
                   colorTerrain_Plains(COLORING_ARGS),
                   SHARPEN(SHARPEN(smoothstep(0.0, 0.5, bufA.biome))));
    else
        return mix(colorTerrain_Plains(COLORING_ARGS),
                   colorTerrain_Mountains(COLORING_ARGS),
                   SHARPEN(SHARPEN(smoothstep(0.5, 1.0, bufA.biome))));
}


float raymarchedClouds(Camera cam, SunState sun, float time)
{
    float seed = 0.989;
    float cloudStrength = 0.0;
    //"Raymarch" through multiple parallax layers of clouds.
    #define N_ITERATIONS 5
    for (int i = 0; i < N_ITERATIONS; ++i)
    {
        float depthT = float(i) / float(N_ITERATIONS-1);
        float iterSeed = seed + (10.0 * fract(100.0 * depthT));

        //Compute the world position of this layer of clouds under the pixel.
        float cloudSpeed = 0.2 * mix(0.2, 0.01, depthT);
        vec2 cloudPosParallax = vec2(
            (iTime * cloudSpeed) + iterSeed,
            0.0
        );
        float cloudScale = mix(1.0, 1.5, depthT);
        #define CLOUD_NOISE_POS(scale) ((cam.pixelWorld * cloudScale * (scale)) + cloudPosParallax)

        float cloudMask = worley1(CLOUD_NOISE_POS(vec2(0.5, 2.0)),
                                  iterSeed + 0.5721, 0.4);
        cloudMask = 1.0 - cloudMask;
        cloudMask *= pow(cam.pixelWorld.y, 1.21);
        cloudMask = SHARPEN(SHARPEN(SHARPEN(cloudMask)));
        cloudMask = pow(cloudMask, 0.65);

        float sunStrength = 1.0 - SATURATE(distance(cam.pixelWorld, sun.worldPos) - 0.04);
        sunStrength = pow(sunStrength, 20.0);

        float cloudNoise = octaveWorley1Noise(CLOUD_NOISE_POS(2.0),
                                              iterSeed + 8.3423,
                                              4, 2.0,
                                              1.0);
        cloudNoise = pow(1.0 - cloudNoise, 0.9);
        cloudNoise += sunStrength * pow(cloudNoise, 0.95);
        cloudNoise = pow(SATURATE(cloudNoise), 0.75);
        cloudNoise = SHARPEN(cloudNoise);

        float brightness = 1.5;
        cloudStrength += cloudMask * cloudNoise * brightness;
    }

    return SATURATE(cloudStrength / float(N_ITERATIONS));
}
//TODO: Try coarser clouds using a worley-esque approach.

vec3 colorSky(Camera cam, SunState sun, BufferA bufA)
{
    //TODO: If near the terrain, output a "shadow" of it, based on sun direction.

    //Compute sun data.
    float sunField = 1.0 - SATURATE(distance(cam.pixelWorld, sun.worldPos) - 0.04),
          sunStrength = pow(sunField, 50.0);

    //Compute clouds.
    float cloudStrength = raymarchedClouds(cam, sun, iTime);
    //TODO: Color the clouds based on sun direction and distance

    return pow(
        mix(
            //Make the sky bluer near the zenith.
            mix(vec3(0.5, 0.5, 1.0),
                vec3(0.15, 0.15, 1.0),
                cam.pixelWorld.y),
            //Draw the sun:
            vec3(1.0, 0.95, 0.45),
            sunStrength    
        ),
        //Apply clouds:
        vec3(1.0 - cloudStrength)
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.a = 1.0;

    Camera cam = getCamera(fragCoord, iTime, iMouse.xy / iResolution.xy);
    SunState sun = getSunData(cam, iTime);

    BufferA bufA = readBufferA(TEX_BUF_A, cam.screenUV);

    if (cam.pixelWorld.y > bufA.terrHeight)
        fragColor.rgb = colorSky(cam, sun, bufA);
    else
        fragColor.rgb = colorTerrain(cam, sun, bufA);

}

