///////////////////
//    Camera     //
///////////////////

struct Camera
{
    vec2 pixelUV, pixelWorld;
    vec2 screenUV; //Uncorrected for aspect ratio;
                   //   use this to read from buffers

    vec2 horzUVRange, horzWorldRange;

    //Vertical UV and world space are identical, ranging from 0 to 1, for simplicity.
};

Camera getCamera(vec2 fragCoord, float time, vec2 mouseUV)
{
    vec2 uvScreen = fragCoord / iResolution.xy;

    float aspect = iResolution.x / iResolution.y;
    vec2 uv = vec2(uvScreen.x * aspect, uvScreen.y);

    vec3 worldHorzData  //Pos, Min, Max
        = vec3(uv.x, 0.0, aspect);
    float worldY = uv.y;

    //Find a good position for mountain-debugging.
    worldHorzData -= 0.75;

    //Pan rightward.
    //worldHorzData += time * 0.3;

    //Add mouse controls.
    worldHorzData += mouseUV.x * 20.0;

    return Camera(uv, vec2(worldHorzData.x, worldY),
                  uvScreen,
                  vec2(0.0, aspect),
                  vec2(worldHorzData.yz));
}

///////////////
//    Sun    //
///////////////

struct SunState
{
    vec2 dir;
    vec3 brightness;
    vec2 worldPos;
};

SunState getSunData(Camera cam, float time)
{
    vec2 dir = normalize(vec2(-1));

    //Use the sun's X direction to determine its horizontal position.
    // -1 maps to the right edge of the screen; +1 maps to the left edge.
    float horzRange = cam.horzWorldRange.y - cam.horzWorldRange.x;
    vec2 worldPos = vec2(
        mix((cam.horzWorldRange.x + cam.horzWorldRange.y) / 2.0,
            cam.horzWorldRange.y,
            -dir.x),
        0.9
    );

    return SunState(dir, vec3(1), worldPos);
}


/////////////////////////
//    Data buffers     //
/////////////////////////

//Buffer A contains terrain data.
//  Channel R: biome (0.0 is "underwater", 0.5 is "plains", 1.0 is "mountains")
//  Channel G: terrain height (in UV space -- 0 to 1
//  Channel B: mountain cave mask (1 means caves, 0 means solid rock)
//  Channel A: [unused]
struct BufferA
{
    float biome, terrHeight, caveMask;
};
//Buffer B contains various biome-specific data.
// Channel R: Gold ore (0.0 is "none", 0.1-1.0 is used to generate color)
// Channel G: Iron ore (0.0 is "none", 0.1-1.0 is used to generate color)
// Channel B: [unused]
// Channel A: [unused]
struct BufferB
{
    //TODO: Remove the "mask" from the names
    float goldMask, ironMask;
};


//---------------------------
//Buffer-specific helpers:

//From buffer value to real value:
float bufferOreWeight(float oreValue) { return INV_LERP(0.1, 1.0, oreValue); }
//From buffer value to 0-or-1 mask:
float bufferOreMask(float oreValue) { return step(0.05, oreValue); }

//From real masks to buffer value:
float bufferOreValue(float binaryMask, float noiseMask) { return binaryMask * mix(0.1, 1.0, noiseMask); }

//---------------------------

//Packing helpers:
vec4 packBuffer(BufferA buf)
{
    return vec4(buf.biome, buf.terrHeight, buf.caveMask, 1.0);
}
vec4 packBuffer(BufferB buf)
{
    return vec4(buf.goldMask, buf.ironMask, 0, 1);
}

//Unpacking helpers:
void unpackBuffer(vec4 v, out BufferA buf)
{
    buf.biome = v.x;
    buf.terrHeight = v.y;
    buf.caveMask = v.z;
}
void unpackBuffer(vec4 v, out BufferB buf)
{
    buf.goldMask = v.x;
    buf.ironMask = v.y;
}
//One-liner unpacking helpers:
BufferA unpackBufferA(vec4 v) { BufferA b; unpackBuffer(v, b); return b; }
BufferB unpackBufferB(vec4 v) { BufferB b; unpackBuffer(v, b); return b; }

//Reading/unpacking helpers:
void readBuffer(sampler2D tex, vec2 uv, out BufferA buf) { unpackBuffer(textureLod(tex, uv, 0.0), buf); }
void readBuffer(sampler2D tex, vec2 uv, out BufferB buf) { unpackBuffer(textureLod(tex, uv, 0.0), buf); }
//One-liner reading helpers:
BufferA readBufferA(sampler2D tex, vec2 uv) { BufferA b; readBuffer(tex, uv, b); return b; }
BufferB readBufferB(sampler2D tex, vec2 uv) { BufferB b; readBuffer(tex, uv, b); return b; }

//Samples from four neighboring buffer values and stores the results.
#define BUF_SAMPLE_AROUND_ME(BufType, bufTex, uv, radius, outName) \
    BufType outName[4]; { \
        vec3 texel = vec3(vec2(radius) / iResolution.xy, 0.0); \
        readBuffer(bufTex, uv - texel.xz, outName[0]); \
        readBuffer(bufTex, uv - texel.zy, outName[1]); \
        readBuffer(bufTex, uv + texel.xz, outName[2]); \
        readBuffer(bufTex, uv + texel.zy, outName[3]); \
    }
#define BUF_PULL_MINX(outName) outName[0]
#define BUF_PULL_MINY(outName) outName[1]
#define BUF_PULL_MAXX(outName) outName[2]
#define BUF_PULL_MAXY(outName) outName[3]

