float getBiome(float x)
{
    //For testing, use something very simple.
    //The real biome curve is further below.
    float dumbBiome = OSCILLATE(0.0, 1.0, fract(x/4.0) + 0.25);
    return tripleSmoothSmoothstep(0.0, 0.5, 1.0, dumbBiome);

    float raw = OSCILLATE(0.15 + OSCILLATE(0.0, 0.2, x/21.0),
                          0.5 + OSCILLATE(-0.275, 0.4, x/25.0),
                          x/6.7);
    return tripleSmoothSmoothstep(0.0, 0.5, 1.0, raw);
}

float getTerrHeight(float x, float biome)
{
    float mountains = mix(0.7, 1.0,
                          octaveNoise(x*8.0, 2.234, 8, 1.8)
                          * min(1.0, 1.6*worley1(x*2.0, 0.8, 4.31))),
          plains = mix(0.4, 0.6, octaveNoise(x*2.0, 0.7834, 5, 2.0)),
          water = mix(0.0, 0.2, octaveNoise(x*2.0, 1.56927, 3, 8.0));

    return tripleLerp(water, plains, mountains, biome);
}

float getCaveMask(vec2 pos, float biome, float terrainHeight)
{
    //TODO: Get rid of the tiny floating chunks of rock inside caves

    float domainWarp = octaveWorley1Noise(pos * vec2(45, 25),
                                          5.234, 2, 2.0, 0.5);

    vec2 domainOffset = vec2(0, (domainWarp * 1.3 * domainWarp));
    float rawValue = octaveWorley1Noise(pos*vec2(15, 15) + domainOffset,
                                        2.543, 1,
                                        2.0, 1.0);
    return step(mix(0.25, 0.65, pos.y / terrainHeight),
                rawValue * pow(biome, 1.5));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    Camera cam = getCamera(fragCoord, iTime, iMouse.xy / iResolution.xy);

    float biome = getBiome(cam.pixelWorld.x),
          terrHeight = getTerrHeight(cam.pixelWorld.x, biome),
          caveMask = getCaveMask(cam.pixelWorld, biome, terrHeight);

    fragColor = packBuffer(BufferA(biome, terrHeight, caveMask));
}

