#define TEX_BUF_A iChannel1

float getGoldValue(vec2 pos, BufferA bufA,
                  BufferA bufANeighbors[4])
{
    //Remove gold as the mountain biome drops off,
    //    and as the elevation increases.
    float maskExponent = (0.75 + (pos.y * 7.0)) / pow(bufA.biome, 2.5);
    float mask1 = pow(octaveNoise(pos * 2.5, 0.0023453, 5, 3.0),
                      maskExponent),
          mask2 = pow(octaveNoise(pos * 2.5, 0.345, 2, 3.0),
                      1.4);
    #define ORE_GOLD_MASK1 0.35
    #define ORE_GOLD_MASK2 0.45
    float mask = step(ORE_GOLD_MASK1, mask1)
                  * step(ORE_GOLD_MASK2, mask2);

    //Remove gold that's too close to a cave surface.
    mask *= 1.0 - step(0.01, bufA.caveMask
                               + bufANeighbors[0].caveMask + bufANeighbors[1].caveMask
                               + bufANeighbors[2].caveMask + bufANeighbors[3].caveMask);

    float border = min(mask,
                       border(mask1,
                              ORE_GOLD_MASK1, 1.0 - (ORE_GOLD_MASK1/2.0),
                              65.0)
                         + border(mask2,
                                  ORE_GOLD_MASK2, 1.0 - (ORE_GOLD_MASK2/2.0),
                                  55.0));

    float oreNoise = SHARPEN(octaveNoise(pos * 80.0, 8.2523, 4, 2.0));

    return bufferOreValue(mask, oreNoise);
}

