void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec2 q = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;
    p *= 2.0;
    q *= 2.0;
    p = rotmat2(radians(-5.0 * iTime)) * p;
    q = rotmat2(radians(-5.0 * iTime)) * q;

    float t = 0.075;

    // Wythoff parameters
    //// pick point within fundamental triangle
    //// https://mathworld.wolfram.com/TrianglePointPicking.html
    //// http://extremelearning.com.au/evenly-distributing-points-in-a-triangle/
    float a1 = 0.5 + sin(0.25 * iTime) / 2.0;
    float a2 = 0.5 + cos(2.00 * iTime) / 2.0;
    //// generator point
    vec2 g = (((a1 + a2) < 1.0) ? (a1 * v3 + a2 * v2) : ((1.0 - a1) * v3 + (1.0 - a2) * v2));
    //// mirror
    float m = mod(round((iTime - 2.0 / 2.0) / 2.0), 9.0);

    // distance
    float d = sdSchwartz632Plane(p, g, m, t);

    // color
    vec3 col = vec3(0.5);
    col = (d > 0.0) ? col : 0.5 + 0.5 * cos(iTime + p.xyx + vec3(0, 2, 4));
    col *= t - exp(-1.0 * abs(d));
    col *= t + cos(150.0 * d);
    col = mix(col, vec3(0.5), 1.0 - smoothstep(0.0, t, abs(d)));

    //// mouse
    if (iMouse.z > 0.001)
    {
        d = sdSchwartz632Plane(q, g, m, t);
        col = mix(col, vec3(1.0, 1.0, 0.0), 1.0 - smoothstep(0.0, 0.005, abs(length(p - q) - abs(d)) - 0.005));
        col = mix(col, vec3(1.0, 1.0, 0.0), 1.0 - smoothstep(0.0, 0.005, length(p - q) - 0.015));
    }

    // output
    fragColor = vec4(col,1.0);
}

