#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float SK = 0.03;

// thx iq! https://iquilezles.org/articles/distfunctions/
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float barX(vec2 uv, float w){
    uv.x -= clamp( uv.x, -w, w );
    float d = length(uv)-0.02;
    return d;
}

float barY(vec2 uv, float h){
    uv.y -= clamp( uv.y, -h, h );
    float d = length(uv)-0.02;
    return d;
}

float Hash21(vec2 p) {
    p = fract(p*vec2(234.56,789.34));
    p+=dot(p,p+34.56);
    return fract(p.x+p.y);
}

float circle(vec2 uv, float dir) {
    vec2 prevUV = uv;
    float d = 1.0;

    float w[9] = float[](
        0.09,
        0.17,
        0.21,
        0.23,
        0.25,
        0.23,
        0.21,
        0.17,
        0.09
    );

    for(float i = 0.; i<float(w.length()); i++){
        uv = prevUV;      

        uv.x+=dir*sin(dir*iTime*(dir*20.5)+i)*(0.01+(i*0.001));
        uv.y+=(i*0.045);

        uv*=Rot(radians(sin(iTime*(dir*30.0)+i)*5.0+i));

        float d2 = barX(uv,w[int(i)]);
        d = smin(d,d2,SK);
    }
    return d;
}

float bg(vec2 uv, float dir, float num){
    vec2 prevUV = uv;
    float d = 1.0;

    for(float i = 1.; i<=num; i++){
        uv = prevUV;      

        uv.x+=dir*sin(dir*iTime*(dir*20.5)+i)*(0.01+(i*0.001));
        uv.y+=(i*0.045);

        uv*=Rot(radians(sin(iTime*(dir*sin(i)*30.0)+i)));

        float d2 = barX(uv,0.85);
        d = smin(d,d2,SK);
    }
    return d;
}

float triangle(vec2 uv, float dir, float h, float w) {
    vec2 prevUV = uv;
    float d = 1.0;

    for(float i = 1.; i<h; i++){
        uv = prevUV;      

        uv.x+=dir*sin(dir*iTime*(dir*20.5)+i)*(0.01+(i*0.001));
        uv.y+=(i*0.045);

        uv*=Rot(radians(sin(2.*iTime*(dir*sin(i)*30.0))*sin(i*0.5)*5.0));

        float d2 = barX(uv,i*w);
        d = smin(d,d2,SK);
    }
    return d;
}


float cloud(vec2 uv, float dir, float h) {
    vec2 prevUV = uv;
    float d = 1.0;

    for(float i = 1.; i<=h; i++){
        uv = prevUV;      

        uv.x+=dir*sin(dir*iTime*(dir*20.5)+i)*(0.01+(i*0.001));
        uv.y+=(i*0.045);

        uv*=Rot(radians(sin(2.*iTime*(dir*sin(i)*30.0))*sin(i*0.5)*5.0));

        float d2 = barX(uv,sin(i*0.5+0.5)*0.17);
        d = smin(d,d2,SK);
    }
    return d;
}

vec3 tree(vec2 uv, vec3 col, float dir){
    vec2 prevUV = uv;
    uv.y+=0.27;
    uv*=Rot(radians(2.*sin(iTime*(dir*20.0))*3.0));
    float d = barY(uv,0.2);
    col=mix(col,vec3(0.3,0.3,0.2),S(d,0.0)); 

    uv = prevUV;
    d = triangle(uv,dir, 6., 0.03);
    uv.y+=0.15;
    float d2 = triangle(uv,dir, 6., 0.03);
    d = smin(d,d2,SK);
    col=mix(col,mix(vec3(0.5,0.8,0.5),vec3(0.3,0.4,0.3),uv.y*-3.),S(d,0.0)); 
    return col;
}

vec3 treeSmall(vec2 uv, vec3 col, float dir){
    vec2 prevUV = uv;
    uv.y+=0.42;
    uv*=Rot(radians(2.*sin(iTime*(dir*20.0))*3.0));
    float d = barY(uv,0.1);
    col=mix(col,vec3(0.4,0.4,0.3),S(d,0.0)); 

    uv = prevUV;
    uv.y+=0.2;
    d = triangle(uv,dir, 6., 0.03);
    col=mix(col,mix(vec3(0.3,0.9,0.3),vec3(0.3,0.4,0.3),uv.y*-3.),S(d,0.0)); 
    return col;
}

vec3 grass(vec2 uv, vec3 col, float dir){
    vec2 prevUV = uv;
    float d = 1.0;

    for(float i = 1.; i<=6.; i++){
        uv = prevUV;      

        uv.x+=(i*0.05);        
        uv*=Rot(radians(sin(2.*iTime*(dir*sin(i)*30.0))*10.0));

        float d2 = barY(uv,0.03+sin(i)*0.01);
        d = smin(d,d2,SK);
    }
    col=mix(col,mix(vec3(0.1,0.2,0.1),vec3(0.3,0.4,0.3),uv.y*-10.),S(d,0.0)); 
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;


    float glitchBlock = 30.0;
    vec2 uv2 = fract(uv*glitchBlock)-0.5;
    vec2 id = floor(uv2);

    vec2 n2 = fract(sin(id*123.456)*789.125);
    n2+=dot(id.x,id.y*567.89);

    float glitchDist = 0.02;
    float glitchTime = iTime*10.0;
    uv.x-=(fract(floor(uv.y+n2.y*glitchBlock)*glitchTime)*glitchDist);
    uv.x+=sin(floor(uv.y*glitchBlock*10.0)*iTime*30.)*0.01;


    vec2 prevUV = uv;

    vec3 col = vec3(1.0);

    uv.y-=0.52;
    float d = bg(uv,-1.0,19.);
    col=mix(col,mix(vec3(0.6,0.8,1.0),vec3(0.5,0.9,1.0),uv.y),S(d,0.0));
    uv = prevUV;

    uv.y+=0.35;
    d = bg(uv,1.0,3.);
    col=mix(col,mix(vec3(0.6,0.3,0.2),vec3(0.3,0.2,0.1),uv.y*10.0),S(d,0.0));

    uv = prevUV;
    uv.x-=0.45;
    uv.y-=0.45;
    d = circle(uv,1.0);
    col=mix(col,mix(vec3(0.9,0.83,0.63),vec3(0.7,0.7,0.6),uv.y*20.0),S(d,0.0));    
    uv = prevUV;

    uv.x+=iTime*0.2;
    uv.x = mod(uv.x,2.0)-1.;
    d = triangle(uv,1.0, 9., 0.05);
    col=mix(col,mix(vec3(0.2,0.4,0.2),vec3(0.1,0.3,0.1),uv.y*10.0),S(d,0.0));    
    uv = prevUV;

    uv.x+=iTime*0.2;
    uv.x-=1.35;
    uv.x = mod(uv.x,2.0)-1.;
    uv.y+=0.03;
    d = triangle(uv,-1.2, 8., 0.05);
    col=mix(col,mix(vec3(0.2,0.3,0.2),vec3(0.1,0.2,0.1),uv.y*5.0),S(d,0.0));    
    uv = prevUV;      

    uv.x+=iTime*0.2;
    uv.x-=0.9;
    uv.x = mod(uv.x,2.0)-1.;
    uv.y+=0.13;
    d = triangle(uv,1.3, 6., 0.05);
    col=mix(col,mix(vec3(0.2,0.5,0.2),vec3(0.1,0.2,0.1),uv.y*4.0)*0.8,S(d,0.0));    
    uv = prevUV;       

    uv.x+=iTime*0.2;
    uv.x-=0.5;
    uv.x = mod(uv.x,2.0)-1.;
    uv.y+=0.1;
    d = triangle(uv,-1.0, 7., 0.05);
    col=mix(col,mix(vec3(0.2,0.6,0.2),vec3(0.1,0.2,0.1),uv.y*3.0)*0.8,S(d,0.0));    
    uv = prevUV;    

    uv.x+=iTime*0.25;
    uv.x = mod(uv.x,1.6)-0.8;
    uv.y-=0.45;
    d = cloud(uv,-1.2, 4.);
    col=mix(col,mix(vec3(0.88),vec3(0.5),uv.y*2.0),S(d,0.0)); 
    uv.x-=0.2;
    uv.y-=0.05;
    d = cloud(uv,1.2, 3.);
    col=mix(col,mix(vec3(0.92),vec3(0.6),uv.y),S(d,0.0));
    uv.x-=0.4;
    uv.y+=0.28;
    uv.x*=1.6;
    d = cloud(uv,-1.3, 3.);
    col=mix(col,mix(vec3(0.92),vec3(0.6),uv.y*-3.0),S(d,0.0));  
    uv = prevUV;    

    uv*=2.0;
    uv.x+=iTime*0.3;
    uv.x = mod(uv.x,1.2)-0.6;
    uv.y+=0.4;
    col = tree(uv,col,1.0);
    uv = prevUV;    

    uv*=2.0;
    uv.x+=iTime*0.3;
    uv.x +=0.5;
    uv.x = mod(uv.x,1.2)-0.6;
    uv.y+=0.4;
    col = treeSmall(uv,col,-1.1);
    uv = prevUV; 

    uv.x+=iTime*0.3;
    uv.x = mod(uv.x,0.6)-0.3;
    uv.y+=0.5;
    col = grass(uv,col,1.0);

    uv.x+=iTime*2.0;
    col+=Hash21(uv*100.0)*0.3;

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define BACKGROUND_COLOR vec3(0.1, 0.2, 0.6)
#define SECONDARY_COLOR vec3(0.49, 0.45, 0.655)
#define BIG_STAR_SIZE 16.
#define MEDIUM_STAR_RATIO 0.3
#define BIG_STAR_CHANCE 0.1
#define STAR_COLOR vec3(0.9, 0.7, 1.0)
#define SPACE_DUST_COLOR vec3(0.432941, 0.38627, 0.69020)

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

vec3 hash(vec3 p3)
{    
	p3 = fract(p3 * vec3(.1031,.11369,.13787));
    p3 += dot(p3, p3.yxx+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}


float voronoi(vec2 n, float t) {

    vec3 p = vec3(n.x, n.y, t);
    const float K1 = 0.333333333;
    const float K2 = 0.166666667;

    vec3 i = floor(p + (p.x + p.y + p.z) * K1);
    vec3 d0 = p - (i - (i.x + i.y + i.z) * K2);

    vec3 e = step(vec3(0.0), d0 - d0.yzx);
	vec3 i1 = e * (1.0 - e.zxy);
	vec3 i2 = 1.0 - e.zxy * (1.0 - e);

    vec3 d1 = d0 - (i1 - 1.0 * K2);
    vec3 d2 = d0 - (i2 - 2.0 * K2);
    vec3 d3 = d0 - (1.0 - 3.0 * K2);

    vec4 h = max(0.6 - vec4(dot(d0, d0), dot(d1, d1), dot(d2, d2), dot(d3, d3)), 0.0);
    vec4 q = h * h * h * h * vec4(dot(d0, hash(i)), dot(d1, hash(i + i1)), dot(d2, hash(i + i2)), dot(d3, hash(i + 1.0)));

    return dot(vec4(50.), q);

}


float voronoiFbm(vec2 p, int iter)
{
    float f = 0.0;
    float a = 1.0;
    float norm = 0.0;

    for (int i = 0; i < iter; i++) {
	    f += a * abs(voronoi(p, 0.0)); 
        norm += a;
        p *= 2.0;
        a *= 0.65;
    }

    return 1.0 - f / norm;
}

float fbm(vec2 p, int iter, float freq, float amp)
{
    float n = 0.0;
    float a = 1.0;
    float norm = 0.0;
    for(int i = 0; i < iter; ++i)
    {
        n += noise(p) * a;
        norm += a;
        p *= freq;
        a *= amp;
    }
    return n / norm;
}


float fbm(vec2 p, int iter)
{
    return fbm(p, iter, 2.0, 0.5);
}

vec3 starColor(vec2 p)
{
    return sin(vec3(.2, .3, .9)*fract(rand(p + vec2(7.91, 246.792))*2345.2)*123.2)*vec3(0.15, 0.1, 0.15) + 0.75;
}

void stars(inout vec3 col, vec2 fragCoord, float density)
{
    vec2 p = fragCoord;
    if(rand(vec2(ivec2(fragCoord / 2.0)) * 2.0) < MEDIUM_STAR_RATIO)
        fragCoord = vec2(ivec2(fragCoord / 2.0)) * 2.0;
    vec3 star = col * 0.5 + 0.5 * starColor(p);

    if(rand(fragCoord + vec2(1071.52, -662.1)) > 0.0) return;
    col = star;
}

void bigStars(inout vec3 col, vec2 fragCoord, float density)
{
    vec2 p = vec2(ivec2(fragCoord / BIG_STAR_SIZE)) * BIG_STAR_SIZE;
    float star_size = BIG_STAR_SIZE * pow(rand(p), 2.0);
    vec3 star = col * 0.5 + 0.5 * starColor(p);
    float d = max(1.0 - distance(p + star_size * 0.5, fragCoord) / star_size * 2.0, 0.0);
    star += d * d * d * d;

    if(rand(p + vec2(6181.616, 9028.1)) > BIG_STAR_CHANCE) return;
    col = mix(col, star, d * d * d);
}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = BACKGROUND_COLOR; // Background
    float primary = sqrt(fbm(uv * 3.0 + vec2(6.175, -23.612), 8, 1.5, 0.5)); // Space lighness
    float secondary = fbm(uv * 4.0, 8);
    float nebula = 4.0 * pow(fbm(uv * 4.0 + vec2(-12.1, 2.62), 18, 2.0, 0.5), 5.0);
    float space = primary * secondary * nebula;
    vec3 variation = normalize(vec3(fbm((uv + vec2(11.61)) * 2.0, 8), fbm((uv - vec2(2.11)) * 2.0, 8), fbm((uv + vec2(66.61)) * 2.0, 8)));

    col *= space;
    col = mix(col, secondary * SECONDARY_COLOR, 0.5); // Nebula
    col = mix(variation, col, 0.93);
    col += SPACE_DUST_COLOR * vec3(pow(fbm(uv * 12.0, 18, 2.0, 0.5), 3.0) * 0.5);
    col += vec3(0.7, 0.6, 0.5) * nebula;
