            for(float j = 3.; j > 2.; j-=.3)
            {

        		vec3 wp = p;

        		wp = opCheapBend(wp, sin(iTime*14.) * .2 * sin(iTime));

        		wp.x += j * i;
            	wp.x += .1;
        		wp.z += .6;
        		wp.y -= .2;
