        //wp.z += abs(sin(wp.x*1.5));

        //if(wp.x > 1.)
        	wp.z += sin(wp.x + PI/2.)*2.;
        wp.x *= 1.5;

        wp.x *= 1. + wp.z*.9;
        wp.x *= 1.4;

        // texturing
        wp.y *= 1. - sin(wp.x*25.)*.3;

    	tail = sdBox(wp, vec3(.5, .05, .4)) * .7;
    }
    bird = opSmoothUnion(bird, tail, .25);


    // return
    return bird;
}

float raymarch(in vec3 ro, in vec3 rd)
{
    float dist = 0.;
    for(int i = 0; i < 90; i++)
    {
		float m = model(ro+rd*dist);
        dist += m;

        if(m < .05) return dist;
        else if(dist > 30.) break;
    }
    return -1.;
}

vec3 normal(vec3 pos)
{
    vec3 eps = vec3(.01, -.01, 0.);

    return normalize(vec3(
        model(pos + eps.xzz) - model(pos + eps.yzz),
        model(pos + eps.zxz) - model(pos + eps.zyz),
        model(pos + eps.zzx) - model(pos + eps.zzy)));
}

float shadow(in vec3 pos, in vec3 ld)
{
    float spread = 3.;
    float res = 1.0;
	for(float t = .2; t < .4;)
    {
        float dist = model(pos+ld*t);
        if(dist<.001) return 0.;
        res = min(res, spread*dist/t);
        t += dist;
    }
    return res;
}

vec3 background()
{
	return vec3(0.);  
}

vec3 shade(vec3 pos, vec3 nor, vec3 rd, float dist)
{
    if(dist < 0.) return background();

    vec3 ld = normalize(vec3(2., 10., 2.));
    //vec3 ld = normalize(lp-pos);

    vec3 lightColor = vec3(.9, .6, .1) * 1.8;

    float dif = max(dot(nor,ld),0.);
    float sha = 0.;
    if(dif > .01) sha = shadow(pos, ld);
    vec3 lin = vec3(dif*sha);

    vec3 col = lin;

    // bird
    col *= lightColor;

    col = min(col, vec3(1.));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord - .5*iResolution.xy)/iResolution.y;

    float zoom = 3.;
    vec3 ro = vec3(sin(iTime*.5)*zoom, zoom * 2., cos(iTime*.5)*zoom); 
    vec3 ta = vec3(0.);				

    vec3 w = normalize (ta-ro);
    vec3 u = normalize (cross (w, vec3(0., 1., 0.)));
    vec3 v = normalize (cross (u, w));
    mat3 mat = mat3(u, v, w);
    vec3 rd = normalize (mat*vec3(p.xy,.5));

    float dist = raymarch(ro, rd);
    vec3 pos = ro+rd*dist;
    vec3 nor = normal(pos);

    vec3 col = shade(pos, nor, rd, dist);

    col = mix(col, background(), pow(dist/30., 2.));

    col = vec3(dot(col.rgb, vec3(0.299, 0.587, 0.114)));

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 gamma(vec3 linear) {
    return mix(1.055 * pow(linear, vec3(1.0 / 2.4)) - 0.055, 12.92 * linear, lessThanEqual(linear, vec3(0.0031308)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    float x;
    if (uv.x < 0.5 && uv.y > 0.5) {
        x = fragCoord.x + fragCoord.y;
    } else if (uv.x > 0.5 && uv.y < 0.5) {
        x = 0.5;
    } else if (fragCoord.x + 0.5 * iResolution.y < fragCoord.y + 0.5 * iResolution.x ^^ uv.x < 0.5) {
        x = fragCoord.y - 0.5 + float(uv.x > 0.5);
    } else {
        x = fragCoord.x - 0.5 + float(uv.x > 0.5);
    }
    vec3 col = 0.5 + 0.5 * cos(iTime + vec3(0, 2, 4));
    fragColor = vec4(vec3(mod(x, 2.0)) * col, 1);
    if (iMouse.z <= 0.0) fragColor.rgb = gamma(fragColor.rgb);
}

