struct Ray
{
    vec3 ro;
    vec3 rd;
};

struct Material
{
  int materialType;
  vec3 normal;
  vec3 baseColor;
  float fuzz;
  float refractionIndex;
};

struct HitResult
{
  float t;
  vec3 hitPoint;
  Material material;
  bool front_face;
};

struct Sphere
{
    vec4 sph;
    Material material;
};

struct Camera
{
    vec3 origin;
    vec3 lowerLeftCorner;
    vec3 horizontal;
    vec3 vertical;
    vec3 u, v, w;
    float lensRadius;
};

vec3 set_face_normal(Ray r, vec3 outward_normal)
{
    float front_face = dot(r.rd, outward_normal);
    if(front_face < 0.)
        return outward_normal;
    else
        return -outward_normal;
}


vec3 nSphere(vec3 pos, Sphere s ) {
    return (pos-s.sph.xyz)/s.sph.w;
}

bool iSphere(Ray r, Sphere s, float t_min, float t_max, inout HitResult res)
{
  vec3 oc = r.ro - s.sph.xyz;
  float a = dot(r.rd, r.rd);
  float h = dot(oc, r.rd);
  float c = dot(oc, oc) - s.sph.w * s.sph.w;
  float discriminant = h * h - a * c;
  vec3 outward_normal;

  if(discriminant > 0.)
  {
      float root = sqrt(discriminant);
      float temp = (-h - root) / a;
      if(temp < t_max && temp > t_min)
      {
          res.t = temp;
          res.hitPoint = r.ro + res.t * r.rd;
          outward_normal = nSphere(res.hitPoint, s);
          res.front_face = dot(r.rd, outward_normal) < 0.;

          res.material = s.material;
          res.material.normal = set_face_normal(r, outward_normal);
          return true;
      }
      temp = (-h + root) / a;
      if(temp < t_max && temp > t_min)
      {
          res.t = temp;
          res.hitPoint = r.ro + res.t * r.rd;
          outward_normal = nSphere(res.hitPoint, s);
          res.front_face = dot(r.rd, outward_normal) < 0.;

          res.material = s.material;
          res.material.normal = set_face_normal(r, outward_normal);
          return true;
      }
  }
  return false;    
}

Material m_Lambertian(vec3 baseColor)
{
	return Material(LAMBERT, vec3(0.), baseColor, 0., 0.);
}

Material m_Metal(vec3 baseColor, float fuzz)
{
	return Material(METAL, vec3(0.), baseColor, fuzz, 0.);
}

Material m_Dielectric(float refractionIndex)
{
	return Material(DIELECTRIC, vec3(0.), vec3(0.), 0., refractionIndex);
}

float schlick(float cos_theta, float n2)
{
    const float n1 = 1.0f;  // refraction index for air

    float r0s = (n1 - n2) / (n1 + n2);
    float r0 = r0s * r0s;

    return r0 + (1.0f - r0) * pow((1.0f - cos_theta), 5.0f);
}

bool refractVec(vec3 v, vec3 n, float ni_over_nt, out vec3 refracted)
{
    vec3 uv = normalize(v);

    float dt = dot(uv, n);

    float discriminant = 1.0 - ni_over_nt * ni_over_nt * (1.0f - dt * dt);

    if (discriminant > 0.0f)
    {
        refracted = ni_over_nt*(uv - n * dt) - n * sqrt(discriminant);

        return true;
    }
    else
        return false;
}

bool lambertian(Ray r, HitResult res, out vec3 attenuation, out Ray scattered) 
{
    scattered = Ray(res.hitPoint, res.material.normal + random_in_unit_sphere());
	attenuation = res.material.baseColor;
	return true;
}

bool metal(Ray r, HitResult res, out vec3 attenuation, out Ray scattered) 
{
    float fuzz = res.material.fuzz;
    vec3 reflected = reflect(normalize(r.rd), res.material.normal);
    scattered = Ray(res.hitPoint, reflected + fuzz * random_in_unit_sphere());
    attenuation = res.material.baseColor;
    return (dot(scattered.rd, res.material.normal) > 0.0);   
}

bool dielectric(Ray r, HitResult res, out vec3 attenuation, out Ray scattered) 
{
	attenuation = vec3(1.);
    float etai_over_etat = (res.front_face) ? (1.0 / res.material.refractionIndex) : (res.material.refractionIndex);

    vec3 unit_direction = normalize(r.rd);
    float cos_theta = min(dot(-unit_direction, res.material.normal), 1.0);
    float sin_theta = sqrt(1.0 - cos_theta*cos_theta);
    if (etai_over_etat * sin_theta > 1.0 ) {
        vec3 reflected = reflect(unit_direction, res.material.normal);
        scattered = Ray(res.hitPoint, reflected);
        return true;
    }
    float reflect_prob = schlick(cos_theta, etai_over_etat);
    if (random() < reflect_prob)
    {
        vec3 reflected = reflect(unit_direction, res.material.normal);
        scattered = Ray(res.hitPoint, reflected);
        return true;
    }
    vec3 refracted = refract(unit_direction, res.material.normal, etai_over_etat);
    scattered = Ray(res.hitPoint, refracted);
    return true;
}

bool material_BSDF(HitResult res, Ray wo, out Ray wi, out vec3 attenuation)
{
    int materialType = res.material.materialType;

    if(materialType == LAMBERT)
    {
        return lambertian(wo, res, attenuation, wi);
    }
    else if(materialType == METAL)
    {
        return metal(wo, res, attenuation, wi);
    }
    else if(materialType == DIELECTRIC)
    {
    	return dielectric(wo, res, attenuation, wi);
    }

    return false;
}

bool iScene(Ray r, float t_min, float t_max, out HitResult tempRes)
{
    tempRes.t = t_max;
    bool hit = false;

	Sphere s1 = Sphere(vec4(0., -1000, 0., 1000.), m_Lambertian(vec3(0.5)));
    if(iSphere(r, s1, t_min, tempRes.t, tempRes))
    {
        hit = true;
    }

    Sphere s2 = Sphere(vec4(0, 1, 0, 1.0), m_Dielectric(1.5));
    if(iSphere(r, s2, t_min, tempRes.t, tempRes))
    {
        hit = true;
    }

    Sphere s3 = Sphere(vec4(-4, 1, 0, 1.0), m_Lambertian(vec3(0.4, 0.2, 0.1)));
    if(iSphere(r, s3, t_min, tempRes.t, tempRes))
    {
        hit = true;
    }

    Sphere s4 = Sphere(vec4(4, 1, 0, 1.0), m_Metal(vec3(0.7, 0.6, 0.5), 0.0));
    if(iSphere(r, s4, t_min, tempRes.t, tempRes))
    {
        hit = true;
    }

    for(int a = -11; a < 11; a++)
        for(int b = -11; b < 11; b++)
        {
            float m_seed = float(a) + float(b)/1000.;
            vec3 rand1 = hash3(m_seed);            
            vec3 center = vec3(float(a)+.9*rand1.x,.2,float(b)+.9*rand1.y); 
            float choose_mat = rand1.z;

            if (distance(center,vec3(4,.2,0)) > .9) 
            {
                if (choose_mat < 0.8) 
                { 
                    Sphere s = Sphere(vec4(center, 0.2), m_Lambertian(hash3(m_seed)* hash3(m_seed)));
                    if (iSphere(r, s, t_min, tempRes.t, tempRes))
                        hit=true;
                } 
                else if (choose_mat < 0.95) 
                { 
                    Sphere s = Sphere(vec4(center, 0.2), m_Metal(.5*(hash3(m_seed)+1.),.5*hash1(m_seed)));
                    if (iSphere(r, s, t_min, tempRes.t, tempRes))
                        hit=true;
                } 
                else 
                { 
                    Sphere s = Sphere(vec4(center, 0.2), m_Dielectric(1.5));
                    if (iSphere(r, s, t_min, tempRes.t, tempRes)) 
                        hit=true;
                }
            }
        }

    return hit;
}

vec3 ray_color(Ray r, vec2 uv)
{
    vec3 col = vec3(1.);

    HitResult res;
    Ray scattered;
    vec3 attenuation;

    for(int i = 0; i < depth; i++)
    {
		if(iScene(r, MIN_FLOAT, MAX_FLOAT, res))
		{
			bool wasScattered = material_BSDF(res, r, scattered, attenuation);
			r.ro = scattered.ro;
			r.rd = scattered.rd;

			if(wasScattered)
				col *= attenuation;
			else
			{
				col *= vec3(0.);
				break;
			}
		}
		else
		{
			vec3 unit_direction = normalize(r.rd);
            float t = 0.5*(unit_direction.y+1.0);
            col *= mix(vec3(1.),vec3(.5,.7,1.),t);
            return col;
		}
    }

    return vec3(0.);	
}

void Camera_init(out Camera camera, vec3 lookfrom, vec3 lookat, vec3 vup, float vfov, float aspect, float aperture, float focusDist)
{
    camera.lensRadius = aperture / 2.0;

    float theta = vfov * pi / 180.0;
    float halfHeight = tan(theta / 2.0);
    float halfWidth = aspect * halfHeight;

    camera.origin = lookfrom;

    camera.w = normalize(lookfrom - lookat);
    camera.u = normalize(cross(vup, camera.w));
    camera.v = cross(camera.w, camera.u);

    camera.lowerLeftCorner = camera.origin  - halfWidth  * focusDist * camera.u
                                            - halfHeight * focusDist * camera.v
                                            -              focusDist * camera.w;

    camera.horizontal = 2.0 * halfWidth  * focusDist * camera.u;
    camera.vertical   = 2.0 * halfHeight * focusDist * camera.v;
}


Ray Camera_getRay(Camera camera, float s, float t)
{
    vec3 rd = camera.lensRadius * random_in_unit_sphere();
    vec3 offset = camera.u * rd.x + camera.v * rd.y;

    Ray r;

    r.ro = camera.origin + offset;
    r.rd = camera.lowerLeftCorner + s * camera.horizontal + t * camera.vertical - camera.origin - offset;

    return r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    seed = iTime;
    UV = fragCoord / iResolution.xy;

    vec3 lookfrom = vec3(13, 2, 3);
    const vec3 lookat = vec3(0.0, 0.0, 0.);
    float aspect_ratio = iResolution.x / iResolution.y;
    float distToFocus = 10.;
    float aperture = 0.1;

    Ray r;
    Camera cam;
    vec3 col;
    vec2 uv;


    for(int i = 0; i < spp; i++)
    {   
        uv = (fragCoord.xy + vec2(random(), random())) / iResolution.xy;

        Camera_init(cam, lookfrom, lookat, vec3(0., 1., 0.), 20., aspect_ratio, aperture, distToFocus);
	    r = Camera_getRay(cam, uv.x, uv.y);

        col += ray_color(r, uv);
    }

    col /= float(spp);

    col = pow(clamp(col, 0., 1.), vec3(0.45));

    vec3 lastFrameColor = texture(iChannel0, fragCoord / iResolution.xy).rgb;
    col = mix(lastFrameColor, col, 1.0f / float(iFrame+1));

    fragColor = vec4(col,1.0);
}

