float rand(vec2 co)
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0); 
    int points = 4;
     vec2 array[4] = vec2[4](
     vec2(0.1+0.2*sin(iTime), 0.3),
     vec2(0.3+cos(iTime)*0.05, 0.1),
     vec2(0.8+sin(iTime)*0.02, 0.7-cos(iTime)*0.01),
     vec2(0.1, 0.3));
     float dmin = 1000.0;
     int point = 0; 
     vec2 factor = vec2(100.0, 100.0*iResolution.y/iResolution.x);
     for (int i = 0; i < points; i++){
         float uv_x = float(int(uv.x*factor.x))/factor.x;
         float uv_y = float(int(uv.y*factor.y))/factor.y;

         float uv_x_a = float(int(array[i].x*factor.x))/factor.x;
         float uv_y_a = float(int(array[i].y*factor.y))/factor.y;
         if(vec2(uv_x, uv_y) == vec2(uv_x_a, uv_y_a)){
             col = vec3(1,0,0);
         }

         if(length(array[i] - uv) < dmin){
             point = i; 
             dmin = length(array[i] - uv);
         }
     }

    // Output to screen
    vec2 vector = vec2(0.2*float(point),0.2*float(point));
        vec2 vector2 = vec2(sin(0.2*float(point)),cos(0.2*float(point)));
                vec2 vector3 = vec2(cos(0.2*float(point)),sin(0.2*float(point)));

    fragColor = vec4(col+vec3(rand(vector),rand(vector2), rand(vector3)),1.0);
}

#define NUM_COLORS 2

float sdSphere( vec3 p, float s )
{
    p.y += sin(iTime*4.)/20. +.08;
  return length(p)-s;
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), -1.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdOcta( vec3 p, float s)
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float model(vec3 p)
{
    //vec2 mouse = iMouse.xy/iResolution.xy * 1.;
    vec2 mouse = vec2(cos(iTime*.1), sin(iTime*.1)) * .5 + .5;
    float time = iTime + mouse.x*1.5;

    p *= 1.;

    // movement
    p.x += sin(time*8.0)/30.;
  	p.z += cos(time*8.0)/30.;
    p.y += sin(time*4.0)/10. - .1;

    float octaWidth = .5 + mouse.x*.25;
    float octa = sdOcta(p + vec3(octaWidth*sin(time), 0., octaWidth*cos(time)), .25);
    octa = opSmoothUnion(octa, sdOcta(p + vec3(octaWidth*sin(time + 3.14), 0., octaWidth*cos(time + 3.14)), .25), cos(time*8.)*.1 + .5);
    octa = opSmoothUnion(octa, sdOcta(p + vec3(octaWidth*cos(time), 0., octaWidth*sin(time + 3.14)), .25), cos(time*8.)*.1 + .5);
    octa = opSmoothUnion(octa, sdOcta(p + vec3(octaWidth*cos(time + 3.14), 0., octaWidth*sin(time)), .25), cos(time*8.)*.1 + .5);


    float shiftAmount = 15. - mouse.y*10.;

    p.x += sin(p.x*30. + time)/shiftAmount;
    p.z += cos(p.z*30. + time)/shiftAmount;

    p.x = abs(p.x);
    p.z = abs(p.z);
    p.y = abs(p.y);

    // sizing
    p*= .7;

    float torus = sdTorus (p + vec3(0., 0., 0.), vec2(0.25,0.05));
    float sphere = sdSphere(p, .2);

    float dist = opSmoothUnion(torus, sphere, .1 + cos(time*8.0)*.05);
    dist = opSmoothUnion(dist, octa, mouse.x*.4);
    return dist * .9;
}

float raymarch(in vec3 ro, in vec3 rd)
{
    float dist = 0.;
    for(int i = 0; i < 90; i++)
    {
		float m = model(ro+rd*dist);
        dist += m;

        if(m < .01) return dist;
        else if(dist > 20.) break;
    }
    return -1.;
}

vec3 normal(vec3 pos)
{
    vec3 eps = vec3(.01, -.01, 0.);

    return normalize(vec3(
        model(pos + eps.xzz) - model(pos + eps.yzz),
        model(pos + eps.zxz) - model(pos + eps.zyz),
        model(pos + eps.zzx) - model(pos + eps.zzy)));
}

float shadow(in vec3 pos, in vec3 ld)
{
    float spread = 3.;
    float res = 1.0;
	for(float t = .2; t < .4;)
    {
        float dist = model(pos+ld*t);
        if(dist<.001) return 0.;
        res = min(res, spread*dist/t);
        t += dist;
    }
    return res;
}

vec3 getColor(int r)
{
    switch(r)
    {
        case 0:
        	return vec3(86., 48., 107.) 	/256.;
        case 1:
        	return vec3(98., 67., 198.) 	/256.;
        case 2:
        	return vec3(102., 165., 250.)	/256.;
        case 3:
        	return vec3(186., 250., 236.)	/256.;
    }

    return vec3(0.);
}

vec3 shade(vec3 pos, vec3 nor, vec3 rd, float dist)
{
    if(dist < 0.) return vec3(0., 1., 0.);

    vec3 lp = vec3(sin(iTime*2.0)*2.0, 4.0, cos(iTime*2.0)*2.0);
    vec3 ld = normalize(lp-pos);

    float dif = max(dot(nor,ld),0.);
    float sha = 0.;
    if(dif > .01) sha = shadow(pos, ld);
    vec3 lin = vec3(dif*sha);

    float sharp = .25;
    float inten = 1.0;
    vec3 ref = 2.0 * dot(ld, nor)*nor-ld;
    lin += pow(max(0., dot(-rd, ref)), sharp) * vec3(inten) * sha;

    vec3 col = lin;
    col *= exp(-.01*dist*dist);

    // toon
    col *= vec3(NUM_COLORS);
    float colFract = abs(sin(fract(col.r) * 3.14 * 1.5));
    col = floor(col);


    // pallete
    //col = getColor(int(col.r));
    col = mix(getColor(int(col.r-1.)), getColor(int(col.r)), colFract);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord - .5*iResolution.xy)/iResolution.y;
    p.x = abs(p.x) - .1;
    p *= .7;

    int pix = int(fragCoord.x + fragCoord.y);
    float dither = float(pix%2)*.6;

    vec3 ro = vec3(1. + dither, 1. + dither, 1. + dither); 
    vec3 ta = vec3(0.);				

    vec3 w = normalize (ta-ro);
    vec3 u = normalize (cross (w, vec3(0., 1., 0.)));
    vec3 v = normalize (cross (u, w));
    mat3 mat = mat3(u, v, w);
    vec3 rd = normalize (mat*vec3(p.xy,.5 + sin(iTime)*.1));

    float dist = raymarch(ro, rd);
    vec3 pos = ro+rd*dist;
    vec3 nor = normal(pos);

    vec3 col = shade(pos, nor, rd, dist);

    // Output to screen
    fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Spherical texturing with Mercator projection, with indexed tiles
// moving along loxodromes.
//
// Matthew Arcus, mla, 2022.
//
////////////////////////////////////////////////////////////////////////////////

vec3 indexed(vec2 uv) {
  float t = -PI/4.0;
  vec2 uv0 = uv;
  uv = rotate(uv,t);
  uv *= 4.0/cos(t);
  uv0 *= 4.0/cos(t);
  uv.y += 0.1*iTime*(mod(uv.x,2.0) < 1.0 ? 1.0 : -1.0);
  uv = mod(uv,8.0);
  ivec2 iv = ivec2(floor(uv));
  float rnd = float(ihash((uint(iv.x)<<16) ^ uint(iv.y)))/pow(2.0,32.0);
  uv = mod(uv,1.0);
  vec2 dv = min(uv,1.0-uv);
  float d = min(dv.x,dv.y);
  vec3 tex = textureGrad(iChannel0,uv,vec2(dFdx(uv0.y)),vec2(dFdy(uv0.y))).rgb;
  float tone = dot(tex,tex);
  vec3 col = hsv2rgb(vec3(rnd,0.5,1));
  col *= 0.8*tone;
  col = mix(vec3(0),col,smoothstep(0.01,0.02,d));
  return col;
}

vec3 getColor(vec3 p) {
  float x = atan(p.x,p.y);       // Longitude
  float y = asin(p.z/length(p)); // Latitude
  y = log(tan(0.25*PI+0.5*y));   // Mercator
  vec2 uv = vec2(x,y);
  uv /= PI;
  return indexed(uv);
}

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // normal
};

struct Sphere {
  float r;      // radius
  vec3 p;       // centre
};

bool intersectSphere(Sphere s, Ray ray, out Hit hit) {
  vec3 p = s.p;
  float r = s.r;
  float c = length(p);
  vec3 q = ray.q, d = ray.d;
  // |q + t*d - p|^2 = r^2
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+(c+r)*(c-r);
  float D = B*B - C;
  if (D < 0.0) return false;
  D = sqrt(D);
  float t,t1;
  if (B >= 0.0) {
    t = -B-D; t1 = C/t;
  } else {
    t1 = -B+D; t = C/t1;
  }
  if (t < 0.0) t = t1;
  if (t < 0.0) return false;
  // Normal is the radial vector of sphere
  hit = Hit(t, (q+t*d-p)/r);
  return true;
}

bool intersectScene(Ray r, out Hit hit) {
  Sphere s = Sphere(1.0, vec3(0));
  if (intersectSphere(s,r,hit)) {
    return true;
  }
  return false;
}

vec3 light;
float ambient;
float diffuse;

vec3 solve(Ray r) {
  Hit hit;
  if (!intersectScene(r,hit)) {
    return vec3(0);
  } else {
    vec3 n = hit.n;
    if (dot(r.d,n) > 0.0) n *= -1.0;
    vec3 baseColor = getColor(hit.n);
    vec3 color = baseColor.xyz*(ambient+diffuse*max(0.0,dot(light,n)));
    float specular = pow(max(0.0,dot(reflect(light,n),r.d)),10.0);
    color += 0.5*specular;
    //color *= clamp(1.0 - (hit.t-3.0)/5.0,0.0,1.0);
    return color;
  }
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = -(2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = -(2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (true) {
    p.yz = rotate(p.yz,iTime * 0.1618);
    p.zx = rotate(p.zx,iTime * 0.1);
  }
  return p.xzy;
}

// https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
#define float3 vec3
float3 ACESFilm(float3 x) {
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    x = x*(a*x+b)/(x*(c*x+d)+e);
    return x;
}

vec3 postprocess(vec3 col, vec2 q) {
  // Some experimental tone mapping.
  col = ACESFilm(col);
  //color = 2.0*color/(1.0+color);
  col *= 0.5+0.5*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  col = clamp(col,0.0,1.0);
  col = pow(col, 1.0/vec3(2.2));
  return col;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/index.htm
vec3 postprocess0(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, 1.0/vec3(2.2));
  //col = col*0.6+0.4*col*col*(3.0-2.0*col);
  //col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  light = normalize(vec3(0.5,1.0,-1.0));
  light = transform(light);
  vec3 p = vec3(0,0,-2.5);
  p = transform(p);
  ambient = 0.4;
  diffuse = 1.0-ambient;
  vec3 col = vec3(0);
  float AA = 2.0;
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 r = vec3(uv,2);
      r = transform(r);
      r = normalize(r); // Normalize after transform
      col += solve(Ray(p,r));
    }
  }
  col /= AA*AA;
  vec2 q = fragCoord.xy/iResolution.xy;
  col = postprocess(col,q);
  fragColor = vec4(col,1);
  if (check) fragColor = vec4(1,0,0,1);
}

