float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float SmoothMin (float a, float b, float r);
float SmoothMax (float a, float b, float r);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 HsvToRgb (vec3 c);
vec2 Hashv2v2 (vec2 p);
float IFbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 sunDir, ltPos, qHit;
vec2 xzBk, csRf;
float tCur, dstFar, rOcc, rDom, rEx, hDom, hWal, nSecW, nSecD, aHitW, aHitD, bWid,
   rfAng, rfEx, tnSecW, refSphHt, refSphRad;
int idObj;
bool isSh, extVu;
const int idDom = 1, idWsup = 2, idRf = 3, idWal = 4, idEntr = 5, idLtop = 6, idLsup = 7,
   idLmp = 8, idPil = 9, idTab = 10, idChr = 11, idLeg = 12, idBcaseH = 13, idBcaseL = 14,
   idBk = 15, idCen = 16, idPlat = 17, idRail = 18, idDor = 19, idFlr = 20, idSphr = 21;
const float pi = 3.1415927;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, qqW, qqD;
  float dMin, d, a, r, dOcc, dBk, ddz, hd;
  dMin = dstFar;
  a = atan (p.z, - p.x) / (2. * pi);
  aHitW = (floor (nSecW * a) + 0.5) / nSecW;
  aHitD = (floor (nSecD * a) + 0.5) / nSecD;
  hd = 2. * hWal + 2. * rfEx + hDom + rDom;
  r = length (p.xz);
  dOcc = rOcc - r;
  qqW = vec3 (Rot2D (p.xz, 2. * pi * aHitW), p.y).xzy;
  qqD = vec3 (Rot2D (p.xz, 2. * pi * aHitD), p.y).xzy;
  ddz = 1. - abs (qqD.z);
  q = qqW;
  q.xy -= vec2 (- rOcc, 2. * hWal + 2. * rfEx);
  d = max (max (abs (length (vec2 (q.x, max (q.y - hDom, 0.))) - rDom) - 0.05, dOcc), - q.y);
  d = max (d, - PrBox2Df (vec2 (abs (q.z) - 0.5, q.y - 1.5), vec2 (0.25, 0.8)));
  DMINQ (idDom);
  q = qqD;
  q.xy -= vec2 (-0.5 * rOcc, hd);
  d = PrCylDf (q.yzx, 0.05, 0.5 * rOcc);
  DMINQ (idLtop);
  q = qqW;
  q.xy = Rot2Cs (q.xy - vec2 (- rDom - rEx - rOcc, 2. * hWal + 0.05), csRf) - vec2 (0.1, rfEx);
  d = PrBox2Df (q.xy, vec2 (rEx + 0.3, 0.06));
  q.x = qqW.x;
  DMINQ (idRf);
  q = qqW;
  q.xy -= vec2 (- rDom - 2. * rEx - rOcc + 0.05, hWal + 0.05);
  d = PrBox2Df (q.xy, vec2 (0.05, hWal + 0.05));
  d = SmoothMax (d, - PrBox2Df (vec2 (fract (0.8 * q.z + 0.5) - 0.5, q.y - 1.75),
     vec2 (0.25, 0.25)), 0.02);
  q = qqD;
  q.y -= hWal - 1.3;
  d = max (d, - PrBox2Df (q.yz, vec2 (1.25, 0.7)));
  DMINQ (idWal);
  q.x -= - rDom - 2. * rEx - 1.2;
  d = max (PrBoxDf (q, vec3 (0.7, 1.35, 0.8)), - PrBox2Df (q.yz, vec2 (1.25, 0.7)));
  DMINQ (idEntr);
  q.y -= 0.05;
  d = max (PrBoxDf (q, vec3 (0.04, 1.25, 0.7)), - PrBox2Df (abs (q.yz) - vec2 (0.52, 0.3),
     vec2 (0.47, 0.25)));
  DMINQ (idDor);
  q = qqW;
  q.xz = Rot2D (vec2 (q.x, abs (q.z)) - vec2 (- rOcc, rOcc * tnSecW), pi / nSecW);
  q.y -= 2. * hWal + 2. * rfEx;
  d = max (max (PrRoundBox2Df (vec2 (length (vec2 (q.x + 0.1, max (q.y - hDom, 0.))) -
     rDom, q.z), vec2 (0.1, 0.08), 0.02), dOcc), - q.y);
  DMINQ (idWsup);
  q.xy -= vec2 (- rDom - 0.05, - hWal - rfEx);
  d = min (PrCylDf (q.xzy, 0.1, hWal + rfEx), PrCylDf (vec3 (q.xz, abs (q.y) - hWal -
     rfEx + 0.04), 0.12, 0.04));
  DMINQ (idPil);
  q.xy -= vec2 (-2. * rEx - 0.25, - rfEx);
  d = PrRoundBoxDf (q, vec3 (0.13, hWal + 0.05, 0.1), 0.02);
  DMINQ (idWsup);
  q = p;
  q.y -= hd;
  d = PrCylDf (q.xzy, 0.5 * rOcc, 0.05);
  DMINQ (idLtop);
  q.y -= -0.5 * rDom;
  d = PrCylDf (q.xzy, 0.1 * rOcc, 0.5 * rDom + 0.05);
  q = qqW;
  q.xy -= vec2 (- rOcc, hd - rDom);
  d = min (d, PrCylDf (q.yzx, 0.02, rOcc));
  DMINQ (idLsup);
  q = qqW;
  q.xy -= vec2 (-2. * rOcc, hd - rDom);
  d = PrSphDf (q, 0.1);
  DMINQ (idLmp);
  q = qqW;
  q.xy -= vec2 (- rDom - rEx, 2. * hWal + rfEx - 0.2);
  d = PrCylDf (q.xzy, 0.03, 0.25);
  DMINQ (idLsup);
  q.y -= -0.3;
  d = PrSphDf (q, 0.15);
  DMINQ (idLmp);
  q = qqW;
  q.x = abs (q.x + rDom + rEx + 0.4);
  q.xy -= vec2 (1.3, 0.9);
  d = PrRoundBoxDf (q, vec3 (1., 0.03, 0.5), 0.01);
  DMINQ (idTab);
  d = PrCylDf ((q - vec3 (0., 0.25, 0.)).xzy, 0.015, 0.2);
  DMINQ (idLsup);
  d = PrSphDf (q - vec3 (0., 0.5, 0.), 0.07);
  DMINQ (idLmp);
  q.xz = abs (q.xz);
  q -= vec3 (0.9, -0.45, 0.4);
  d = PrCylDf (q.xzy, 0.05, 0.45);
  DMINQ (idLeg);
  q = qqW;
  q.x = abs (q.x + rDom + rEx + 0.4);
  q.xy -= vec2 (1.3, 0.5);
  q.xz = abs (q.xz) - vec2 (0.5, 0.7);
  d = PrRoundBoxDf (q, vec3 (0.3, 0.03, 0.25), 0.01);
  DMINQ (idChr);
  q.xz = abs (q.xz);
  q -= vec3 (0.2, -0.25, 0.15);
  d = PrCylDf (q.xzy, 0.03, 0.25);
  DMINQ (idLeg);
  q = qqW;
  q.z = 2.1 * fract (q.z / 2.1 + 0.5);
  xzBk.y = floor ((qqW.z + 1.05) / 2.1);
  q -= vec3 (- rDom - 2. * rEx - 0.2, 1.7, 1.05);
  d = PrRoundBoxDf (q, vec3 (0.2, 1.7, 0.9), 0.01);
  dBk = d;
  q.y -= -0.15;
  d = max (d, - max (PrBox2Df (vec2 (0.4 * (fract (q.y / 0.4 + 0.5) - 0.5), q.z),
     vec2 (0.18, 17. * bWid)), abs (q.y - 0.2) - 1.6));
  d = max (d, ddz);
  DMINQ (idBcaseH);
  d = max (q.x - 0.17, max (dBk, ddz));
  DMINQ (idBk);
  q = qqW;
  q.xy -= vec2 (- rDom - 0.5, 0.66);
  xzBk.x = sign (q.x);
  q.x = abs (q.x) - 0.7;
  d = PrRoundBoxDf (q, vec3 (0.22, 0.66, 0.42), 0.01);
  dBk = d;
  q.y -= 0.05;
  d = max (d, - max (PrBox2Df (vec2 (0.4 * (fract (q.y / 0.4 + 0.5) - 0.5), q.z),
     vec2 (0.18, 8. * bWid)), abs (q.y) - 0.6));
  d = max (d, ddz);
  DMINQ (idBcaseL);
  d = max (abs (q.x) - 0.17, max (dBk, ddz));
  DMINQ (idBk);
  q = p;
  d = PrRoundBox2Df (vec2 (r - (rDom - 1.7), q.y - 0.5), vec2 (0.15, 0.5) - 0.02, 0.01);
  d = SmoothMax (d, ddz - 0.5, 0.02);
  DMINQ (idCen);
  q = p;
  d = length (vec2 (r - (rDom - 1.7), q.y - 0.98)) - 0.03;
  d = SmoothMax (d, ddz - 0.45, 0.02);
  DMINQ (idRail);
  q = p;
  d = max (r - (rDom - 1.6), abs (q.y - 0.05) - 0.05);
  DMINQ (idPlat);
  q = qqW;
  d = max (r - (2. * rEx + rDom + 2.), q.y - 0.02);
  DMINQ (idFlr);
  if (isSh) {
    q = p;
    q.y -= refSphHt;
    d = PrSphDf (q, refSphRad);
    DMINQ (idSphr);
  }
  q = p;
  q.y -= 0.5 * (hd - 0.8 * rDom + refSphHt);
  d = PrCylDf (q.xzy, 0.02, 0.5 * (hd - 0.8 * rDom - refSphHt));
  DMINQ (idLsup);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float ltDist)
{
  float sh, d, h;
  isSh = true;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 40; j ++) {
    h = ObjDf (ro + d * rd);
    sh = SmoothMin (sh, smoothstep (0., 0.015 * d, h), 0.1);
    d += h;
    if (sh < 0.05 || d > ltDist) break;
  }
  isSh = false;
  return 0.8 + 0.2 * sh;
}

vec3 ShGrid (vec2 p, vec2 g, int stag)
{
  vec2 q, sq, ss;
  q = p * g;
  if (stag > 0 && 2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.02, 0.03, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.07 * pi * smoothstep (0.4, 0.47, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

float FloorAO (vec3 ro)
{
  vec3 q;
  float d, ddz;
  ddz = abs (Rot2D (ro.xz, 2. * pi * (floor (nSecD * atan (ro.z, - ro.x) / 
     (2. * pi)) + 0.5) / nSecD).y);
  q = qHit;
  q.xz = Rot2D (vec2 (q.x, abs (q.z)) - vec2 (- rOcc, rOcc * tnSecW), pi / nSecW);
  q.x -= - rDom - 0.05;
  d = length (q.xz) - 0.12;
  q = qHit;
  q.x = abs (q.x + rDom + rEx + 0.4) - 1.3;
  q.xz = abs (q.xz) - vec2 (0.9, 0.4);
  d = min (d, length (q.xz) - 0.05);
  q = qHit;
  q.x = abs (q.x + rDom + rEx + 0.4) - 1.3;
  q.xz = abs (abs (q.xz) - vec2 (0.5, 0.7)) - vec2 (0.2, 0.15);
  d = min (d, length (q.xz) - 0.03);
  q = qHit;
  q.x = abs (q.x + rDom + 0.5) - 0.7;
  if (ddz > 1.) d = min (d, PrRoundBox2Df (q.xz, vec2 (0.22, 0.42), 0.01));
  q = qHit;
  q.z = 2.1 * fract (q.z / 2.1 + 0.5);
  q.xz -= vec2 (- rDom - 2. * rEx - 0.2, 1.05);
  if (ddz > 1.) d = min (d, PrRoundBox2Df (q.xz, vec2 (0.22, 0.9), 0.01));
  if (ddz > 0.45) d = min (d, abs (length (ro.xz) - (rDom - 1.7)) - 0.14);
  d = min (d, length (ro.xz) - (rDom - 1.6));
  return 0.8 + 0.2 * smoothstep (0., 0.03, d);
}

vec4 ObjCol (vec3 ro, vec3 rd, inout vec3 vn, out vec2 vf)
{
  vec4 col4;
  vec3 rg;
  vec2 q, s, b;
  float r, a, h;
  r = length (ro.xz);
  a = atan (ro.z, - ro.x);
  q = vec2 (0.);
  vf = vec2 (0.);
  if (idObj <= idEntr) {
    if (idObj == idDom) {
      col4 = (length (vec2 (qHit.x, max (qHit.y - hDom, 0.))) > rDom) ?
         vec4 (1., 0.8, 0., 0.3) : vec4 (0.8, 0.6, 0.4, 0.2);
      vf = vec2 (64., 0.2);
    } else if (idObj == idWsup) {
      col4 = vec4 (0.8, 0.5, 0.4, 0.2);
      vf = vec2 (64., 0.5);
    } else if (idObj == idRf) {
      col4 = (qHit.y > 0.) ? vec4 (1., 0.4, 0.3, 0.2) : vec4 (0.7, 0.3, 0., 0.);
      col4 *= 0.8 + 0.2 * smoothstep (0., 0.02, min (abs (dot (vec2 (abs (qHit.z), qHit.x),
         sin (pi / nSecW + vec2 (0.5 * pi, 0.)))), abs (fract (((qHit.y > 0.) ? 1. : 4.) *
         qHit.x + 0.5) - 0.5)));
      vf = vec2 (64., 0.2);
    } else if (idObj == idWal) {
      col4 = vec4 (0.8, 0.6, 0.4, 0.2);
      rg = ShGrid (qHit.zy, vec2 (3., 4.), 1);
      col4 *= rg.y;
      vn.xz = Rot2D (vn.xz, 2. * pi * aHitW);
      rg.xz *= sign (vn.x);
      if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
      else vn.xz = Rot2D (vn.xz, rg.x);
      vn.xz = Rot2D (vn.xz, -2. * pi * aHitW);
      vf = vec2 (64., 1.);
    } else if (idObj == idEntr) {
      if (abs (vn.y) > 0.9) {
        col4 = (qHit.y < 0.) ? vec4 (0.5, 0.6, 0.5, 0.2) : vec4 (1., 0.4, 0.3, 0.2);
      } else {
        col4 = vec4 (0.8, 0.6, 0.4, 0.2);
        if (- qHit.x < 0.69) {
          rg = ShGrid (qHit.xy, vec2 (3., 4.), 1);
          col4 *= rg.y;
          vn.xz = Rot2D (vn.xz, 2. * pi * aHitD);
          rg.xz *= sign (vn.z);
          if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
          else vn.zx = Rot2D (vn.zx, rg.x);
          vn.xz = Rot2D (vn.xz, -2. * pi * aHitD);
        }
      }
      vf = vec2 (64., 1.);
    }
  } else if (idObj <= idTab) {
    if (idObj == idLtop) {
      col4 = vec4 (1., 0.8, 0., 0.3);
    } else if (idObj == idLsup) {
      col4 = vec4 (0.8, 0.8, 0.9, 0.2);
    } else if (idObj == idLmp) {
      col4 = vec4 (1., 1., 0.9, -1.);
    } else if (idObj == idPil) {
      col4 = vec4 (1., 0.2, 0.2, 0.2);
      vf = vec2 (128., 0.5);
    } else if (idObj == idTab) {
      col4 = vec4 (0.8, 0.4, 0.2, 0.2);
      if (PrBox2Df (qHit.xz, vec2 (1., 0.5)) < 0.) q = qHit.zx;
    }
  } else if (idObj <= idBk) {
    if (idObj == idChr) {
      col4 = vec4 (0.5, 0.3, 0.2, 0.2) * (0.8 + 0.2 * smoothstep (0., 0.01,
         abs (abs (qHit.x) - 0.25)));
      vf = vec2 (64., 0.5);
    } else if (idObj == idLeg) {
      col4 = vec4 (0.5, 0.5, 0.5, 0.2) * (0.8 + 0.2 * smoothstep (0., 0.02,
         abs (fract (16. * qHit.y + 0.5) - 0.5) - 0.05));
      vf = vec2 (64., 0.2);
    } else if (idObj == idBcaseH) {
      col4 = vec4 (0.9, 0.4, 0.2, 0.2);
      if (qHit.y > 1.7) q = qHit.xz;
      else if (abs (qHit.z) > 0.9) q = qHit.xy;
      else q = qHit.yz;
    } else if (idObj == idBcaseL) {
      col4 = vec4 (0.9, 0.4, 0.2, 0.2);
      if (qHit.y > 0.61) q = qHit.xz;
      else if (abs (qHit.z) > 0.4) q = qHit.xy;
      else q = qHit.yz;
    } else if (idObj == idBk) {
      b = vec2 ((qHit.y + 0.2) / 0.4, qHit.z / bWid);
      s = Hashv2v2 (floor (b.yx) + ((r > rDom + 2. * rEx - 1.) ? nSecW * aHitW + 1.3 * xzBk.y :
         nSecD * aHitD + 1.3 * xzBk.x));
      b = fract (b);
      b.x *= 0.4;
      b.y -= 0.5;
      h = 0.32 + 0.06 * s.x - b.x;
      if (h > 0.) {
        col4 = vec4 (HsvToRgb (vec3 (s.y, 0.8, 1.)), 0.2) * (1.2 - 0.2 * smoothstep (0., 0.02, h)) *
           (0.5 + 0.5 * smoothstep (0., 0.04, b.x - 0.002 - 0.03 * b.y * b.y));
        col4 = mix (vec4 (0.8, 0.8, 0.3, 0.), col4, smoothstep (0., 0.01, min (abs (b.x -
           0.02 * s.x - 0.07) - 0.012, PrBox2Df (vec2 (b.x - 0.02 * s.x - 0.2, b.y),
           vec2 (0.07, 0.2)))));
        vn.xz = Rot2D (vn.xz, 0.35 * pi * b.y);
        vf = vec2 (64., 0.2);
      } else col4 = vec4 (0.2, 0.2, 0.2, 0.);
    }
  } else if (idObj <= idFlr) {
    if (idObj == idCen) {
      col4 = vec4 (0.9, 0.4, 0.2, 0.2);
      if (ro.y > 0.98) q = vec2 (r, a);
      else q = vec2 (0.5 * ro.y, a);
    } else if (idObj == idPlat) {
      col4 = vec4 (0.6, 0.3, 0.1, 0.2) * (0.8 + 0.2 * smoothstep (0., 0.01,
         abs (fract (4. * r + 0.5) - 0.5) - 0.02));
    } else if (idObj == idRail) {
      col4 = vec4 (0.9, 0.7, 0.2, 0.3);
    } else if (idObj == idDor) {
      col4 = vec4 (0.8, 0.4, 0.2, 0.2);
      q = qHit.zy;
    } else if (idObj == idFlr) {
      b = Rot2D (ro.xz, 2. * pi * aHitD);
      if (- qHit.x < 2. * rEx + rDom + rOcc + 0.05 || - b.x < rDom + 2. * rEx + 1.9 &&
         abs (b.y) < 0.7) {
        col4 = vec4 (0.5, 0.6, 0.5, 0.2) * (0.85 + 0.15 * smoothstep (0., 0.01,
           min (abs (dot (vec2 (abs (qHit.z), qHit.x), sin (pi / nSecW + vec2 (0.5 * pi, 0.)))),
           abs (fract (qHit.x + 0.5) - 0.5))));
        q = qHit.xz;
        col4.rgb *= FloorAO (ro);
      } else col4 = vec4 (0.5, 0.5, 0.5, 0.1);
    }
  }
  if (q != vec2 (0.)) col4 *= 0.6 + 0.4 * Fbm2 (vec2 (12., 3.) * q);
  return col4;
}

vec3 GrndNf (vec2 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return vec3 (normalize (vec3 (Fbm2 (p) - vec2 (Fbm2 (p + e.xy),
     Fbm2 (p + e.yx)), 4. * e.x)).xzy);
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  if (rd.y > -0.02 && rd.y < 0.02 * (0.1 + 0.9 * IFbm1 (32. * atan (rd.x, rd.z) / pi))) {
    col = 0.8 * vec3 (0.35, 0.4, 0.5);
  } else {
    q = 0.01 * (ro.xz + 5. * tCur + ((200. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    col = mix (vec3 (0.3, 0.4, 0.8), clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

vec4 SphHit (vec3 ro, vec3 rd, float rad)
{
  vec3 vn;
  float b, d, w, dMin;
  dMin = dstFar;
  b = dot (rd, ro);
  w = b * b - dot (ro, ro) + rad * rad;
  if (w > 0.) {
    d = - b - sqrt (w);
    if (d > 0.) {
      dMin = d;
      vn = (ro + d * rd) / rad;
    }
  }
  return vec4 (dMin, vn);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, ds4;
  vec3 col, vn, ltDir;
  vec2 vf;
  float dstObj, dstSph, ltDist, sh;
  rOcc = 0.5;
  rDom = 4.;
  rEx = 4.;
  hDom = 2.;
  hWal = 2.5;
  nSecW = 12.;
  nSecD = 4.;
  tnSecW = tan (pi / nSecW);
  bWid = 0.05;
  rfAng = 0.05 * pi;
  csRf = sin (- rfAng + vec2 (0.5 * pi, 0.));
  rfEx = - (rEx + 0.2) * csRf.y / csRf.x;
  refSphRad = 0.4;
  refSphHt = 4. - cos (0.03 * pi * tCur);
  isSh = false;
  for (int k = VAR_ZERO; k < 2; k ++) {
    dstObj = ObjRay (ro, rd);
    if (k == 0) {
      ds4 = SphHit (ro - vec3 (0., refSphHt, 0.), rd, refSphRad);
      dstSph = ds4.x;
      if (dstSph < min (dstObj, dstFar)) {
        ro += dstSph * rd;
        rd = reflect (rd, ds4.yzw);
        ro += 0.01 * rd;
      } else break;
    }
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = ObjCol (ro, rd, vn, vf);
    if (! extVu) {
      ltDist = length (ltPos - ro);
      ltDir = normalize (ltPos - ro);
    } else {
      ltDir = sunDir;
      ltDist = dstFar;
    }
    sh = (idObj != idBk) ? ObjSShadow (ro + 0.01 * vn, ltDir, ltDist) : 1.;
    if (vf.y != 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
       col4.a * step (0.95, sh) * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
    else col = col4.rgb * (0.5 + 0.5 * max (- dot (rd, vn), 0.));
  } else if (rd.y < 0.) {
    ro += (- ro.y / rd.y) * rd;
    vn = GrndNf (0.5 * ro.xz);
    col = (0.4 * mix (vec3 (0.8, 1., 0.5), vec3 (0.7, 0.9, 0.5), 0.2 +
       0.8 * smoothstep (0.3, 0.7, Fbm2 (4. * ro.xz)))) * (0.2 + 0.8 * max (dot (vn, sunDir), 0.));
    col = mix (col, 0.8 * vec3 (0.35, 0.4, 0.5), pow (1. + rd.y, 16.));
  } else {
    col = SkyBgCol (ro, rd);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv, mMid[2], ut[2], mSize, msw;
  float el, az, zmFac, asp, sr;
  int vuId, regId;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  mSize = (1./6.) * vec2 (asp, 1.);
  mMid[0] = (1. - mSize.y) * vec2 (asp, -1.);
  mMid[1] = (1. - mSize.y) * vec2 (- asp, -1.);
  for (int k = 0; k < 2; k ++) ut[k] = abs (uv - mMid[k]) - mSize;
  regId = -1;
  if (mPtr.z > 0.) {
    regId = 0;
    for (int k = 0; k < 2; k ++) {
      msw = 2. * mPtr.xy - mMid[k] / vec2 (asp, 1.);
      if (Maxv2 (abs (msw)) < mSize.y) {
        regId = k + 1;
        msw /= 2. * mSize.y;
        break;
      }
    }
    if (regId == 0) msw = mPtr.xy;
  }
  vuId = 0;
  for (int k = 0; k < 2; k ++) {
    if (Maxv2 (ut[k]) < 0.) {
      uv = (uv - mMid[k]) / mSize.y;
      vuId = k + 1;
      break;
    }
  }
  if (regId > 0 && (vuId == 0 || vuId == regId)) vuId = regId - vuId;
  az = 0.;
  if (vuId == 0) el = -0.02 * pi;
  else if (vuId == 1) el = -0.07 * pi;
  else if (vuId == 2) el = 0.;
  if (mPtr.z > 0. && vuId == regId) {
    az += 2. * pi * msw.x;
    el += pi * msw.y;
  }
  if (vuId == 0) {
    el = clamp (el, -0.3 * pi, 0.3 * pi);
  } else if (vuId == 1) {
    az = clamp (mod (az + pi, 2. * pi) - pi, -0.8 * pi, 0.8 * pi);
    el = clamp (el, -0.3 * pi, -0.02 * pi);
  } else if (vuId == 2) {
    el = 0.;
  }
  vuMat = StdVuMat (el, az);
  extVu = false;
  if (vuId == 0) {
    ro = vec3 (1., 2. , -6.);
    zmFac = 3.;
  } else if (vuId == 1) {
    ro = vuMat * vec3 (0., 3., -50.);
    zmFac = 4.;
    extVu = true;
  } else if (vuId == 2) {
    ro = vec3 (0., 2. , -6.);
    zmFac = 0.3;
  }
  ltPos = ro + vec3 (2., 2., -4.);
  dstFar = 150.;
  sunDir = normalize (vec3 (1., 1., -1.));
  if (extVu) sunDir = vuMat * sunDir;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi)) / zmFac;
    rd = vuMat * normalize ((vuId == 2) ? vec3 (2. * sin (uvv.x / asp + vec2 (0., 0.5 * pi)), uvv.y).xzy : 
       vec3 (2. * tan (0.5 * atan (uvv.x / asp)) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  for (int k = 0; k < 2; k ++) {
    if (Maxv2 (ut[k]) < 0. && Minv2 (abs (ut[k])) * canvas.y < 3.) col = vec3 (0.3, 0.3, 0.7);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. -
     3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float IFbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 10.;
  for (int j = 0; j < 3; j ++) {
    f += floor (a * Noiseff (p));
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / (1.75 * a));
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

const vec3 faceColor = vec3(1.,0.7843,0.2392);
const vec3 black = vec3(0.);
const vec3 white = vec3(1.);
const vec3 lightBlue = vec3(0.517, 0.705, 0.905);
const vec3 darkBlue = vec3(0.368, 0.427, 0.850);
const vec3 tongueColor = vec3(0.941, 0.227, 0.090);
const vec3 sweatColor = vec3(0, 0.737, 0.949);

float saturate(float x)
{
    return clamp(x, 0., 1.);
}

float cro(in vec2 a, in vec2 b )
{
    return a.x*b.y - a.y*b.x; 
}

// https://iquilezles.org/articles/distfunctions2d/
float sdUnevenCapsule( in vec2 p, in vec2 pa, in vec2 pb, in float ra, in float rb )
{
    p  -= pa;
    pb -= pa;
    float h = dot(pb,pb);
    vec2  q = vec2( dot(p,vec2(pb.y,-pb.x)), dot(p,pb) )/h;

    //-----------

    q.x = abs(q.x);

    float b = ra-rb;
    vec2  c = vec2(sqrt(h-b*b),b);

    float k = cro(c,q);
    float m = dot(c,q);
    float n = dot(q,q);

         if( k < 0.0 ) return sqrt(h*(n            )) - ra;
    else if( k > c.x ) return sqrt(h*(n+1.0-2.0*q.y)) - rb;
                       return m                       - ra;
}

float sdCircle(vec2 p, float r, vec2 offset, float smSize) 
{
    return 1. - smoothstep(r - smSize, r, distance(p, offset));
}

float sdRectangle(vec2 p, vec2 offset, vec2 size, float smSize)
{
    vec2 lb = offset - size * .5;
    vec2 rt = offset + size * .5;
    vec2 lbRes = smoothstep(lb, lb + smSize, p);
    vec2 rtRes = 1. - smoothstep(rt - smSize, rt, p);
    return lbRes.x * lbRes.y * rtRes.x * rtRes.y;
}

// r1 > r2
float sdRim(vec2 p, float r1, float r2, vec2 offset, float smSize)
{
    float d = distance(p, offset);
    float d1 = 1. - smoothstep(r1 - smSize, r1, d);
    float d2 = smoothstep(r2 - smSize, r2, d);

    return min(d1, d2);
}

vec4 face(vec2 p) 
{
    float outline = sdCircle(p, 0.65, vec2(.0), .005);
    float inside = sdCircle(p, 0.625, vec2(.0), .005);

    vec3 col = faceColor * inside;
	return vec4(col, saturate(outline));
}

vec4 eye(vec2 p)
{
    float rim1 = sdRim(p + vec2(0.265, .02), 0.20, 0.15, vec2(.0, 0.03), .005);
    float rim2 = sdRim(p + vec2(-0.265, .02), 0.20, 0.15, vec2(.0, 0.03), .005);

    float cut = sdRectangle(p, vec2(.0, -0.35), vec2(0.95, 0.75), .005);

	return vec4(black, saturate(max(rim1, rim2) - cut));
}

vec4 mouth(vec2 p)
{
    float outline1 = sdCircle(p, 0.42, vec2(.0, 0.05), .005);
    float cut = sdRectangle(p, vec2(.0, 0.41), vec2(0.90, 0.75), .005);
    float tooth = sdRectangle(p, vec2(.0, -0.05), vec2(0.75, 0.1), .005);
    float tongue = sdCircle(p, 0.18, vec2(0.0, -0.4), .0001);
    float outline2 = sdRim(p, 0.43, 0.38, vec2(.0, 0.05), .005);

    vec3 col = mix(black, tongueColor, tongue);
    col = mix(col, white, tooth);
    col = mix(col, black, outline2);

	return vec4(col, saturate(outline1  - cut));
}

vec4 sweat(vec2 p)
{
    vec2 v1 = cos(vec2(0.0, 2.0));
	vec2 v2 = cos(vec2(0.0, 1.0));
    float r1 = 0.5;
    float r2 = 0.01;

	float d = sdUnevenCapsule(p * 3., v1, v2, r1, r2 );
    d = 1. - smoothstep(0.01, 0.02, saturate(d));

	return vec4(sweatColor, d);
}

vec3 background(vec2 p) 
{
    float x = sin(7. * (p.x + iTime));
    float y = cos(7. * (p.y + iTime));
    float r = length(p);

    return mix(lightBlue, darkBlue, smoothstep(0.5, 0.7, acos(r * x * y)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

	// coloring
    vec4 Face = face(p);
    vec4 Eye = eye(p);
    vec4 Mouth = mouth(p + vec2(.0, 0.15));
    vec4 Sweat = sweat(p + vec2(-0.15, -0.5));

    vec3 col = background(p);
    col.rgb = mix(col, Face.rgb, Face.a);    
    col.rgb = mix(col, Eye.rgb, Eye.a);  
    col.rgb = mix(col, Mouth.rgb, Mouth.a);
    col.rgb = mix(col, Sweat.rgb, Sweat.a);

    // Output to screen
    fragColor = vec4(col,1.0);
}

