const float PI = 3.14159;
const float TWOPI = 2.0*PI;
float WIDTH = 400.0;
float HEIGHT = 200.0;

float cot(float x) { return 1.0/tan(x); }
float sqr(float x) { return x*x; }

vec2 werner(float x, float y) {
  float r = length(vec2(x,y)); // Concentric circles
  float theta = atan(x,-y);
  float phi = 0.5*PI - r; // Colatitude is distance from pole
  float lambda = r * theta/cos(phi);
  return vec2(phi,lambda);
}

vec2 bonne(float x, float y, float p) {
  float cotp = 1.0/tan(p);
  float rho = sqrt(sqr(x) + sqr(cotp - y));
  float phi,lambda;
  if (p > 0.0) {
    phi = cotp + p - rho;
    lambda = rho * atan(x, cotp - y)/cos(phi);
  } else if (p < 0.0) {
    phi = cotp + p + rho;
    lambda = rho * atan(x, y - cotp)/cos(phi);
  } else {
    // Degenerate case - the sinusoidal projection
    phi = y;
    lambda = x/cos(phi);
  }
  return vec2(phi,lambda);
}

vec3 sphere(float phi, float lambda) {
  float y = sin(phi);
  float x = cos(phi)*cos(lambda);
  float z = cos(phi)*sin(lambda);
  return vec3(x,y,z);
}

// Rotate vector p by angle t.
vec2 rotate(vec2 p, float t) {
  return cos(t)*p + sin(t)*vec2(-p.y,p.x);
}

vec3 getcol(float phi, float lambda) {
  vec3 p = sphere(phi,lambda);
  if (WIDTH <= iResolution.x) p.zx = rotate(p.zx,0.1*PI*iTime);
  if (iMouse.z > 0.0) {
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  lambda = atan(p.z,p.x);
  phi = asin(p.y);
#if 0
  vec3 col = texture(iChannel1,p).xyz; // Cubemap
#elif 0
  vec3 col = texture(iChannel0,vec2(0.5+lambda/TWOPI,0.5+phi/PI)).xyz;
#else
  float u = 0.5+lambda/TWOPI;
  float v = 0.5+phi/PI;
  v = 1.0-v;
  u *= WIDTH/iResolution.x;
  v *= HEIGHT/iResolution.y;
  vec3 col = texture(iChannel2,vec2(u,v)).xyz;
  col = pow(col,vec3(0.5));
#endif
  if (true) {
    // Draw grid lines
    phi *= 12.0/PI;
    vec3 lcol = vec3(0.8);
    lambda *= 12.0/PI;
    col = mix(lcol,col,0.5+0.5*smoothstep(0.0,fwidth(lambda),abs(lambda-round(lambda))));
    //if (round(phi) == 0.0) lcol = vec3(0.8,0,0);
    col = mix(lcol,col,0.5+0.5*smoothstep(0.0,fwidth(phi),abs(phi-round(phi))));
  }
  return col;
}

void mainImage( out vec4 fragColor, vec2 fragCoord ) {
    vec2 uv = 2.25*(2.0*fragCoord-iResolution.xy)/iResolution.y;
    //uv.y -= 0.5;
    float x = uv.x, y = uv.y;
    float p = 0.5*PI*sin(0.5*iTime);
    y -= 0.7*p;
    vec2 latlong = bonne(x,y,p);
    float phi = latlong.x, lambda = latlong.y;
    vec3 col = getcol(phi,lambda);
    // Smooth out the edges - this does produce an annoying
    // halo further out where the fwidths go awry.
    if (abs(lambda) > PI+0.1) col *= 0.0;
    col *= 1.0-smoothstep(0.0,fwidth(lambda),abs(lambda)-PI);
    col *= 1.0-smoothstep(0.0,fwidth(phi),abs(phi)-0.5*PI);
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 O, vec2 u )
{
    O = texelFetch(iChannel0, ivec2(u), 0 );
}

