#define PI  3.14159265359
#define PI2 6.28318530718

#define COLOR1 vec3(0.9, 0.9, 0.9)
#define COLOR2 vec3(0.03, 0.03, 0.03)
#define LIGHTCOLOR  vec3(0.94, 0.95, 1.)

#define ARMS 10.
#define DENSITY 2.
#define ANGLE_SPEED 2.
#define ANGLE_TEMPO 3.
#define SPEED 2.
#define SMOOTH 0.02

float smoothstepCheckerboard(in vec2 uv) 
{
    uv = fract(uv + 0.25);
    float sm2 = SMOOTH * 0.5;
    vec2 p01 =
        smoothstep(0.25 - sm2, 0.25 + sm2, uv) -
        smoothstep(0.75 - sm2, 0.75 + sm2, uv);
    vec2 pn11 = (p01 - 0.5) * 2.;
    return 0.5 - 0.5 * pn11.x * pn11.y;
}

void checkerboardTunnel()
{
    float a = atan(uv.x, uv.y);
    float r = length(uv);
    float w = a - sin(1. / r) * r * ANGLE_SPEED * sin((iTime - 1./r) / ANGLE_TEMPO);
    vec2 polar = vec2(w * ARMS / PI2, 1. / r * DENSITY + iTime * SPEED );
    vec3 checker = mix(COLOR1, COLOR2,smoothstepCheckerboard(polar));
    float light = smoothstep(0.7, 0.2, r);
    color = mix(checker, LIGHTCOLOR, light);
    color = pow(color, vec3(1./2.2));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    uv = vec2(ivec2(uv * iResolution.xy * 0.15)) / (iResolution.xy * 0.15);
    checkerboardTunnel();
    uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    col = color * 0.4;
    fill = mix(color, vec3(1.0, uv.yx),  0.9);
    width = 0.0125;
    _ST(-1.0, 0.1 + sin(iTime * 4.0) * 0.03, 0.75)_B _T _C _R

    float a = iResolution.x/iResolution.y;
    fill = color * 0.2;
    width = 0.03;
    rect(-a * 2.0, -0.8, a * 4.0, 0.35);

    col = vec3(0);
    fill = vec3(1);
    fill = vec3(sin(iTime * 3.0) * 0.5 + 1.2, sin(-iTime * 3.0) * 0.5 + 1.2, cos(iTime * 3.0) * 0.5 + 1.2); //Colorful play text
    width = 0.0;

    _ST(a - mod(iTime, a * 2.5), -0.7, 0.15)
    _P _L _A _Y


    color = color * (1.0 - pow(length(uv), 3.0) * 0.1);

    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(random(vec3(fragCoord, float(iFrame))));
}

