uint rotl32(uint x, int shift)
{
	return (x << shift) | (x >> (32 - shift));
}
void XXH32_InitV(inout uint v[4], uint seed)
{
    v[0] = seed + PRIME32_1 + PRIME32_2;
    v[1] = seed + PRIME32_2;
    v[2] = seed + uint(0);
    v[3] = seed - PRIME32_1;
