    v[1] += p[1] * PRIME32_2;
    v[1] = rotl32(v[1], 13);
    v[1] *= PRIME32_1;

    v[2] += p[2] * PRIME32_2;
    v[2] = rotl32(v[2], 13);
    v[2] *= PRIME32_1;

    v[3] += p[3] * PRIME32_2;
    v[3] = rotl32(v[3], 13);
    v[3] *= PRIME32_1;
}

uint XXHash32_32(uint input_s[8])
{
    uint h32;
	uint v[4];

	XXH32_InitV(v, uint(0));

	XXH32_Inner(uint[4](input_s[0], input_s[1], input_s[2], input_s[3]), v);
    XXH32_Inner(uint[4](input_s[4], input_s[5], input_s[6], input_s[7]), v);

    h32 = rotl32(v[0], 1) + rotl32(v[1], 7) + rotl32(v[2], 12) + rotl32(v[3], 18);

