uint lzcnt(uint x)
{
    x |= x >> 1;
    x |= x >> 2;
    x |= x >> 4;
    x |= x >> 8;
    x |= x >> 16;

    uint c;

    x = x - ((x >> 1) & uint(0x55555555));                    
    x = (x & uint(0x33333333)) + ((x >> 2) & uint(0x33333333));
    c = ((x + (x >> 4) & uint(0xF0F0F0F)) * uint(0x1010101)) >> 24;

    return uint(32) - c;
}

// random float that uses the full range from [0, 1)

float floatrandhq(uvec6 x) 
{
    const uint ieeeMantissa = 0x007FFFFFu; // binary32 mantissa bitmask
    const uint mantissabits = uint(23);
    const uint exponentbits = uint(8);
    const uint hashbits     = uint(32);
    const uint hashbitslg2  = uint(5);
    uint current;
    uint cnt;
    uint pw2;    
    // ceil((2^(ebits - 1) - 2) / 2^hashbits)
    uint maxrec = (uint(1 << (exponentbits - uint(1))) - uint(2) + hashbits - uint(1)) >> hashbitslg2;
    uint m;
    uint i;

    current = hash(x, uint(0));
    cnt = lzcnt(current);
    pw2 = cnt;

    for (i = uint(1); (i < maxrec) && (cnt == hashbits); i++)
    {
        current = hash(x, i +  uint(1));
        cnt = lzcnt(current);
        pw2 += cnt;
    }

    // if less than 23 bits left, we need to generate a new hash to fill the mantissa
    if ((int(hashbits) - int(cnt) - 1) < int(mantissabits))
        current = hash(x, i +  uint(1));

    if (pw2 < uint(1 << (exponentbits - uint(1))) - uint(2))
        m = (uint(1 << (exponentbits - uint(1))) - uint(2) - pw2) << mantissabits;
    else // subnormal
        m = uint(0);

    m |= ieeeMantissa & current;

    return uintBitsToFloat( m );
}

uvec5 floatBitsToUint2(vec5 v)
{
    uvec5 u;

    u.v[0] = floatBitsToUint(v.v[0]);
    u.v[1] = floatBitsToUint(v.v[1]);
    u.v[2] = floatBitsToUint(v.v[2]);
    u.v[3] = floatBitsToUint(v.v[3]);
    u.v[4] = floatBitsToUint(v.v[4]);

    return u;
}

