void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 sz = ivec2(pow(vec2(2.0), floor(log2(iResolution.xy))));
    ivec2 px = ivec2(fragCoord) % sz;
    float v = texelFetch(iChannel0, px, 0).r;    

    fragColor = vec4(v, v, v, 1.0);

    if (gl_FragCoord.x/iResolution.x > 0.8)
        fragColor = texelFetch(iChannel0, px, 0).gggg;
#if SHOW_DITHERING
    ivec2 tpos = ivec2(fragCoord) % textureSize(iChannel1, 0);
    if (gl_FragCoord.x/iResolution.x > 0.5)
    {
        if (v < texelFetch(iChannel1, tpos, 0).r)
            fragColor = vec4(1.0);
        else
            fragColor = vec4(0.0);
    }
    else
    {
        if (random(vec2(px)) < texelFetch(iChannel1, tpos, 0).r)
            fragColor = vec4(1.0);
        else
            fragColor = vec4(0.0);
    }
#endif
}

