#if USE_MAX
#define CMPF max
#else
#define CMPF min
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float swap = 0.0;
#if USE_MAX
    // if using max
    float chance_limit = 0.64; // only converges if <= ~0.661819 @ 640 x 480 (512 x 256), or ~0.661 @ 1920 x 1080 (1024 x 1024)
#else    
    // if using min
    float chance_limit = 0.24; // only converges if <= ~0.25 @ 640 x 480 (512 x 256)
#endif    
    ivec2 p0 = ivec2(fragCoord);

    if (iFrame < INIT_FRAMES || iMouse.z > 0.0) 
    {
        // uniform histogram of values, can change to arbitrary histogram
        float v = random(vec3(p0, float(iFrame)));
#if QUANTIZE && !POISSON_DISC
        v = floor(v * 256.0) / 256.0;
#elif POISSON_DISC
        v = v < 0.875 ? 0.0 : 1.0;  // only converges for >= ~0.825
#endif
        fragColor = vec4(v, vec3(0.0));
        return;
    }

    ivec2 sz = ivec2(pow(vec2(2.0), floor(log2(iResolution.xy)))); // make data power-of-two in dimensions
    uint hashval = hash(uint(iFrame), uint(0));
    ivec2 mask = ivec2(uvec2(hashval >> 16, hashval & uint(0xFFFF)) % uvec2(sz));// constant mask for all pixels in the current frame

    ivec2 p1 = (p0 ^ mask) % sz; // coordinates of pixel to potentially swap with, importantly this is a one-to-one mapping between pairs

    float v0 = texelFetch(iChannel0, p0, 0).r; // get data at current pixel
    float v1 = texelFetch(iChannel0, p1, 0).r; // get data at pixel to potentially swap with

    float chance0 = random(vec3(p0, float(iFrame))); // random number [0, 1) seeded with p0 coords and current frame
    float chance1 = random(vec3(p1, float(iFrame))); // random number [0, 1) seeded with p1 coords and current frame
    float chance = CMPF(chance0, chance1);           // any function that's order-independent with respect to "chance0" and "chance1"

    if (chance > chance_limit) // limit swapping rate to make sure algorithm converges
    {
        fragColor = vec4(v0, vec3(0.0));
        return;
    }

    vec2 s0_x0 = quantify_error(iChannel0, p0, sz, v0, v1);
    vec2 s1_x1 = quantify_error(iChannel0, p1, sz, v1, v0);

    float err_s = s0_x0.x + s1_x1.x;
    float err_x = s0_x0.y + s1_x1.y;

    float p = v0;

    // less error if a swap were to take place: so swap.
    // note that this code will ALWAYS execute at p1 if it executed at p0, and vice-versa
    if (err_x < err_s) 
    {
        p = v1;
        if (v0 != v1)
            swap += 1.0;
    }

    fragColor = vec4(p, swap, 0.0, 1.0);
}

