/*
GLSL implementation of:
  https://www.microsoft.com/en-us/research/wp-content/uploads/2016/10/Aerophones.pdf

Keyboard:
  Toggle controls, current state is visible at the left side.
  0 = inflow (mouse)
  1 = wall (mouse)
  2 = erase (mouse)
  3 = draw ellipse
  4 = draw trumpet
  5 = trumpet path A
  6 = trumpet path B

Buffer D: interaction
Common: simulation + physical constants
*/

void mainImage(out vec4 C, in vec2 O)
{
    vec4 M = texelFetch(iChannel0, ivec2(O), 0);
    if (false)
    {
        C = M;
        //C.xy = C.yx;
        C.xy *= 1. * deltat / deltas;
        C.xy += .5;
        C.w = .5 + C.w / 2e5;
        //C.xyz = C.xyw;
        C.xyz = mix(C.xyw, vec3(1,0,1), C.z);
    }
    else
    {
        float f = M.a;
        f /= 2e4;
        //f = sign(f) * sqrt(abs(f / 5e4));
        //f = sign(f) * log(max(0., abs(f / 1e2))) / log(2e4 / 1e2);
        C = vec4(1,.2,0,0) * max(0., f) + vec4(0,.05,1,0) * max(0., -f);
        C = mix(C, vec4(1,0,1,0), M.z);
    }

    if (O.x < 10.)
    {
        int key = 0x30 + int(O.y/iResolution.y*10.);
        float v = texelFetch(iChannel2, ivec2(key, 2), 0).r;
    	C = mix(
            vec4(.2),
            vec4(1,0,0,0),
            key == 0x30 || key == 4 ? 1.-v : v
        );
    }

    if (false)
    {
        ivec2 I = ivec2(O);
        vec4 U = 1. - vec4(I, iResolution.xy - 1. - vec2(I)) / 6.;
        float border = max(0., max(max(U.x, U.y), max(U.z, U.w)));
        C = vec4(border);
    }

    C = sqrt(C);
}

