// offset to reference temperature [K]
const float deltaT = 0.;

// density [kg / m³]
const float rho    = 1.1760    * (1. - 0.00335 * deltaT);

// speed of sound at reference pressure [m / s]
const float c      = 3.4723e2  * (1. + 0.00166 * deltaT);

const float gamma  = 1.4017    * (1. - 0.00002 * deltaT);

const float mu     = 1.8460e-5 * (1. + 0.0025  * deltaT);

const float P      = 0.7073    * (1. - 0.0004  * deltaT);

// time-step [s]
//const float deltat = 7.81e-6;
//const float deltat = 7.70e-6;
//const float deltat = 7.81e-7;
const float deltat = 7.81e-6 / 4.;

// grid resolution [m]
const float deltas = 3.83e-3;

// air pressure at equilibrium [Pa]
const float P0     = 1.01325e5;

const float betac  = 1.2;

float next_p(vec4 L, vec4 M, vec4 D, float sigmaprime_M)
{
    // local speed of sound
    float cn = c;
    if (true)
        cn *= clamp(1. + betac * M.a / P0, 0.9, 1.1);

    vec2 dvdxy = (M.xy - vec2(L.x, D.y)) / deltas;
    float divergencev = dvdxy.x + dvdxy.y;

    float next_p_M = (M.a - rho * cn * cn * deltat * divergencev) / (1. + sigmaprime_M * deltat);
    return next_p_M;
}

vec4 fetch(sampler2D iChannel, ivec2 RI, ivec2 I, ivec2 dI)
{
    I = min(RI - 1, max(ivec2(0, 0), I + dI));
    return texelFetch(iChannel, I, 0);
}

void boundary(out float sigmaprime, out float beta, ivec2 RI, ivec2 I, float wall)
{
    vec4 U = 1. - vec4(I, RI - 1 - I) / 6.;
    float border = max(0., max(max(U.x, U.y), max(U.z, U.w)));

    beta = 1. - wall;
    float sigma = .5 / deltat * border;
    sigmaprime = 1. - beta + sigma;
}

vec4 update(sampler2D iChannel, vec3 iResolution, ivec2 O)
{
    ivec2 I = ivec2(O);
    ivec2 RI = ivec2(iResolution.xy);

    /*
        A T -
        L M R
        - D B

		x: vx M -> R
		y: vy M -> T
		b:  b @ M
		a:  p @ M
    */
    vec4 A = fetch(iChannel, RI, I, ivec2(-1, +1));
    vec4 T = fetch(iChannel, RI, I, ivec2( 0, +1));
    vec4 L = fetch(iChannel, RI, I, ivec2(-1,  0));
    vec4 M = fetch(iChannel, RI, I, ivec2( 0,  0));
    vec4 R = fetch(iChannel, RI, I, ivec2(+1,  0));
    vec4 D = fetch(iChannel, RI, I, ivec2( 0, -1));
    vec4 B = fetch(iChannel, RI, I, ivec2(+1, -1));

    float sigmaprime_T, sigmaprime_M, sigmaprime_R;
    float beta_T, beta_M, beta_R;
    boundary(sigmaprime_T, beta_T, RI, I + ivec2( 0, +1), T.b);
    boundary(sigmaprime_M, beta_M, RI, I + ivec2( 0,  0), M.b);
    boundary(sigmaprime_R, beta_R, RI, I + ivec2(+1,  0), D.b);

    float next_p_T = next_p(A, T, M, sigmaprime_T);
    float next_p_M = next_p(L, M, D, sigmaprime_M);
    float next_p_R = next_p(M, R, B, sigmaprime_R);

    vec2 betav_M = min(vec2(beta_M), vec2(beta_R, beta_T));
    vec2 next_vb = vec2(0);

    vec2 next_gradient_p = (vec2(next_p_R, next_p_T) - next_p_M) / deltas;
    vec2 next_v
        = ( betav_M * M.xy
          - betav_M * betav_M * deltat * next_gradient_p / rho
          + sigmaprime_M * deltat * next_vb
          )
        / ( beta_M
            + sigmaprime_M * deltat
          )
        ;

    return vec4(next_v, M.b, next_p_M);
}

float sdLine(vec2 p, vec2 a, vec2 b, float i, float j)
{
    vec2 v = b - a;
    vec2 l = p - a;
    float h = dot(l, v) / dot(v, v);
    return length(l - v * clamp(h, i, j));
}

float sdLine(vec2 p, vec2 a, vec2 b)
{
    return sdLine(p, a, b, 0., 1.);
}

