void mainImage(out vec4 C, in vec2 O)
{
    C = update(iChannel0, iResolution, ivec2(O));

    bool inflow = texelFetch(iChannel2, ivec2(0x30, 2), 0).r == 0.;
    bool wall   = texelFetch(iChannel2, ivec2(0x31, 2), 0).r != 0.;
    bool erase  = texelFetch(iChannel2, ivec2(0x32, 2), 0).r != 0.;
    bool pipe   = texelFetch(iChannel2, ivec2(0x33, 2), 0).r == 0.;
    bool ellipse= texelFetch(iChannel2, ivec2(0x34, 2), 0).r != 0.;
    bool pathA  = texelFetch(iChannel2, ivec2(0x35, 2), 0).r != 0.;
    bool pathB  = texelFetch(iChannel2, ivec2(0x36, 2), 0).r != 0.;
    bool mouse  = iMouse.z >= 0.;
    bool init = iFrame < 20 || (texelFetch(iChannel2, ivec2(0x33, 1), 0).r != 0.) || (texelFetch(iChannel2, ivec2(0x34, 1), 0).r != 0.);
    float rmouse = length(iMouse.xy - O);

    //if (false)
    if (ellipse)
    {
        //C = vec4(0.);
        //C.x += 1e3 * exp(-4. * pow((O.x - iResolution.x / 2.) / 50., 2.));
        //C.y += 1e3 * exp(-4. * pow((O.y - iResolution.y / 2.) / 50., 2.));
        //C.x += 1e3* exp(-4. * pow((length(O - iResolution.xy / 2.)) / 50., 2.));

        vec2 o = (O - iResolution.xy / 2.) / iResolution.x;
        vec2 a = o + vec2(.1, 0);
        vec2 b = o - vec2(.1, 0);
        float r = length(a) + length(b);
        C.b = float(r > .4);
        if (init)
            C.a = exp(-pow(length(a) / .01, 2.)) * 1e5;
    }
    if (pipe)
    {
        //C = vec4(0);
        // Position [mm]
        vec2 o = (O - floor(iResolution.xy / 2.) + .5) * deltas / 1e-3;
        float r = 1e3;
        if (pathA)
        {
            r = min(r, sdLine(o, vec2(-400, 30), vec2(-30, 30)) - 3.);
            r = min(r, sdLine(o, vec2(30, 30), vec2(400, 30)) - 3.);
        }
        else
        {
            r = min(r, sdLine(o, vec2(-400, 30), vec2(400, 30)) - 3.);
        }
        if (pathB)
        {
            r = min(r, sdLine(o, vec2(-200, -150), vec2(-200, -30)) - 3.);
            r = min(r, sdLine(o, vec2(-140, -90), vec2(-140, -30)) - 3.);
            r = min(r, sdLine(o, vec2(200, -150), vec2(200, -30)) - 3.);
            r = min(r, sdLine(o, vec2(140, -90), vec2(140, -30)) - 3.);
            r = min(r, sdLine(o, vec2(-200, -150), vec2(200, -150)) - 3.);
            r = min(r, sdLine(o, vec2(-140, -90), vec2(140, -90)) - 3.);
            r = min(r, sdLine(o, vec2(-400, -30), vec2(400, -30)) - 3.);
        }
        else
        {
            r = min(r, sdLine(o, vec2(-200, -150), vec2(-200, -30)) - 3.);
            r = min(r, sdLine(o, vec2(-140, -90), vec2(-140, 30)) - 3.);
            r = min(r, sdLine(o, vec2(200, -150), vec2(200, -30)) - 3.);
            r = min(r, sdLine(o, vec2(140, -90), vec2(140, 30)) - 3.);
            r = min(r, sdLine(o, vec2(-200, -150), vec2(200, -150)) - 3.);
            r = min(r, sdLine(o, vec2(-140, -90), vec2(140, -90)) - 3.);
            r = min(r, sdLine(o, vec2(-400, -30), vec2(-200, -30)) - 3.);
            r = min(r, sdLine(o, vec2(-140, -30), vec2(140, -30)) - 3.);
            r = min(r, sdLine(o, vec2(200, -30), vec2(400, -30)) - 3.);
        }
        r = min(r, sdLine(o, vec2(-400, 30), vec2(-400, -30)) - 3.);
        C.b = float(r < 0.);
        if (init)
            C.a = length((o - vec2(-370., 0))) < 30. ? 1e5 : 0.;
    }

    if (mouse)
    {
        if (inflow)
        {
            float r = 1. - rmouse / 10.;
            if (r > 0.)
            {
                r = 3. * r * r - 2. * r * r * r;
                C.a += r * 1e5 * (1. - C.b);

            }
        }
        if (wall)
        {
            if (rmouse < 3.)
            {
                C.b = 1. - float(erase);
                C.a *= (1. - C.b);
            }
        }
    }
}

