image_code:
#define AA 75.
#define SWIRLY 50.
#define SCALE 75.
#define SIZE 0.25
#define TIMESCALE 2.0

vec3 hue(float x) {
    x = 1.5 + 1.5 * cos(sin(x / 6.283185) * 6.283185);
    return clamp(abs(vec3(x) - vec3(1., 2., 3.)), 0., 1.);
}

void mainImage( out vec4 O, in vec2 FC )
{
    vec2 R = iResolution.xy;
    vec2 U = ( FC - 0.5 * R ) / R.y;   
    vec2 m = ( iMouse.xy - 0.5 * R) * 2.0 / R.y;
    float t = iTime * TIMESCALE;

    // Which side of screen?
    // Now with janky mouse control.
    float side = iMouse.z > 0. ? ( FC.x - (0.5 + 0.3 * m.x) * R.x) : ( FC.x - (0.5 + sin(iTime)*0.20) * R.x );
    //side = 1.;

    // 2D SDF
    float dist = log( 1.0 - length(U) ) * SCALE;

    // Polar conversion
    float angle = atan(U.x,U.y);

    // Derivative length.
    vec2 derv = vec2( dFdx(dist), dFdy(dist) );

    // Note that fwidth is implemented as abs(dFdx(x)) + abs(dFdy(x)).
    float width = ( side > 0. ) 
        ? length(derv) 
        : fwidth(dist);

    dist *= 0.5;

    // Apply anti-aliasing.
    float aa = min( AA, AA * width );
    float swirls = smoothstep(
        -aa, 
        aa * width, 
        SIZE - sin(  dist + angle + t)
    );

    // Screen divider
    float divider = min( side * side / 8., 1.);


    vec2 layer = vec2(
        sin( angle * 0.5 ), 
        cos( swirls * swirls ) * SWIRLY 
    );

    // Give me the swirlies boss
    O = vec4(divider * 
        mix(
            1.0 - hue( length(layer) + t * 0.3 ), 
            hue( length(layer) + t * 0.1 ), 
            swirls
        ), 1.0);
}

#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

float bwave(vec2 uv, float sc, float r) {
    uv *= sc;
    float fx = fract(uv.x) - 0.5;
    float ix = floor(uv.x) + 0.5;

    float wave = thc(2., uv.x + iTime);
    float wave2 = thc(2., ix + iTime);

    float d = uv.y - wave;
    d = smin(d, length(vec2(fx, uv.y - wave2)) - r, 0.25);

    float k = sc / iResolution.y;
    return smoothstep(-k, k, -d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float time = iTime + 1500.;

    float sc = 12. + 4. * thc(1.5, 0.5 * time + 0.75 * uv.x);
    uv.x += 0.2 * time / sc;
    uv.y -= 0.05 * thc(0.5, 0.5 * time + 0.75 * uv.x);

    vec3 col = vec3(0,48,59)/255.,
         col1 = vec3(255,119,119)/255.,
         col2 = vec3(255,206,150)/255.,
         col3 = vec3(241,242,218)/255.;

    float o = 2. * pi / 3.;

    float t = uv.x + time;
    float r1 = 0.35 + 0.15 * thc(3., t);
    float r2 = 0.35 + 0.15 * thc(3., t + o);
    float r3 = 0.35 + 0.15 * thc(3., t - o);

    float x1 = 0.125 + 0.075 * thc(1., time + uv.y);
    float x2 = 0.25  + 0.075 * thc(1., time + uv.y + o);
    float x3 = 0.375 + 0.075 * thc(1., time + uv.y - o);

    float y1 = 0.25/sc - 0.22 + 0.075 * cos(t);
    float y2 = 0.25/sc - 0.   + 0.075 * cos(t + o);
    float y3 = 0.25/sc + 0.22 + 0.075 * cos(t - o);

    float w1 = bwave(uv + vec2(x1, y1), sc, r1);
    float w2 = bwave(uv + vec2(x2, y2), sc, r2);   
    float w3 = bwave(uv + vec2(x3, y3), sc, r3);

    col = mix(col, col1, w1);              
    col = mix(col, col2, w2);              
    col = mix(col, col3, w3);

    fragColor = vec4(col,1.0);
}

// License CC0: The benefit of random reciprocals
//  Result from some random coding and adding a random reciprocal turned out "weird" so I kept it

#define RESOLUTION  iResolution
#define TIME        iTime
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
float sRGB(float t) { return mix(1.055*pow(t, 1./2.4) - 0.055, 12.92*t, step(t, 0.0031308)); }
// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(in vec3 c) { return vec3 (sRGB(c.x), sRGB(c.y), sRGB(c.z)); }

vec3 alphaBlend(vec3 bg, vec4 fg) {
  return mix(bg, fg.xyz, fg.w);
}

vec4 rasterbar(vec2 p, float bf, float aa) {
  p = p.yx;

  const float hw = 0.2;
  float d = abs(p.y) - hw;
  float x = p.y/hw;

  float df = sqrt(max(1.0-x*x, 0.0));
  vec3 col = hsv2rgb(vec3(bf, df, 1.0-0.5*df));
  col = mix(vec3(1.0), col, mix(0.0, 1.0, exp(-1.5*(1.0-bf))));

  float t = smoothstep(aa, -aa, d);
  return vec4(col, t);
}

float offset(float tm) {
  return 1.5*sin(tm)*mix(0.25, 1.0, 0.5+0.5*sin(sqrt(0.5)*tm*0.2));
}

float doffset(float tm) {
  const float eps = 0.01; 
  return (offset(tm+eps)-offset(tm-eps))/(2.0*eps);
}

vec3 effect(vec2 p) {
  float aa = 2.0/RESOLUTION.y;
  vec3 col = vec3(1.0);

  const int bars = 14;

  for (int i = 0; i < bars; ++i) {
    vec2 pp = p;
    float ii = float(i);
    float bf = ii/float(bars);
    float itime = TIME + 0.2*ii;
    float off = offset(itime); 
    float doff = doffset(itime);

    float oz = mix(0.5, 1.0, bf);
    pp /= oz;
    pp.x += off; 
    pp *= ROT(0.2*doff);

    // Bunch of random code
    const float flip = 0.125;
    float ai = smoothstep(-flip, flip, sin(0.25*itime));
    float iz = pow(max((length(pp-vec2(0.0, 0.6*sin(bf+0.3*itime)))-0.3), 0.), -mix(1.0, 4.0, ai));
    float mm = mix(5.0, 20.0, length(pp));
    iz *= 0.05+0.5+0.5*cos(mm*pp.x);
    iz = mix(iz, 1.0/iz, 1.0-ai);
    pp /= iz;

    vec4 rcol = rasterbar(pp, bf,aa*iz*oz);
    col = alphaBlend(col, rcol);
  }

  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = effect(p);
  col = mix(col, vec3(1.0), smoothstep(2.0, 0.0, TIME));
  col = sRGB(col);
  fragColor = vec4(col, 1.0);
}

#define TEXMULT vec2(1.0/6.0, 1.0) // get just 1 nyancat
#define DEPTH 4.0 // fun to mess around with
#define RADIUS 0.3
#define AMP 1. // please do not set this to 0. or else you'll cause an infinite loop

//#define NOFLASH
//#define NOCOLOR

vec4 dist(vec2 uv, float radius, vec2 dir) {
    float middle = uv.x * iResolution.x;
    float rad = radius * iResolution.x;
    for (
        vec2 offset = vec2(0.0);
        length(offset) <= rad;
        offset += dir
    ) {
        vec3 oldCol = vec3(0.0);
        for (
            float neg = -1.0;
            neg <= 1.0;
            neg += 2.0
        ) {
          vec2 uv2 = uv + offset * neg / iResolution.xy;
          vec4 tex = texture(iChannel0, uv2 * TEXMULT);
          if (tex.a < 1.0) return vec4(oldCol.rgb, length(offset) / rad);
          oldCol = tex.rgb;
        }
    }
    return vec4(vec3(0.0), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    vec4 tcolor = texture(iChannel0, uv * TEXMULT);
    if (tcolor.a >= 1.0) {
        float t = iTime * 0.9;
        vec4 dr = dist(uv, RADIUS, vec2(sin(t), cos(t)) * AMP);
        float d = dr.a;
        float a = sin(t * 4.0 + d * DEPTH);
        #ifdef NOFLASH
        a = 1.0;
        #endif
        #ifdef NOCOLOR
        dr.rgb = vec3(1.0);
        #endif
        fragColor = vec4(vec3(a) * dr.rgb, 1.0);
    } else {
        fragColor = vec4(vec3(sin(uv.y * 1.8 + iTime * 0.9 * 2.0) * 0.1 + 0.45 - 0.3) * vec3(0.8, 0.7, 1.0), 1.0);
    }
}

