// Hash functions from https://www.shadertoy.com/view/4djSRW
vec2 random22(vec2 p) {
    vec3 p3 = fract(vec3(p.xyx) * vec3(0.1031, 0.1030, 0.0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);
}

vec2 random21(float p) {
	vec3 p3 = fract(vec3(p) * vec3(0.1031, 0.1030, 0.0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);
}

vec4 textureMario(vec2 uv) {
    ivec2 p = ivec2(floor(fract(uv) * vec2(size)));

    int id = (p.x % size.x) + (((size.y - 1) - (p.y % size.y)) * size.x);

    return colors[pixels[id]] * step(abs(uv.x - 0.5), 0.5) * step(abs(uv.y - 0.5), 0.5);
}

vec3 voronoiPoint(vec2 uv, vec2 size) {
    float d = 10.0;
    vec2 p = vec2(0.0);

    uv *= size;

    vec2 i = floor(uv);
    vec2 f = uv - i;

    for(int ix = -1; ix <= 1; ++ix) {
        for(int iy = -1; iy <= 1; ++iy) {
            vec2 np = vec2(float(ix), float(iy));
            vec2 point = random22(i + np);
            float sd = length(np + point - f);

            if(sd < d) {
                d = sd;
                p = i + np + point;
            }
        }
    }

    return vec3(p / size, d);
}

vec4 mainImage(vec2 imageCoordinate) {
    vec2 uv = (imageCoordinate / imageResolution) - 0.5;
    uv *= vec2(max(1.0, imageAspectRatio), max(1.0, 1.0 / imageAspectRatio));
    uv += 0.5;

    vec3 color = vec3(0.0);

    int iterations = int(floor(imageTime * 8.0)) % 64;
    for(int i = 1; i <= iterations; ++i) {
        float z = float(i);
        vec2 offset = random21(z);
        color += textureMario(voronoiPoint(uv + offset, vec2(z)).xy - offset).xyz;
    }
    color /= max(1.0, float(iterations));

    color = mix(vec3(0.15, 0.15, 0.15), color, step(abs(uv.x - 0.5), 0.5) * step(abs(uv.y - 0.5), 0.5));

    return vec4(color, 1.0);
}

