/*
    CLOUDY PILLARS [shader-a-day #1]
        by NEAR DREAM UNITY
            2022/06/18
*/


float  Cloud  (vec3 q) {
    vec2 plane = q.xz * mat2x2(15., 8., -8., 15.);
    float thicc = dot(q, vec3(0.3, 1., 0.7));
    float value = pow(
        0.5 + 0.5 * dot(sin(plane), cos(plane)),
        4. + cos(20. * thicc) + sin(100. * thicc)
    );
    return clamp(-.25 + 1.25 * value, 0., 1.);
}


vec3  HotColour  (float value) {
    return 0.5 + 0.5 * cos(3.141 * (
        vec3(1.5, 1., 0.) + value
    ));
}


vec3  CoolColour  (float value) {
    return vec3(
        1.,
        0.5 * value,
        0.375 + 0.625 * value
    );
}


vec3  TestColours2DImage  (vec2 p) {
    //return HotColour(fract(p.y));
    return CoolColour(fract(p.y));
}


vec3  TestPillars2DImage  (vec2 p) {
    vec3 d3 = vec3(p.x, iTime, p.y);
    return HotColour(Cloud(d3));
}


vec2  Rotate  (vec2 a, float r) {
    float s = sin(r);
    float c = cos(r);
    return vec2(a.x * c - a.y * s, a.x * s + a.y * c);
}


vec3  Rayshoot3DImage  (vec2 p) {
    vec3 direction = normalize(vec3(p * 2., -1.));
    direction.yz = Rotate(direction.yz, 0.25 + 0.25 * cos(iTime * 0.5));
    direction.xz = Rotate(direction.xz, sin(iTime * 0.4));
    vec3 origin = vec3(iTime * 0.125, 0., 0.);

    float capacity = 5.;
    vec3 colour = vec3(0.);
    for (float progress = 0.; progress < 1.; progress += 0.001953125) {
        vec3 ray = origin + progress * direction * 3.;  // Terrible I'm sure. This is my first try.

        float density = Cloud(ray);

        colour += clamp(capacity, 0., 1.) * (1. - progress) * CoolColour(density) * density * 0.175;
        capacity -= density * 0.3;
    }
    return colour;
}


void  mainImage  (out vec4 o, vec2 p) {
    p = (p * 2. - iResolution.xy) / iResolution.y;
    //o = vec4(TestColours2DImage(p), 1.);
    //o = vec4(TestPillars2DImage(p), 1.);
    o = vec4(Rayshoot3DImage(p), 1.);
}

