    d5 = opUnion(d5, sdSegment(rotmat2(radians(240.0)) * (p - t3), t1, h1));
    d5 = opUnion(d5, sdSegment(rotmat2(radians(240.0)) * (p - t3), h1, t2));
    d5 = opUnion(d5, sdSegment(rotmat2(radians(240.0)) * (p - t3), h1, x1));
    d5 -= th;
    float d6 = (
        opUnion(
            opOnion(sdTriangle(p, t1, t2, t3), th),
            opOnion(
                sdTriangle(
                    p,
                    x1,
                    t2 + (t3 - t2) * length(x1),
                    t3 + (t1 - t3) * length(x1)
                ),
                th
            )
        )
    );

    // color
    vec3 col = vec3(0);
    col = (d1 > 0.0) ? col : vec3(0.72, 0.45, 0.20);
    col = (d2 > 0.0) ? col : vec3(1.0, 0.0, 0.0);
    col = (d3 > 0.0) ? col : vec3(0.0, 1.0, 0.0);
    col = (d4 > 0.0) ? col : vec3(0.0, 0.0, 1.0);
    col = (d5 > 0.0) ? col : vec3(0.0, 1.0, 1.0);
    col = (d6 > 0.0) ? col : 0.5 + 0.5 * cos(iTime + p.xyx + vec3(0, 2, 4));
    float d = min(min(min(d1, d2), d3), d4);
    col *= th - exp(-2.0 * abs(d));
    col *= th + cos(300.0 * d);
    col = mix(col, vec3(0.5), 1.0 - smoothstep(0.0, th, abs(d)));
    fragColor = vec4(col, 1.0);
}

