#define PI 3.14159265359
#define TWO_PI 6.28318530718

float circleSdf(in vec2 st)
{
    return length(st);
}

float boxSdf(in vec2 st, in vec2 sz)
{
    st = abs(st) - sz;
    return length(max(st.x, st.y));
}

float stroke(float f, float size, float w)
{
    float a = f + w * .5;
    float b = f - w * .5;
    return smoothstep(size, size+.01, a) - smoothstep(size-.01, size, b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 scale = iResolution.xy / vec2(640, 360);
    vec2 st = (2. * fragCoord - iResolution.xy) / iResolution.y;
    st *= scale;

    float scaleT = iTime * 0.2;

    float cosT = cos(scaleT);
    float sinT = sin(scaleT);
    float sin3T = sin(scaleT * 3.);

    float r = length(st);
    float a = atan(st.y, st.x);
    float circle = circleSdf(st);
    float cutArea = step(circle, 1.35);

    float angle =  iTime * 2.;
    float cosA = cos(angle);
    float sinA = sin(angle);
    mat2 rot = mat2(cosA, -sinA, sinA, cosA);
    vec2 stR = st * rot;

    vec3 col;

    vec2 animMove = vec2(cosT + sinT, sinT / cosT);
    float redCircle = circleSdf(st + animMove);
    float redF = smoothstep(0.025, 0.02, redCircle);

    redF = smoothstep(0.031, 0.03, redCircle) * abs(sin(iTime * 17.));
    redF += stroke(redCircle, 0.04, 0.02);
    float waveR = 0.07 + fract(iTime) * 0.43;
    redF += smoothstep(waveR - 0.1, waveR, redCircle) * smoothstep(waveR+.01, waveR, redCircle);
    redF *= cutArea;


    float whiteF = stroke(circle, 1.35, .02);
    whiteF += stroke(abs(st.x), abs(st.y), 0.02) * cutArea * .5;

    animMove = vec2(cosT + sin3T / 1.5, -sin3T + (cosT * 1.2));
    float moveCircle = circleSdf(st + animMove);
    whiteF += smoothstep(0.025, 0.02, moveCircle) * cutArea;

    animMove = vec2(cosT * 0.7 + sinT, sin3T + cosT);
    moveCircle = circleSdf(st + animMove);
    whiteF += smoothstep(0.025, 0.02, moveCircle) * cutArea;



    float blueF = stroke(circle, 1.75, 0.035);
    blueF = max(blueF - smoothstep(0.,.01,cos(a * 8.) - 0.95), .0);
    blueF = max(blueF - step(cos(a * 2.), .01) * 0.5, .0);

    float cut1 = step(cos(a * 2.), 0.8 * (1. + sin(iTime)) / 2.);
    blueF += (stroke(circle, 1.45, .02) * 0.3) * cut1;

    blueF += stroke(circle, 0.9, .015);
    blueF += stroke(circle, 0.55, .015);
    blueF += stroke(circle, 0.05, .015);


    blueF += (1. - stroke(abs(st.x), abs(st.y), 3.45 + sin(iTime) * 0.35)) * 
    smoothstep(1., 0.99, boxSdf(st, vec2(0.76 + sin(iTime) * .175)));


    a = atan(stR.y, -stR.x);

    blueF += (smoothstep(0.8 , 1., (a + PI) / TWO_PI) * .3) * cutArea + 
    smoothstep(.01, .002, boxSdf(stR + vec2(-0.67, 0.), vec2(.67, .001)));


    col = vec3(redF * vec3(0.988, 0.376, 0.223));
    col += whiteF;
    col += vec3(0.741, 0.949, 1.) * blueF;


    fragColor = vec4(col,1.0);
}

// Bouncing equation: cf https://www.desmos.com/calculator/ldosgtfifk
// I: bounce number.  X(i): bounce start.  p(x,I(x)): bounce trajectory
#define I(x)    floor( log2( 1. - (1.-k)*((x)/L+.5) ) / log2(k) )
#define X(i)    L* ( (1.-pow(k,i)) / (1.-k) - .5 )
#define p(x,i) ( x<X(40.) ? (x-X(i))* ( V*pow(k,i) - g/2.* (x-X(i)) ) : 0.)

void mainImage( out vec4 O, vec2 u )
{
    float k=.85, H=4., g=20., V = sqrt(2.*g*H), L = 2.*V/g; // physics parameters
    vec2  R = iResolution.xy,
          U = 5.*( 2.*u - R ) / R.y;                        // normalize coordinates

    float t = fract(iTime),
          x = floor(U.x-t+2.) + t,                          // action domain
          h = x < 0. ?  2. 
                     : -2. + p(x,I(x));                     // bouncing
                 //  : -2. + 4.*abs(cos(x)) * exp(-.5*x);   // very fake test
    O = vec4(1,0,0,1)                                       // draw
      * smoothstep(15./R.y,0.,length( vec2(fract(U.x-t)-.5, U.y-h) ) -.2 );
}









