vec2 localHex(vec2 p){
    const float z = sqrt(1.-0.25);
    const vec2 g = vec2(1.5,z);
    vec2 tile = mod(p+g,2.*g)-g;        
    if (abs(tile.y) > z/0.5*(1.-abs(tile.x))){tile = mod(tile,2.*g)-g;}
