    float n = smoothstep(.9,.7,l)*(-1./(smoothstep(.6,0.9,l)+0.01));
    vec2 id = round(4.*(w-p));
    vec2 m = iMouse.xy/iResolution.xy;
    vec2 r = p*rot(3.*iTime*sin(id.y)+id.x+m.x*3.+m.y*5.);
    float hex = smoothstep(0.2,0.,-sdHexagon(r, 0.45)+0.1)*100.;
    n = l > 0.5 ? n: hex;
    vec3 nor = -normalize(vec3(n*p.x,-1.,n*p.y));
    const vec3 sun = normalize(vec3(0,1,0));
    vec3 scl = vec3(0.957,0.827,0.541);
    vec3 amb = vec3(1.000,1.000,1.000);
    vec3 col;
    vec3 bcl = vec3(0.855,0.510,0.027);
    vec3 hvc = normalize(sun-(normalize(vec3(nuv.x*-2.,-0.7,-nuv.y*2.))));

    col += bcl*max(0.,dot(nor,sun));
