////////////////////////////////////////////////////////////////////////////////
//
// Eisenstein Series, Matthew Arcus, mla, 2022
//
// Based on original work by David Lowry-Duda:
// https://davidlowryduda.com/quanta-langlands-viz/
// Also inspired by (and some code based on)
// @branc116's https://www.shadertoy.com/view/Nsyyzh
//
// <mouse>: apply transform to disk or plane
// 'h': Half plane/disk
// 'p': Show phase as color
// 'm': Show (log) magnitude as shading
// 'l': Show log(w) tiles
// 'r': Show fundamental region
// 'f': Use pullback/fold
// 'c': Continuous coloring
// 't': Rotate colors
// 'e': Use texture for coloring
//
// A Modular Form is a complex valued function, defined on
// the upper half plane, that is invariant (up to a certain
// scaling factor, determined by the "weight" of the form) 
// under the action of the modular group (integer valued
// Mobius transformations with unit determinant).
//
// The Eisenstein Series modular form of weight k is the sum of 
// 1/(nz+m)^k for all integers n,m, except 0,0. Since k >= 4,
// and Re(z) < 0.5 in the fundamental region the sum converges
// fairly quickly, so can be used directly to evaluate the form.
// Some optimization of the calculation is possible, in which
// Riemann's zeta function plays an important part (see code).
//
// The value of the form is shown with color indicating phase
// and shading showing (log) magnitude. The boundary of the
// fundamental region is also shown ('p','m' and 'r' control
// these).
//
// By default the upper halfplane is shown, (press 'h' to map
// into the unit disk). Use mouse for a Mobius transformation 
// of the disk or plane.
//
////////////////////////////////////////////////////////////////////////////////

float zeta(int n) {
  // Should probably use a fixed array here, eg. zeta(4) = 1.082323 etc.
  float x = 0.0;
  for (int j = 6; j > 1; --j) {
    x += 1.0/pow(float(j),float(n));
  }
  return x+1.0;
}

// Euclid's algorithm - since this is only used for small n,m,
// we hardly ever loop.
bool coprime(int n, int m) {
  while (m != 0) {
    int t = m;
    m = n%m;
    n = t;
  }
  return n == 1;
}

// The Eisenstein series - sum 1/(nz+m)^k for all integers n,m, except 0,0
// Since for a modular form, k is at least 4, and Re(z) <= 1/2, things
// converge fairly quickly & for drawing pictures we can get away with n,m <= 3.
// Based on original function by @branc116
vec2 eisen0(vec2 z, int weight) {
  vec2 w = complex(0);
  int N = 3;
  for (int i = -N; i <= N; ++i) {
    vec2 prod = float(i)*z;
    for (int j = -N; j <= N; ++j) {
      if (i == 0 && j == 0) continue;
      w += cinv(cpow(prod+complex(j), weight));
    }
  }
  return w;
}

// Optimized version - since all pairs (n,m) are all pairs (tn',tm')
// for coprime n',m' and any positive t, can sum 1/(nz+m)^k just for coprime
// n, m, and multiply by zeta(k) = sum(1/t^k). Once we do this, various
// simplifications of the loop bounds are also possible.
// Note that zeta(k) is approximately 1 for integer k >= 4 and anyway
// multiplication by a constant factor doesn't make much difference
// to the displayed image.
vec2 eisen(vec2 z, int k) {
  int N = 3;
  if (key(CHAR_Z)) N++; // Do an extra round, just to check.
  vec2 w = complex(1); // 0,1
  w += cinv(cpow(z,k)); // 1,0 
  w += cinv(cpow(z+complex(1), k)); // 1,1
  w += cinv(cpow(z-complex(1), k)); // 1,-1
  for (int i = 1; i <= N; ++i) {
    vec2 iz = float(i)*z;
    for (int j = i+1; j <= N; ++j) {
      if (!coprime(i,j)) continue;
      vec2 jz = float(j)*z;
      w += cinv(cpow(iz+complex(j), k));
      w += cinv(cpow(iz-complex(j), k));
      w += cinv(cpow(jz+complex(i), k));
      w += cinv(cpow(jz-complex(i), k));
    }
  }
  w *= 2.0*zeta(k);
  return w;
}

vec2 act(mat2 gamma, vec2 z) {
  // Action of Mobius transformation matrix on z.
  float a = gamma[0][0], b = gamma[0][1];
  float c = gamma[1][0], d = gamma[1][1];
  return cdiv(a*z+complex(b),c*z+complex(d));
}

vec2 pullback(vec2 z, out mat2 gamma) {
  // Returns gamma, w such that gamma(z) = w and w is
  // (essentially) in the fundamental domain. We specify
  // the matrices in "row major" form, and swap the order
  // of multiplication (since this is GLSL).
  gamma = mat2(1);
  for (int i = 0; i < 40; i++) {
    if (abs(z.x) < 0.5 && dot(z,z) > 1.0) break;
    float xshift = -floor(z.x + 0.5);
    mat2 shiftmatrix = mat2(1, xshift, 0, 1);
    gamma *= shiftmatrix;
    z = act(shiftmatrix, z);
    if (dot(z,z) < 1.0) {
      z = -cinv(z);
      gamma *= mat2(0, -1, 1, 0);
    }
  }
  return z;
}

float clength(vec2 z) {
  // "Careful" length - avoid overflow for very large values
  float t = max(abs(z.x),abs(z.y));
  if (t <= 1e4) return length(z);
  z /= t;
  return t*length(z);
}

vec3 getcolor(vec2 z) {
  if (!key(CHAR_H)) {
    // Halfplane
    z.y += 1.0;
    if (iMouse.x > 0.0) {
      vec2 c = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
      z.x -= c.x;
      z *= (c.y+1.0)/dot(z,z);
      z.x += c.x;
      z /= dot(z,z);
    }
  } else {
    if (dot(z,z) > 1.0) return vec3(0);
    if (iMouse.x > 0.0) {
      vec2 c = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
      if (dot(c,c) > 1.0) c /= dot(c,c);
      z -= c;
      z *= (dot(c,c)-1.0)/dot(z,z);
      z += c;
      z /= -dot(z,z);
    }
    z.y += 1.0;
    z *= 2.0/dot(z,z);
    z.y -= 1.0;
    z /= dot(z,z);
  }
  mat2 gamma = mat2(1);
  if (!key(CHAR_F)) z = pullback(z,gamma);
  float a = gamma[0][0], b = gamma[0][1];
  float c = gamma[1][0], d = gamma[1][1];
  int weight = 2*(int(iTime)%14+2);
  vec2 w = eisen(z,weight);
  if (w == vec2(0)) return vec3(0);
  // Apply modularity - note that gamma is inverse of mapping
  // to fundamental region & and has unit determinant.
  w = cmul(w,cpow(-c*z + complex(a),weight));
  if (key(CHAR_D)) w = cmul(w,w);
  float t = 0.0;
  if (key(CHAR_T)) t = 0.2*iTime;
  float h = 0.5+atan(w.y,w.x)/TWOPI;
  if (!key(CHAR_C)) h *= 0.5; // Use limited color range
  vec3 col = vec3(1,1,0.5);
  if (!key(CHAR_P)) col = 0.2+0.8*h2rgb(h+t);
  if (key(CHAR_E)) {
    // Tile with texture
    vec2 uv = clog(w)/PI;
    uv += 1.0;
    uv = mod(uv,2.0);
    uv = min(uv,2.0-uv); // Mirroring
    col = texture(iChannel0,uv).rgb;
    col = pow(col,vec3(2.2));
  }
  if (key(CHAR_L)) {
    vec2 uv = 4.0*clog(w)/PI;
    float px = fwidth(uv.x);
    uv = fract(uv);
    uv = min(uv,1.0-uv);
    col *= smoothstep(0.0,min(px,0.1),min(uv.x,uv.y));
  }
  if (!key(CHAR_M)) col *= 0.65+0.35*sin(t+4.0*log(clength(w)));
  if (!key(CHAR_R)) {
    //  Fundamental region borders
    float d = abs(0.5-abs(z.x)) - 0.02;
    d = min(d,abs(length(z)-1.0) - 0.02);
    d = min(d,abs(z.x)-0.015);
    float px = fwidth(d);
    col *= 0.5+0.5*smoothstep(-px,px,d);
  }
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  int AA = 2;
  vec3 col = vec3(0);
  for (int i = 0; i < AA; i++){
      for (int j = 0; j < AA; j++) {
        vec2 z = (2.0*(fragCoord+vec2(i,j)/float(AA))-iResolution.xy)/iResolution.y;
        col += getcolor(z);
      }
  }
  col /= float(AA*AA);
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1.0);
}

