// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 256
#define MAX_DIST 128.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define DF(a,b) length(a) * cos( mod( atan(a.y,a.x)+6.28/(b*8.0), 6.28/((b*8.0)*0.5))+(b-1.)*6.28/(b*8.0) + vec2(0,11) )
#define MATERIAL 0
#define MATERIAL1 1
#define NAVY vec3(0.0,0.2,0.3)
#define RED vec3(0.5,0.1,0.1)
#define GREEN vec3(0.0,0.3,0.2)
#define GALAXY vec3(0.75,0.75,0.8)
#define PI 3.141592653589793

#define ZERO (min(iFrame,0))

const vec3 colors[4] = vec3[](NAVY,RED,GREEN,GALAXY);

vec2 combine(vec2 val1, vec2 val2 ){
    return (val1.x < val2.x)?val1:val2;
}

// thx iq! https://iquilezles.org/articles/distfunctions/
vec3 bend(vec3 p, float k){
    float c = cos(k*p.x);
    float s = sin(k*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xy,p.z);
    return q;
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float sdBox( vec3 p, vec3 b ) {
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float sdTorus( vec3 p, vec2 t ) {
    vec2 q = vec2(length(p.xz)-t.x,p.y);
    return length(q)-t.y;
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
    p.x = abs(p.x);
    float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float sdHexPrism( vec3 p, vec2 h ) {
    const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
    p = abs(p);
    p.xy -= 2.0*min(dot(k.xy, p.xy), 0.0)*k.xy;
    vec2 d = vec2(
       length(p.xy-vec2(clamp(p.x,-k.z*h.x,k.z*h.x), h.x))*sign(p.y-h.x),
       p.z-h.y );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// IQ's 3D noise function. It's faster than the FBM and enough for me to design.
// The compile is also faster.
float noise3d( vec3 p )
{
	const vec3 s = vec3(27, 111, 57);
	vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(3. - 2.*p); 
    //p *= p*p*(p*(p*6. - 15.) + 10.); // option
    h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); 
}

vec2 hash(vec2 p){
    vec2 rand = fract(sin(p*123.456)*567.89);
    rand += dot(rand,rand*34.56);
    return fract(rand);
}

vec4 voronoi(vec2 uv, float scale){
    uv*=scale;

    vec2 id = floor(uv);
    vec2 gv = fract(uv);

    vec4 m_col = vec4(0.1);
    float vd = 1.0;
    for(int y = -1; y<=1; y++){
        for(int x = -1; x<=1; x++){
            vec2 n = vec2(float(x),float(y));
            vec2 p = hash(id+n);
            vec2 prevP = p;
            p = 0.5 + 0.5*sin(iTime + 6.2831*p);
            vec2 diff = n + p - gv;
            float d = length(diff);
            float ptd = length(diff)-(0.1*abs(prevP.x));

            if ( d < vd ) {
                vd = d;
                m_col.xy = diff+hash(id+n);
                m_col.w = ptd;
            }

        }
    }

    return m_col;
}

float perlin_noise(vec2 p) {
    vec2 pi = floor(p);
    vec2 pf = p - pi;

    // interpolation
    vec2 w = pf * pf * (3.0 - 2.0 * pf);

    float f00 = dot(hash(pi + vec2(0.0, 0.0)), pf - vec2(0.0, 0.0));
    float f01 = dot(hash(pi + vec2(0.0, 1.0)), pf - vec2(0.0, 1.0));
    float f10 = dot(hash(pi + vec2(1.0, 0.0)), pf - vec2(1.0, 0.0));
    float f11 = dot(hash(pi + vec2(1.0, 1.0)), pf - vec2(1.0, 1.0));

    // mixing top & bottom edges
    float xm1 = mix(f00, f10, w.x);
    float xm2 = mix(f01, f11, w.x);

    // mixing to point
    float ym = mix(xm1, xm2, w.y); 

    return ym;
}

vec3 getVoronoiCol(vec2 p){
    vec3 c = vec3(0.7);

    vec2 vd = voronoi(p,5.0).xy;
    c.rb=vd;
    return c;
}

vec3 pnoiseCol(vec2 p){
    p.y-=iTime*0.3;
    vec3 c1 = vec3(perlin_noise(p*3.0),0.2,0.0);
    vec3 c3 = vec3(0.5,0.0,perlin_noise(p));
    return vec3(1.)+c1+c3; 
}

vec3 getColor(vec2 p, int num){
    vec3 c = vec3(1.);
    int len = colors.length();
    if(num<len){
        c = colors[num];
    } else {
        if(num == len){
            c = getVoronoiCol(p);
        } else {
            c = pnoiseCol(p);
        }
    }
    return c;
}

float capDist(vec3 p){
    vec3 prevP = p;

    p.yz*=Rot(radians(90.));
    p.xy*=Rot(radians(90.));
    p.x*=1.05;
    float d = abs(sdHexPrism(p,vec2(0.5,0.2))-2.0)-0.1;
    p = prevP;
    d = max(-p.y-0.6,d);

    p.z+=2.46;

    vec3  q = bend(p,-0.12);
    float d2 = sdBox(q,vec3(1.5,-2.3,0.7))-2.5;

    q.y+=0.5;
    float mask = sdBox(q,vec3(2.5,0.5,1.57));

    d = max(-mask,d);

    d = smin(d,d2,0.2);

    p = prevP;
    p.y-=2.3;
    p.y*=2.;
    d2 = length(p)-0.22;
    d = min(d,d2);

    p = prevP;
    p.y-=1.92;
    p.xz = DF(p.xz,1.5);
    p.xz -= vec2(1.2,1.2);
    p.xy*=Rot(radians(30.));
    p.yz*=Rot(radians(-30.));

    d2 = sdTorus(p,vec2(0.1,0.05));

    d = min(d,d2);

    return d;
}

/*
I always wanted to do the displacement to add elements to the surface of the model nicely, 
but I have no clue what is the simplet way to approach this kinda stuff.
*/
float capLogo(vec3 p) {
    p.y*=1.5;
    p.y-=1.9;
    p.z+=2.1;

    p.x*=-1.;
    p.xy*=Rot(radians(80.));
    p.xz*=Rot(radians(22.));
    p*=0.8;
    vec3 prevP = p;
    p.x+=0.3;
    p.xy*=Rot(radians(-20.));
    float r = radians(100.0);
    vec2 c = vec2(sin(r),cos(r));
    float d = sdCappedTorus(p,c,0.3,0.2);

    p = prevP;
    p.x-=0.3;
    p.xy*=Rot(radians(160.));
    r = radians(100.0);
    c = vec2(sin(r),cos(r));
    float d2 = sdCappedTorus(p,c,0.3,0.2);

    return smin(d,d2,0.05);
}

vec2 GetDist(vec3 p) {
    vec3 prevP = p;
    p+=noise3d(p*500.0)*0.001;
    float d = capDist(p);
    float d2 = capLogo(p);

    vec2 model = vec2(d*0.6,MATERIAL);
    vec2 model2 = vec2(d2,MATERIAL1);

    return combine(model,model2);
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<4; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(10,10,-10));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 60.)*occ; // spec

    return diffCol;
}

float cubicInOut(float t) {
  return t < 0.5
    ? 4.0 * t * t * t
    : 0.5 * pow(2.0 * t - 2.0, 3.0) + 1.0;
}

float backOut(float t) {
  float f = 1.0 - t;
  return 1.0 - (pow(f, 3.0) - f * sin(f * PI));
}

float getTime(float t, float duration){
    return clamp(t,0.0,duration)/duration;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    vec3 prevP = p;
    if(mat == MATERIAL){
        float frame = mod(iTime,30.);
        int num = 0;
        float time = frame;
        vec3 c = vec3(1.0);
        if(frame<5.){
            c = getColor(p.xz,5);
            time = getTime(time,1.5);
        } else if(frame>=5.0 && frame<10.){
            num = 1;
            c = getColor(p.xz,0);
            time = getTime(time-5.0,1.5);
        } else if(frame>=10.0 && frame<15.) {
            num = 2;
            c = getColor(p.xz,1);
            time = getTime(time-10.0,1.5);
        } else if(frame>=15.0 && frame<20.) {
            num = 3;
            c = getColor(p.xz,2);
            time = getTime(time-15.0,1.5);
        } else if(frame>=20.0 && frame<25.) {
            num = 4;
            c = getColor(p.xz,3);
            time = getTime(time-20.0,1.5);
        } else if(frame>=25.0 && frame<30.) {
            num = 5;
            c = getColor(p.xz,4);
            time = getTime(time-25.0,1.5);
        }

        vec3 pos = p;
        pos.y-=1.2;
        float d = length(pos.xy)-(cubicInOut(time)*5.0);
        c = mix(c,getColor(p.xz,num),S(d,-0.9));

        col = diffuseMaterial(n,rd,p,c);
    } else if(mat == MATERIAL1){
        vec3 c = vec3(0.9);
        col = diffuseMaterial(n,rd,p,c);
    }
    return col;
}

vec3 drawUI(vec2 uv, vec3 col){
    vec2 prevUV = uv;
    float size = 0.035;
    float y = -0.4;

    float frame = mod(iTime,30.);
    vec2 selectedPos = vec2(-0.375,y);
    float time = frame;
    float ptime = frame;
    float interval = 5.;
    if(frame<5.){
        time = getTime(time,0.6);
    } else if(frame>=5.0 && frame<10.){
        selectedPos = vec2(-0.225,y);
        time = getTime(time-5.0,0.6);
        ptime = ptime-5.0;
    } else if(frame>=10.0 && frame<15.) {
        selectedPos = vec2(-0.075,y);
        time = getTime(time-10.0,0.6);
        ptime = ptime-10.0;
    } else if(frame>=15.0 && frame<20.) {
        selectedPos = vec2(0.075,y);
        time = getTime(time-15.0,0.6);
        ptime = ptime-15.0;
    } else if(frame>=20.0 && frame<25.) {
        selectedPos = vec2(0.225,y);
        time = getTime(time-20.0,0.6);
        ptime = ptime-20.0;
    } else if(frame>=25.0 && frame<30.) {
        selectedPos = vec2(0.375,y);
        time = getTime(time-25.0,0.6);
        ptime = ptime-25.0;
    }

    float dd = abs(length(uv-selectedPos)-(backOut(time)*(size+0.01)))-0.002;
    col = mix(col, vec3(1.0),S(dd,0.0));

    dd = length(uv-vec2(-0.375,y))-size;
    col = mix(col, getColor(uv,0),S(dd,0.0));
    dd = length(uv-vec2(-0.225,y))-size;
    col = mix(col, getColor(uv,1),S(dd,0.0));
    dd = length(uv-vec2(-0.075,y))-size;
    col = mix(col, getColor(uv,2),S(dd,0.0));
    dd = length(uv-vec2(0.075,y))-size;
    col = mix(col, getColor(uv,3),S(dd,0.0));
    dd = length(uv-vec2(0.225,y))-size;
    col = mix(col, getColor(uv,4),S(dd,0.0));
    dd = length(uv-vec2(0.375,y))-size;
    col = mix(col, getColor(uv,5)-vec3(0.55),S(dd,0.0)); 

    // icon
    uv.x-=0.07;
    dd = abs(B(uv-vec2(0.3,0.4),vec2(0.04,0.025)))-0.002;
    float dd2 = abs(B(uv-vec2(0.3,0.435),vec2(0.02,0.01)))-0.002;
    dd = min(dd,dd2);
    uv.x*=1.5;
    dd2 = abs(length(uv-vec2(0.45,0.4))-0.01)-0.002;
    dd = min(dd,dd2);
    col = mix(col, vec3(0.0),S(dd,0.0));

    // progress
    uv = prevUV;
    uv-=selectedPos;
    dd = abs(length(uv)-size)-0.003;
    dd2 = abs(length(uv)-size)-0.003;
    float deg = mod((ptime/interval*360.), 360.);
    float r = radians(deg);
    float cmask = dot(uv,vec2(cos(r),sin(r)));

    if(deg<180.){
        dd = max(cmask,dd);
        dd2 = max(-uv.x,dd2);
        dd = min(dd,dd2);    
    } else {
        dd = max(cmask,dd);
        dd = max(-uv.x,dd);
    }

    col = mix(col, vec3(0.5,0.8,0.5),S(dd,0.0));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec2 m =  iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 0.0, -10.);
    if(iMouse.z>0.){
        ro.yz *= Rot(m.y*3.14+1.);
        ro.y = max(-0.9,ro.y);
        ro.xz *= Rot(-m.x*6.2831);
    } else {
        ro.yz *= Rot(radians(20.0));
        ro.xz *= Rot(radians(sin(iTime*0.3)*60.0));
    }

    vec3 rd = R(uv, ro, vec3(0,0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(0.8);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
    } else {
        col = mix(col,vec3(0.6),smoothstep(0.1, -0.5, uv.y));
        uv.y-=iTime*0.2;
        float ptd = voronoi(uv,7.0).w;
        col = mix(col,vec3(0.9),S(ptd,-0.1));
    }

    // gamma correction
    col = pow( col, vec3(0.9545) );    

    // UI
    uv = prevUV;
    col = drawUI(uv,col);

    fragColor = vec4(col,1.0);
}

#define MAX_ITR 1300
#define THRESH 2.
#define LIGHT_BLUE vec3(218.,112.,214.)/255.
#define PURPLE vec3(75.,0.,130.)/255.
#define BLACK vec3(0.)

#define fetchData(buf, addr) texelFetch(buf, addr, 0)
const ivec2 SCREEN_COLOR_ADDR = ivec2(0, 0);

vec2 DoubleMul( vec2 a, vec2 b )
{
	vec2 c;
	// c = a*b
	// (c.y+c.x) = (a.x+a.y)*(b.x+b.y);
	c.y = a.y*b.y; // smallest part
	float l = a.x*b.x; // largest part
	float r = a.x*b.y + a.y*b.x; // part in-between.
    c.x = l;
    c.y += r;
	return c;
}

vec2 complex_square(vec2 z)
{
    return vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y);
}

vec2 complex_cubic(vec2 z)
{
    float a = z.x;
    float b = z.y;
    return vec2(a*a*a-3.*a*b*b+b*a*a, 2.*a*a*b-b*b*b);
}

vec2 complex_sin(vec2 z)
{
    float a = z.x;
    float b = z.y;
    return vec2(sin(a)*cosh(b), cos(a)*sinh(b));
}

float mandal(vec2 c)
{
    vec2 z = vec2(0.);
    int i = 0;
    while (i < MAX_ITR && length(z) < THRESH)
    {
        z = complex_square(z) + c;
        i++;
    }
    return float(i)/float(MAX_ITR);
}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 1.5*fragCoord/iResolution.xy - .5;
    uv.x *= 1.7777777777777777777777777777778;
    uv.x -= 1.;
    uv.y -= 0.25;

    vec3 data = fetchData(iChannel0, SCREEN_COLOR_ADDR).xyz;
    vec2 center = data.xy;

    float zoom = exp(-0.1*(data.z));
    uv = center + (uv-center)*zoom;

    float i = mandal(uv);
    vec3 col = i > 0.95 ? vec3(0.): hsv2rgb(vec3(i, .8, exp(1./(60.*i + 1.8)*3.)));

    // Output to screen
    fragColor = vec4(col,1.0);
}

