// Numbers are based on JavaScript key codes: https://keycode.info/
const int LEFT  = 37;
const int UP    = 38;
const int RIGHT = 39;
const int DOWN  = 40;
const int SPACE = 32;
const int SHIFT = 16;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
        if (storeData(fragCoord, SCREEN_COLOR_ADDR)) {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0); // Initial value
    }

    if (iFrame > 0) { // After initialization
        if (storeData(fragCoord, SCREEN_COLOR_ADDR)) {
            fragColor = fetchData(iChannel0, SCREEN_COLOR_ADDR); // Load value from previous frame

            // Update:
            float stepsize = .1*exp(-0.1*fragColor.z);
            fragColor.x += texelFetch(iChannel1, ivec2(LEFT, 0), 0).x*-stepsize;
            fragColor.x += texelFetch(iChannel1, ivec2(RIGHT, 0), 0).x*stepsize;
            fragColor.y += texelFetch(iChannel1, ivec2(UP, 0), 0).x*stepsize;
            fragColor.y += texelFetch(iChannel1, ivec2(DOWN, 0), 0).x*-stepsize;
            fragColor.z += 3.*exp(-0.02*fragColor.z)*texelFetch(iChannel1, ivec2(SPACE, 0), 0).x;
            fragColor.z -= 3.*exp(-0.01*fragColor.z)*texelFetch(iChannel1, ivec2(SHIFT, 0), 0).x;
            fragColor.z = max(fragColor.z, 0.);
            }
        }
    }

