vec2 Clifford(vec2 pos, vec2 consts)
{
    //a:r b:g c:b d:a
    return vec2(sin(pos.x*pos.y/consts.g)*pos.y+cos(consts.r*pos.x-pos.y),
                pos.x+sin(pos.y)/consts.g);
}

vec2 FractalDream(vec2 pos, vec4 consts)
{
    //a:r b:g c:b d:a
    return vec2(sin(pos.y*consts.g) + consts.b*sin(pos.x*consts.g),
                sin(pos.x*consts.r) + consts.a*sin(pos.y*consts.r));
}
