// Percentage of the total clock cycle time deicated to rendering
#define ClockCycleTime 10.0
#define RenderTime     00.8
#define MaxRenderTime  ClockCycleTime*RenderTime

float GetClock(float Time)
{
    return mod(Time, ClockCycleTime);
}

float GetClockResetCount(float Time)
{
    return floor(Time/ClockCycleTime);
}

bool ShouldRender(float Time)
{
    return (Time<(ClockCycleTime*RenderTime));
}

bool ShouldChangeInitialConditions(float Time)
{
    float KingsCycle = mod(GetClockResetCount(Time), 5.0);
    bool IsFirstReset = (KingsCycle<1.0);
    return IsFirstReset==false;
}

vec2 Noise2(vec2 p)
{
    vec3 a = fract(p.xyx * vec3(123.34, 234.34, 345.65));
    a += dot(a, a + 34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

