////////////////////////////////////////////////////////////////////////////////
//
// BBC Test Card F. mla, 2022
//
////////////////////////////////////////////////////////////////////////////////

const float WIDTH0 = 365.0;
const float HEIGHT0 = 273.0;

const float WIDTH1 = 155.0;
const float HEIGHT1 = 152.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord/iResolution.y;
  uv.y = 1.0-uv.y;
  uv.x -= 0.5*iResolution.x/iResolution.y*(1.0-HEIGHT0/WIDTH0);
  uv.x += 0.0004*sin(100.0*uv.y+10.0*iTime);
  vec2 centre = 0.5*vec2(WIDTH0/HEIGHT0,1.0)+vec2(0.003,0.003);
  vec3 col;
  if (uv.x < 0.0 || uv.x > iResolution.x/iResolution.y*HEIGHT0/WIDTH0) {
    col = vec3(0);
  } else if (length(uv-centre)<0.265) {
    uv -= centre;
    uv *= 1.78;
    uv += 0.5;
    uv += vec2(0.015,0.005);
    uv *= HEIGHT1/iResolution.xy;
    col = texture(iChannel1,uv).rgb;
  } else {
    uv *= HEIGHT0/iResolution.xy;
    col = texture(iChannel0,uv).rgb;
  }
  col *= 1.2; // A little more saturation
  float line = fragCoord.y/ceil(iResolution.y/400.0);
  if (int(line)%2 == 0) {
    col *= 0.8+0.1*float(ihash(uint(30.0*iTime+line)))/exp2(32.0);
  }
  fragColor = vec4(col,1);
}

