// struct to hold 5 floats at a time of my tiling functions
struct tilingVal {
    vec2 grid;       // Coordinates of the cell in the grid (UV centered on cell)
    vec2 id;         // ID values
    float edgeDist;  // Edge distance from the cell's center to its boundaries
};

// rotates UV by direction
vec2 rotateUV(vec2 uv, vec2 direction)
{
    // Normalize the direction vector
    direction = normalize(direction);

    // Calculate the rotation matrix
    mat2 rotationMatrix = mat2(
        direction.x, -direction.y,
        direction.y, direction.x
    );

    // Rotate the UV coordinates
    return rotationMatrix * uv;
}

// rotates and animates texture in flowmap direction 
vec3 PanDirectionalTex (sampler2D sam, vec2 uv, vec2 flowMap, float time) {  
    uv += flowMap*time; // add offset with time

    uv = rotateUV(uv, flowMap); // rotate UVs

    // scale UVs so the entire texture is visible in a tile
    // the proper range is ~1-3 and artists don't read, so this is hard set to
    uv *= 2.0;

    return texture(sam, uv).xyz; // sample texture
}

// generates a -1-1 smooth flowmap
vec2 flowMap(sampler2D sam, vec2 uv) {
    //a random spot in the noise map, normally you use an actual flowmap
    vec2 flowMap = texture(sam, uv*0.05).xy;
    return flowMap * 2. -1.; // constant bias scale for -1 to 1 range
}

// Increases the steepness of Alpha while preserving 0-1 range and 1 sum
// See 2 value example (https://www.desmos.com/calculator/dpxa6mytnv)
vec4 smoothContrast(vec4 alpha, float contrast) {
    // increase steepness using power
    vec4 powAlpha = pow(alpha, vec4(contrast));

    // normalize back to precentage of 1
    return powAlpha/(powAlpha.x + powAlpha.y + powAlpha.z + powAlpha.w);
}

