////////////////////////////////////////////////////////////////////////////////
//
// Hyperbolic Torus
//
// <mouse>: change view direction
// <up/down>: zoom
// 'a': antialiasing
// 'e': show oblique ellipsoidal torus 
// 'r': autorotation
//
////////////////////////////////////////////////////////////////////////////////

const float PI =  3.141592654;

// Lighting
vec3 light = vec3(1,1,-1);
float ambient = 0.4;
float diffuse = 0.6;
float specular = 0.8;
float specularpow = 10.0;
vec3 specularcolor = vec3(1);


float A = 1.0, B = -1.0, C = 0.0, r = 2.0;
int ctorus (vec3 p, vec3 dir, out vec4 roots) {
  float A2 = A*A, B2 = B*B, C2 = C*C;
  float r2 = r*r, r3 = r*r2, r4 = r2*r2;
  float x0 = p.x, y0 = p.y, z0 = p.z;
  float x02 = x0*x0, x03 = x02*x0, x04 = x02*x02;
  float y02 = y0*y0, y03 = y02*y0, y04 = y02*y02;
  float z02 = z0*z0, z03 = z02*z0, z04 = z02*z02;
  float x1 = dir.x, y1 = dir.y, z1 = dir.z;
  float x12 = x1*x1, x13 = x12*x1, x14 = x12*x12;
  float y12 = y1*y1, y13 = y12*y1, y14 = y12*y12;
  float z12 = z1*z1, z13 = z12*z1, z14 = z12*z12;
  float a = (A2*x14 + 2.0*A2*x12*y12 + A2*y14 + 2.0*A*B*x12*z12 - C2*x12*z12 + 2.0*A*B*y12*z12 - C2*y12*z12 + B2*z14);
  float b = 2.0*(2.0*A2*x0*x13 + 2.0*A2*x12*y0*y1 + 2.0*A2*x0*x1*y12 + 2.0*A2*y0*y13 + A*C*r*x12*z1 + A*C*r*y12*z1 + 2.0*A*B*x12*z0*z1 - C2*x12*z0*z1 + 2.0*A*B*y12*z0*z1 - C2*y12*z0*z1 + 2.0*A*B*x0*x1*z12 - C2*x0*x1*z12 + 2.0*A*B*y0*y1*z12 - C2*y0*y1*z12 - B*C*r*z13 + 2.0*B2*z0*z13);
  float c = - (2.0*A2*r2*x12 - 6.0*A2*x02*x12 - 2.0*A2*x12*y02 - 8.0*A2*x0*x1*y0*y1 + 2.0*A2*r2*y12 - 2.0*A2*x02*y12 - 6.0*A2*y02*y12 - 2.0*A*C*r*x12*z0 - 2.0*A*C*r*y12*z0 - 2.0*A*B*x12*z02 + C2*x12*z02 - 2.0*A*B*y12*z02 + C2*y12*z02 - 4.0*A*C*r*x0*x1*z1 - 4.0*A*C*r*y0*y1*z1 - 8.0*A*B*x0*x1*z0*z1 + 4.0*C2*x0*x1*z0*z1 - 8.0*A*B*y0*y1*z0*z1 + 4.0*C2*y0*y1*z0*z1 - 2.0*A*B*r2*z12 - C2*r2*z12 - 2.0*A*B*x02*z12 + C2*x02*z12 - 2.0*A*B*y02*z12 + C2*y02*z12 + 6.0*B*C*r*z0*z12 - 6.0*B2*z02*z12 + 2.0*A*x12 + 2.0*A*y12 + 2.0*B*z12);
  float d = -2.0*(2.0*A2*r2*x0*x1 - 2.0*A2*x03*x1 - 2.0*A2*x0*x1*y02 + 2.0*A2*r2*y0*y1 - 2.0*A2*x02*y0*y1 - 2.0*A2*y03*y1 - 2.0*A*C*r*x0*x1*z0 - 2.0*A*C*r*y0*y1*z0 - 2.0*A*B*x0*x1*z02 + C2*x0*x1*z02 - 2.0*A*B*y0*y1*z02 + C2*y0*y1*z02 + A*C*r3*z1 - A*C*r*x02*z1 - A*C*r*y02*z1 - 2.0*A*B*r2*z0*z1 - C2*r2*z0*z1 - 2.0*A*B*x02*z0*z1 + C2*x02*z0*z1 - 2.0*A*B*y02*z0*z1 + C2*y02*z0*z1 + 3.0*B*C*r*z02*z1 - 2.0*B2*z03*z1 + 2.0*A*x0*x1 + 2.0*A*y0*y1 - C*r*z1 + 2.0*B*z0*z1);
  float e = A2*r4 + A2*x04 - 2.0*A2*r2*y02 + 2.0*A2*x02*y02 + A2*y04 - 2.0*A*C*r3*z0 + 2.0*A*C*r*x02*z0 + 2.0*A*C*r*y02*z0 + 2.0*A*B*r2*z02 + C2*r2*z02 + 2.0*A*B*x02*z02 - C2*x02*z02 + 2.0*A*B*y02*z02 - C2*y02*z02 - 2.0*B*C*r*z03 + B2*z04 - 2.0*(A2*r2 + A)*x02 - 2.0*A*y02 + 2.0*C*r*z0 - 2.0*B*z02 - 2.0*A*r2 + 1.0;
  return quartic(a,b,c,d,e,roots);
}

vec3 ctorusnormal (vec3 p) {
  float A2 = A*A, B2 = B*B, C2 = C*C;
  float r2 = r*r, r3 = r*r2;
  float x = p.x, y = p.y, z = p.z;
  float x2 = x*x, x3 = x2*x;
  float y2 = y*y, y3 = y2*y;
  float z2 = z*z, z3 = z2*z;
  float dx = 4.0*A2*x3 + 4.0*A2*x*y2 + 4.0*A*C*r*x*z + 2.0*(2.0*A*B - C2)*x*z2 - 4.0*(A2*r2 + A)*x;
  float dy = 4.0*A2*y3 + 4.0*A*C*r*y*z + 2.0*(2.0*A*B - C2)*y*z2 - 4.0*(A2*r2 - A2*x2 + A)*y;
  float dz = -2.0*A*C*r3 + 2.0*A*C*r*x2 + 2.0*A*C*r*y2 - 6.0*B*C*r*z2 + 4.0*B2*z3 + 2.0*C*r + 2.0*((2.0*A*B + C2)*r2 + (2.0*A*B - C2)*x2 + (2.0*A*B - C2)*y2 - 2.0*B)*z;
  return vec3(dx,dy,dz);
}

int dosurface(vec3 p0, vec3 r, out vec4 roots) {
  return ctorus(p0,r,roots);
}

vec3 donormal(vec3 p) {
  return ctorusnormal(p);
}

int solve(vec3 p0, vec3 r, out vec4 roots) {
  int nroots = dosurface(p0,r,roots);
  // Sort the roots
  if (nroots >= 2) {
    if (roots.y < roots.x) roots.xy = roots.yx;
  }
  if (nroots >= 3) {
    if (roots.z < roots.y) roots.yz = roots.zy;
    if (roots.y < roots.x) roots.xy = roots.yx;
  }
  if (nroots >= 4) {
    if (roots.w < roots.z) roots.zw = roots.wz;
    if (roots.z < roots.y) roots.yz = roots.zy;
    if (roots.y < roots.x) roots.xy = roots.yx;
  }
  return nroots;
}

vec3 applylighting(vec3 baseColor, vec3 p, vec3 n, vec3 r) {
  if (dot(r,n) > 0.0) n = -n; // Face forwards
  vec3 c = baseColor*ambient;
  c += baseColor*diffuse*(max(0.0,dot(light,n)));
  float s = pow(max(0.0,dot(reflect(light,n),r)),specularpow);
  c += specular*s*specularcolor;
  return c;
}

float gridline(vec3 p) {
  // Draw some gridlines on surface
  vec3 t = fract(p*4.0);
  t = min(t,1.0-t);
  float d = min(t.x,min(t.y,t.z));
  return smoothstep(0.02,0.025,d);
}

vec3 scene(vec3 p0, vec3 r) {
  float time = iTime;
  float a = 0.3333+ max(0.0,3.0*(1.0-cos(0.1*time)));
  float b = (3.0*a-1.0)/(3.0-a);
  vec4 params = vec4(a,b,0,0);
  vec4 roots;
  vec3 color = vec3(0);
  int nroots = solve(p0,r,roots);
  float rwidth = length(fwidth(r));
  for (int i = nroots-1; i >= 0; i--) {
    float t = roots[i];
    if (t < 0.0) break;
    vec3 p = p0+t*r;
    vec3 n = donormal(p);
    if (dot(n,r) > 0.0) n = -n;
    n = normalize(n);
    if (isnan(length(n))) continue;
    float k = 0.0;
    k += 0.1*iTime;
    k += 10.0*p.z;
    vec3 basecolor = pow(abs(n.bgr),vec3(2.0));
    basecolor = h2rgb(1.5*atan(p.z*p.z)/PI);
    float dk = fwidth(k);
    basecolor = mix(vec3(0),basecolor,smoothstep(0.0,rwidth*t*t/abs(dot(n,r)),abs(k-round(k)) - 0.1));
    vec3 c = applylighting(basecolor,p,n,r);
    float fog = 1.0-smoothstep(0.0,200.0,t);
    c *= fog;
    color = mix(color,c,0.5);
  }
  return color;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (!key(CHAR_R)) {
    float t = 0.5*iTime;
    p.yz = rotate(p.yz, 0.1*t);
    p.zx = rotate(p.zx, 0.222*t);
  }
  return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  float scale = 1.0;
  float camera = 6.0;
  camera *= 0.1*float(10+keycount(KEY_DOWN)-keycount(KEY_UP));
  r = 2.0*cos(0.0618*iTime);
  float t = 0.2*iTime, cost = cos(t), sint = sin(t);
  if (key(CHAR_E)) { A = 2.0; B = 1.0; }
  float A1 = A*cost*cost+B*sint*sint;
  float B1 = A*sint*sint+B*cost*cost;
  float C1 = 2.0*sint*cost*(A-B);
  A = A1, B = B1, C = C1;

  vec3 p = vec3(0,0,-camera);
  p = transform(p);
  light = transform(light);
  light = normalize(light);
  int AA = 1;
  if (key(CHAR_A)) AA = 2;
  vec3 color = vec3(0);
  for (int i = 0; i < AA; i++) {
    for (int j = 0; j < AA; j++) {
      vec2 uv = scale*(2.0*(fragCoord.xy+vec2(i,j)/float(AA)) - iResolution.xy)/iResolution.y;
      vec3 r = vec3(uv, 2.0);
      r = transform(r);
      r = normalize(r);
      color += scene(p,r);
    }
  }
  color /= float(AA*AA);
  color /= 1.0+color;
  color = pow(color,vec3(0.4545));
  if (alert) color.x = 1.0;
  fragColor = vec4(color,1);
}

