float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrCylDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

#define N_WLK 3

vec3 sunDir, qHit, wPos;
float tCur, dstFar, spd;
int idObj;
const int idLeg = 1, idAx = 2, idPlat = 3, idTur = 4, idBar = 5;
bool isSh;
const float pi = 3.1415927;

struct Gird {
  float wid, thk, crv, hol, lFac;
};
Gird g;

struct Leg {
  vec2 cs1, cs2, cs3;
};

struct Walker {
  Leg leg[4];
  vec3 wPos;
  vec2 csTur;
  float szFac;
};
Walker wlk[N_WLK];

float hPlat, lLeg, bLen, bWid;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float GirdDf (vec3 p)
{
  return max (length (max (vec2 (length (vec2 (p.x, abs (p.z) - min (abs (p.z),
     (g.lFac - 1.) * g.wid))), abs (p.y)) - vec2 (g.wid, g.thk), 0.)) - g.crv, g.hol -
     length (vec2 (p.x, 2. * g.wid * fract (p.z / (2. * g.wid) + fract (g.lFac / 2.)) - 0.25)));
}

void SetWlkConf ()
{
  float sDir, tc, t, tH, tt, tV, limT, fUp, limEx, a1, a2, fh;
  wlk[0].szFac = 1.2;
  wlk[1].szFac = 0.82;
  wlk[2].szFac = 0.51;
  lLeg = 9.;
  tH = 0.01;
  tV = 0.3;
  limT = 0.6;
  limEx = 0.995;
  fUp = 1.;
  g.wid = 0.25;
  g.hol = (2./3.) * g.wid;
  g.crv = 0.01;
  hPlat = 4. * limEx * (lLeg - 1.) * g.wid * cos (atan (limT));
  for (int j = 0; j < N_WLK; j ++) {
    tc = tCur * spd / (wlk[j].szFac * hPlat * limT * 2. / 3.);
    for (int k = 0; k < 4; k ++) {
      t = mod (tc + float (k), 4.);
      if (t > 1.) {
        sDir = -1.;
        t = (t - 1.) / 3.;
      } else sDir = 1.;
      tt = 0.5 - abs (t - 0.5);
      a1 = atan (limT * (((tt < tH) ? tt * tt / tH : 2. * tt - tH) / (1. - tH) - 1.) *
         sign (0.5 - t) * sDir);
      fh = hPlat;
      if (sDir > 0.) fh -= fUp * smoothstep (0., tV, tt);
      fh *= 0.5 / cos (a1);
      a2 = - ((k == 1 || k == 3) ? -1. : 1.) * acos (0.5 * fh / ((lLeg - 1.) * g.wid));
      wlk[j].leg[k].cs1 = sin (-0.5 * pi + a1 + a2 + vec2 (0.5 * pi, 0.));
      wlk[j].leg[k].cs2 = sin (-2. * a2 + vec2 (0.5 * pi, 0.));
      wlk[j].leg[k].cs3 = sin (0.5 * pi - a1 + a2 + vec2 (0.5 * pi, 0.));
    }
    wlk[j].csTur = sin (0.6 * pi * (2. * SmoothBump (0.25, 0.75, 0.25,
       mod (0.2 * tCur / wlk[j].szFac, 1.)) - 1.) - pi + vec2 (0.5 * pi, 0.));
    wlk[j].wPos = wPos - vec3 (3.5 * float (2 * j - N_WLK + 1), 0., 0.);
  }
  bWid = 1.6;
  bLen = 3.;
}

float WlkDf (vec3 p, float dMin, Walker wk)
{
  vec3 pp, q, qq;
  float d, lGird, qy;
  p.y -= hPlat;
  q = p;
  q.x = abs (q.x) - bWid;
  q.y -= g.wid;
  g.lFac = 13.;
  g.thk = 0.05;
  q = q.yxz;
  d = GirdDf (q);
  DMINQ (idLeg);
  q = p;
  q.y -= g.wid;
  g.lFac = 7.;
  g.thk = bWid - 0.05;
  d = GirdDf (q.yxz);
  DMINQ (idPlat);
  g.thk = 0.05;
  q.z = abs (q.z) - 1.;
  d = PrCylDf (q.zyx, g.hol, bWid + 2. * g.thk);
  DMINQ (idAx);
  q = p;
  q.y -= g.wid + 0.8;
  d = PrRoundCylDf (q.zxy, 0.7, 0.1, 0.5);
  DMINQ (idTur);
  q = p;
  q.xz = Rot2Cs (q.xz, wk.csTur);
  q.yz -= vec2 (g.wid + 0.9, -0.7);
  d = PrCylDf (q, 0.25, 0.7);
  DMINQ (idBar);
  g.thk = 0.05;
  pp = p;
  for (int k = 0; k < 4; k ++) {
    p = pp;
    p.xz += vec2 (bWid * ((k == 0 || k == 1) ? -1. : 1.), bLen * ((k == 1 || k == 3) ? -1. : 1.));
    p.y -= g.wid;
    g.lFac = lLeg;
    lGird = (lLeg - 1.) * g.wid;
    q = p.yxz;
    q.xz = Rot2Cs (q.xz, wk.leg[k].cs1);
    q.z -= lGird;
    qy = q.y;
    q.y = abs (q.y) - 3. * g.thk;
    d = GirdDf (q);
    DMINQ (idLeg);
    q.y = qy;
    d = PrCylDf (vec3 (q.x, abs (q.z) - lGird, q.y), g.hol, 5. * g.thk);
    DMINQ (idAx);
    q.z -= lGird;
    q.xz = Rot2Cs (q.xz, wk.leg[k].cs2);
    q.z -= lGird;
    d = GirdDf (q);
    DMINQ (idLeg);
    q.z -= lGird;
    q.xz = Rot2Cs (q.xz, wk.leg[k].cs3);
    g.lFac = 4.;
    lGird = (g.lFac - 1.) * g.wid;
    q.z -= lGird - 2. * g.wid;
    qy = q.y;
    q.y = abs (q.y) - 3. * g.thk;
    d = GirdDf (q);
    DMINQ (idLeg);
    q.y = qy;
    q.z -= g.wid;
    d = PrCylDf (vec3 (q.xy, abs (q.z) - 2. * g.wid).xzy, g.hol, 5. * g.thk);
    DMINQ (idAx);
  }
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  for (int j = VAR_ZERO; j < N_WLK; j ++) {
    q = p - wlk[j].wPos;
    d = (isSh) ? 0. : wlk[j].szFac * PrRoundBoxDf (q / wlk[j].szFac - vec3 (0., 4.5, 0.),
       vec3 (2.5, 4.5, 8.) - 0.1, 0.1);
    if (d < 0.3) d = wlk[j].szFac * WlkDf (q / wlk[j].szFac, dMin / wlk[j].szFac, wlk[j]);
    dMin = min (d, dMin);
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.02 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.4, 0.5);
  } else {
    q = 0.01 * (ro.xz + 2. * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    skCol = vec3 (0.3, 0.4, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) *
       min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
    col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

float GrndHt (vec2 p)
{
  return 0.7 * Fbm2 (0.2 * p.yx);
}

vec3 GrndNf (vec3 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return normalize (vec3 (GrndHt (p.xz) - vec2 (GrndHt (p.xz + e.xy),
     GrndHt (p.xz + e.yx)), e.x)).xzy;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, dstGrnd, sh, nDotL;
  SetWlkConf ();
  isSh = false;
  dstGrnd = dstFar;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    col4 = vec4 (0.9, 0.9, 0.95, 0.2);
    if (idObj == idLeg) {
      if (abs (qHit.y) > 0.045) col4 = mix (col4, vec4 (0.2, 0.5, 0.8, 0.2),
         step (0.4, Fbm2 (16. * qHit.xz)));
    } else if (idObj == idAx) {
      col4 = vec4 (0.8, 0.8, 0.9, 0.2);
    } else if (idObj == idPlat) {
      col4 *= 0.9 + 0.1 * SmoothBump (0.1, 0.9, 0.05, fract (4. * qHit.x));
    } else if (idObj == idTur) {
      col4 *= 0.9 + 0.1 * SmoothBump (0.1, 0.9, 0.05, fract (4. * qHit.y));
    } else if (idObj == idBar) {
      col4 *= 0.2 + 0.8 * step (0.17, length (qHit.xy));
    }
    vn = ObjNf (ro);
    nDotL = max (dot (vn, sunDir), 0.);
    nDotL *= nDotL;
  } else if (rd.y < 0.) {
    dstGrnd = - ro.y / rd.y;
    ro += dstGrnd * rd;
    col4 = 0.6 * mix (vec4 (0.8, 0.6, 0.4, 0.), vec4 (0.9, 1., 0.5, 0.),
       smoothstep (0.4, 0.6, Fbm2 (0.5 * ro.xz)));
    vn = GrndNf (ro);
    vn = VaryNf (ro, vn, 1. - smoothstep (0.3, 0.5, dstGrnd / dstFar));
    nDotL = max (dot (vn, sunDir), 0.);
  } else {
    col = SkyBgCol (ro, rd);
  }
  if (dstObj < dstFar || rd.y < 0.) {
    sh = (min (dstObj, dstGrnd) < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
    col = col4.rgb * (0.2 + 0.1 * max (- dot (vn, sunDir), 0.) + 0.8 * sh * nDotL) +
       col4.a * step (0.95, sh) * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 32.);
  }
  if (rd.y < 0. && dstObj >= dstFar) col = mix (col, vec3 (0.3, 0.4, 0.5),
     pow (1. + rd.y, 16.));
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  spd = 3.;
  wPos = vec3 (0., 0.01, spd * tCur);
  az = 0.7 * pi;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
  } else {
    az += 0.03 * pi * tCur;
  }
  el = clamp (el, -0.35 * pi, -0.01 * pi);
  vuMat = StdVuMat (el, az);
  ro = wPos + vuMat * vec3 (0., 3.5, -45.);
  zmFac = 4.5;
  dstFar = 150.;
  sunDir = vuMat * normalize (vec3 (0.5, 0.7, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.5*2.*8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = c1.z;
    float offset = 0.;
    float offset1 = iTime/1000.;
    float t4 = floor((uv.x+uv.y+iTime)/16.);

    for(int i=1;i<5;i++)
    {
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 1; k < 4; k++){

            uv += (t2.yx);
            //uv *= (1.+t2.yx/2.);
            //uv += floor(t2.y+t2.x)/2.;
            t2 = triangle_wave(uv.yx+1.5,scale)+float(i-k);
            t3 = triangle_wave(uv-float(i*k),scale);
            t3.y -= float(i)/8.;
            //t3.y -= abs(fract(uv.x-.5))*float(i)/4.;

            uv.yx = (t2+t3)/(scale);
            vec2 uv1 = uv + triangle_wave((uv.yx+iTime/4.)*4.,scale)/16.;
            col.x = min(((uv1.y-uv1.x)+col.x*sign(uv1.x-uv1.y)),col.x)/sqrt(3.);
            col = max(col.yzx,abs(col.yzx+vec3(col.x))/sqrt(3.));
            //col = max(col.yzx,abs(col+vec3(col.x))).yzx/sqrt(3.); //another nice color palette
        }

    }
    fragColor = vec4((col*9.*2.),1.0);
}

int   N = 7;      // N^3 spheres of radius r
float r = .4;
#define hash3(p)      fract(sin(float(p)*vec3(127.1,311.7, 74.7))*4e5 )
#define rot(a)        mat2(cos(a),-sin(a),sin(a),cos(a))  // rotation
//#define sphere(i)  ( 2.*hash3(i) - 1. ) * float(N)/2.        
  #define sphere(i)  ( vec3( i/N/N, (i/N)%N, i%N ) - float(N)/2. + hash3(i)-.5 )       

float raycast(vec3 P, vec3 D, inout int _i) { // --- return dist, _i= id
    float l = 1e5;
    for ( int i=0; i<N*N*N; i++ ) {                // for each sphere
        vec3  C = sphere(i) - P;                   // compute intersection
        float b = dot(C,D), c = dot(C,C)-r*r, d = b*b -c, s = b-sqrt(d);
        if ( d>0. && s>0. && s < l )
            if (_i<0) return s;                    // shadow: early exit
            else      l = s, _i = i;               // keep closest hit (in front)
    }
    return l;
}

void mainImage(out vec4 O, vec2 U)
{
    vec3  R = iResolution,
          D = normalize(vec3(U+U, -5.*R.y) - R),         // ray direction
          P = 30./R,                                      // camera position 
          L = vec3(0,0,1),
          M = iMouse.z > 0. ? iMouse.xyz/R -.5: vec3(5,5,0)/1e2*cos(.5*iTime+vec3(0,11,0)); // auto thumbnail

    L.yz *= rot(-6.*M.y),                                 // rotations
    L.xz *= rot(-6.*M.x);
  /*P.yz *= rot(-6.*M.y),                           
    P.xz *= rot(-6.*M.x);
    D.yz *= rot(-6.*M.y),                           
    D.xz *= rot(-6.*M.x);
  */
    int i=0,j=-1;
    float l = raycast(P,D, i),                            // raycast spheres
          s = raycast(P += (l-1e-2)*D, L , j);            // shadow ray

    vec3 N = normalize( P - sphere(i) );
    O =  l == 1e5 ? vec4(.6,.8,1,1)                       // no hit
                  : ( 1. + dot(N,L) )/2. // max(.0,dot(N,L)) //exp(-.1*(l-25.))   // shading
                      * ( s == 1e5 ? vec4(0,.8,0,0)       // hit and no shadow
                                   : vec4(0,.2,0,0) );    // hit and shadow 
}

/*
Testing out different methods to make a Hexagonal Grid for perfromance. 
To try this test for yourself, increase the "repeat" value to tank you fps 
until it goes well below 144fps. Then comment in the x value calculation
for the function you wish to test. 

There's no one true winner here. If you need the ID values Shanes vec4 
version is fastest. If you don't then the BigWIngs if version is faster.
But try for yourself with the combination of values you need.

If you wish to learn how Hextiling works BigWIngs version has a great video 
explaining how the math works (https://youtu.be/VmrIDyYiJBA).  
If you like to read more Shanes version is very well commented 
(https://www.shadertoy.com/view/Xljczw).
*/

#define sqrt3 		1.7320508075
#define half_sqrt3	0.8660254037

const vec2 r = vec2(1, sqrt3); // 1, sqrt(3)
const vec2 h = vec2(0.5,half_sqrt3); // 1/2, sqrt(3) /2

// Hexagonal Distanstance from the 0,0 coords
float hexDist(vec2 p) {
	p = abs(p);   
    return max(dot(p, h), p.x);
}

// From Amhall "Cube Pixel Tetrahedron" (https://www.shadertoy.com/view/7sXyRN)
// Elegant, but slow, so use the others
vec4 hexTile_lerp(vec2 uv) {
    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    float d1 = dot(a,a);
    float d2 = dot(b,b);

    vec2 gv = mix(a, b, float(d2 < d1)); // Branch-free!

    //float eDist = hexDist(gv); // Edge distance
    // float cDist = dot(gv, gv); // squared distance from center
    vec2 id = uv-gv; // ID coords

    return vec4(gv, id); // xy hex coords + z distance to edge
}

// From BigWIngs "Hexagonal Tiling" https://www.shadertoy.com/view/3sSGWt
// If you need the ID use vec4 version
vec4 hexCoords_if(vec2 uv) {   
    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 gv = dot(a, a) < dot(b,b) ? a : b; // Branch

    //float eDist = hexDist(gv); // Edge distance
    //float cDist = dot(gv, gv); // squared distance from center
    vec2 id = uv-gv; // ID coords

    return vec4(gv, id); // xy hex coords + z distance to edge
}

// From Shanes "Minimal Hexagonal Grid" https://www.shadertoy.com/view/Xljczw
// This one always calculates the ID values, if you don't need those use if version
vec4 HexCoords_vec4(vec2 p){
    // hex centers
    vec4 ab = vec4(p, p - vec2(.5, 1));
    vec4 hC = floor(ab/r.xyxy) + .5;
    // Centering the coordinates with the hexagon centers above.
    vec4 h = vec4(p - hC.xy*r, p - (hC.zw + .5)*r);

    vec4 uvid = dot(h.xy, h.xy)<dot(h.zw, h.zw) ? vec4(h.xy, hC.xy) : vec4(h.zw, hC.zw + .5);

    //float eDist = hexDist(uvid.xy); // Edge distance.
    //float cDist = dot(uvid.xy, uvid.xy); // Squared distance from center

    //vec2 id = vec2(uvid.z, uvid.w*sqrt3); // square grid Id values otherwise use uvid.zw
    return vec4(uvid);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y; //centered square UVs
    vec2 subject = (uv* 2.) + (iTime * 0.2) + 100.; //some arbitrary motion

    // if you get around 144 fps use a higher repeat value
    int repeat = 1; //3000 is enough for RTX2070

    vec4 test;
    for(int i=0; i<repeat; i++)
    {
        vec2 a = subject + float(i); // permutation each iteration

        // use which ever you wish to test
        //vec4 x = hexTile_lerp(a); //62fps 
        //vec4 x = hexCoords_if(a); //68fps 
        vec4 x = HexCoords_vec4(a); //70fps

        test += x; // each iteration adds to final result
    }        

    fragColor = vec4(fract(test)); // Output to screen
}

float PI = 3.141;


vec3 ringy(vec2 center, vec3 color, float time) {
	float mx    = max(iResolution.x, iResolution.y);
	vec2  uv    = gl_FragCoord.xy / mx;

	float rings = smoothstep(0.99, 0.991, sin(time - distance(uv, center) * 255.0));
	vec3  dotty = vec3(smoothstep(0.0011, 0.001, distance(uv, center)));
	vec3  circle_source = dotty + vec3(rings) * color;
	return circle_source;
}


vec2 getCenter(float phase, float t) {
    float mx = max(iResolution.x, iResolution.y);
    return vec2(iResolution / mx * 0.5)
        + vec2(
            sin(t / 10.0 + 2.0 * PI * phase/(2.0*PI)) * 0.1, 
            cos(t / 10.0 + 2.0 * PI * phase/(2.0*PI)) * 0.1);
}

vec3 getColor(vec2 r, vec2 g, vec2 b, vec2 c) {
    float mx = max(iResolution.x, iResolution.y);
    vec2  uv = gl_FragCoord.xy / mx;

    return vec3(
        smoothstep(r.x, r.y, distance(uv, c)),
        smoothstep(g.x, g.y, distance(uv, c)),
        smoothstep(b.x, b.y, distance(uv, c)));
}


void mainImage(out vec4 out_color, in vec2 fragCoord) {
    float mx = max(iResolution.x, iResolution.y);
    vec2  uv = gl_FragCoord.xy / mx;
    float t_wave = iTime * 10.0;
    float t_spin = iTime *  5.0;

    vec2 c1_center = getCenter(2.0/3.0 * PI, t_spin);
    vec2 c2_center = getCenter(4.0/3.0 * PI, t_spin);
    vec2 c3_center = getCenter(2.0     * PI, t_spin);

    vec3 c1_color = getColor(vec2(0.1, 0.05), vec2(0.3, 0.2),  vec2(0.2, 0.1),  c1_center);
    vec3 c2_color = getColor(vec2(0.3, 0.2),  vec2(0.2, 0.1),  vec2(0.1, 0.05), c2_center);
    vec3 c3_color = getColor(vec2(0.2, 0.1),  vec2(0.1, 0.05), vec2(0.3, 0.2),  c3_center);

	vec3 scene = ringy(c1_center, c1_color, t_wave)
        + ringy(c2_center, c2_color, t_wave)
