#define SPEED_MULTIPLIER 0.2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float delayTime = pow(max(mod(iTime, 10.) - DELAY, 0.) * SPEED_MULTIPLIER, 5.);

    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv *= pow(delayTime * 0.1 + 1.0, 5.);

    float r = length(uv);

    float phi = atan(uv.y, uv.x) + delayTime / (r * 0.5);

    vec2 duv = r * vec2(cos(phi), sin(phi)) + 0.5;

    vec4 c = texture(iChannel0, duv);

    float m = 1.0;
    if (duv.x > 1.0 || duv.y > 1.0 || duv.x < 0.0 || duv.y < 0.0) m = 0.0;

    fragColor = c * m;
}

