#define ITERS 128

#define USE_THIN_LINES 1

#define MUL(a, b) (mat2(a, -a.y, a.x) * b)
// complex number multiplication, Fabrice style.

#define TRIM_EDGES 1
// set to 0 to let stalks go to infinity -- best with a small number of ITERS
// for instance, TRIM_EDGES=0 and ITERS=8 works well

#define EXACTLY_TRIM_EDGES 0

float ramp(in float minval, in float maxval, in float val) {
    return clamp((val - minval) / (maxval - minval), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 z = 1.0 * uv;

    vec2 mouse_norm = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;
    vec2 axes = vec2(0.0);
    vec2 c = mouse_norm;

    float dist1 = abs(length(z) - 0.125);
    float dist2 = abs(length(z) - 0.25);
    float min_dist = min(dist1, dist2);

    vec2 grad = vec2(1.0, 0.0);
    // so, in addition to wanting to compute iterations of the function f(z) = z^2+c, we
    // also want it's derivative, so we can estimate the distance in pixel space to the z
    // such that f^i(z) is zero in either the real or the complex axis.

    for (int i = 0; i < ITERS; ++i) {

        vec2 zprime = 2.0 * z;
        // here's our derivative of the next iteration in terms of this one
        // now let's compute the derivative of the next iteration with respect to the start point
        grad = MUL(zprime, grad);

        float gradient_inv_magnitude = 1.0 / length(grad);
        // thanks, @iq, for the tip on how to save a sqrt

        z = MUL(z,z) + c;
        // and here we actually perform our julia iteration, which is the tiniest part of the code


        dist1 = abs(length(z) - 0.125) * gradient_inv_magnitude;
        dist2 = abs(length(z) - 0.25)  * gradient_inv_magnitude;
        // see above comment.
        min_dist = min(min_dist, min(dist1, dist2));


        if (dot(z,z) > 4.0) {
#if EXACTLY_TRIM_EDGES            
            min_dist = 10.0;

#elif !TRIM_EDGES
            min_dist = 0.0;
#endif            
            break;
        }

    }


#if USE_THIN_LINES
    vec3 col = vec3(ramp(0.0 / iResolution.y, 2.0 / iResolution.y, min_dist));
#else
    min_dist = min_dist * sqrt(2.0);
	vec3 col =
        vec3(ramp(0.0 / iResolution.y, 2.0 / iResolution.y, min_dist),
             ramp(1.0 / iResolution.y, 3.0 / iResolution.y, min_dist),
             ramp(2.0 / iResolution.y, 4.0 / iResolution.y, min_dist));
    // and here we compute our tricolored line
#endif
    // TODO : consider coloring by iteration

    // Output to screen
    fragColor = vec4(col,1.0);
}

