#define PI acos(-1.0)

#define ZOOM_SPEED 0.046
#define SPIKE_SPEED 0.063
#define TWIST_SPEED 0.057
#define ROT_SPEED 0.0
#define SPIKE_SIZE 0.05
#define SPIKE_SIZE_OFFSET 0.05
#define SPIKE_SIZE_OFFSET_SPEED 0.03
#define SPIKE_HEIGHT 0.75
#define SPIKE_HEIGHT_OFFSET 0.5
#define SPIKE_HEIGHT_OFFSET_SPEED 0.05
#define HUE_STEPS 9.0
#define HUE_STEEPNESS 2.0
#define HUE_RANGE 0.125
#define HUE_SPEED 0.023

vec3 hsv(float h, float s, float v)
{
    vec3 c = vec3(h, s, v);
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float noise(vec2 coord)
{
    coord /= iResolution.xx;

    float p = -1./255./2.;
    vec2[] aa = vec2[](vec2(-p, -p), vec2(p, -p), vec2(p, p), vec2(-p, p));
    float c = 0.0;
    for (int i=0; i<4; i++)
        c += texture(iChannel0, coord*0.5+aa[i]+iTime*0.01).r/4.;

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float spikes = 3.+(sin(iTime*SPIKE_SPEED*PI)*0.5+0.5)*4.;

    vec2 center = vec2(0.5, 0.5*iResolution.y/iResolution.x);
    vec2 spiral_center = vec2(0.5, center.y);
    vec2 uv = (fragCoord/iResolution.xx)-spiral_center;
    uv *= vec2(exp((0.2+cos(PI*iTime*ZOOM_SPEED))*1.25));

    float dist = length(uv);// * (sin(iTime)*0.5+0.5);
    float a = (dist+1.0)*2.*PI*sin(iTime*TWIST_SPEED)+iTime*ROT_SPEED;
    uv = uv*mat2(cos(a), -sin(a), sin(a), cos(a));

    float ang = acos(clamp(uv.x/dist, -1., 1.))+PI;

    float spike_size = SPIKE_SIZE+(sin(iTime*PI*SPIKE_SIZE_OFFSET_SPEED)*0.5+0.5)*SPIKE_SIZE_OFFSET;
    float d = dist + abs(mod(ang/PI*spikes, 2.)-1.)*spike_size;

    //d *= -5.;
    //d = clamp(d, 0., center.y)/center.y;

    float height = 2.0*center.y*(SPIKE_HEIGHT+( sin(iTime*PI*SPIKE_HEIGHT_OFFSET_SPEED)*0.5+0.5 )*SPIKE_HEIGHT_OFFSET);

    float v = abs(mod(d+iTime*0.001, height*2.0)-height)/height;
    v = v*0.3+0.6;
    v = sqrt(v*v);

    float h = abs(mod(d, height*2.0)-height)/height;

    //d = 1.-d*d;
    h = sin(HUE_STEPS*h*PI)*HUE_STEEPNESS/(HUE_STEPS*PI)+h;
    h = h*HUE_RANGE-iTime*HUE_SPEED;

    vec3 col = hsv(h, sin(ang*2.+iTime*2.)*0.2+0.8, v);//noise(fragCoord)*0.25+0.6);

    fragColor = vec4(col, 1.0);
}

const float PI = 3.14159;
const float TWOPI = 2.0*PI;
